insert  into `MetaData`(`id`,`pattern_type_id`,`data`,`content_id`) values (5,3,'ТагАЗ||российский автопром||кризис',5),(6,5,'Федеральный арбитражный суд Московского округа прекратил производство дела по жалобе ООО «Таганрогский автомобильный завод» (ТагАЗ) и ООО «Русслегавто» на решения нижестоящих судов о взыскании с них 70 млн долл. в пользу ОАО «Банк ВТБ». Об этом говорится в материалах дела. Дело прекращено в связи с ведущейся между сторонами процедурой мирного урегулирования вопроса долгов автомобильного завода перед банком.||Напомним, Арбитражный суд Москвы взыскал по иску ВТБ с ответчиков 70 млн долл. задолженности по кредитному договору. Исковые требования были увеличены банком с 67,4 млн долл. до 77,6 млн долл. Банк взыскал с ответчиков задолженность по кредитному договору от 30 июня 2008 г., заключенному на сумму в 52,749 млн долл. Позже апелляционный суд подтвердил это решение.||В качестве третьего лица, заявляющего самостоятельные требования, в деле участвовал Михаил Парамонов, который выступал одним из поручителей по кредиту.',5),(7,1,'Суд обязал ТагАЗ выплатить 70 млн долларов банку ВТБ',5),(8,2,'12/01/2012',5),(9,3,'ГИБДД||стихия||дороги',6),(10,5,'Управление ГИБДД Московской области настоятельно рекомендует водителям быть аккуратнее на дорогах и по возможности отказаться от поездок в связи с сильным снегопадом, который ожидается в регионе в ближайшие два дня, пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>.||Ранее МЧС сообщало, что ночью с 11 на 12 января и с 12 на 13 января на территории Центрального федерального округа, в том числе в Москве и Московской области, ожидается сильный снег, налипание мокрого снега на провода и деревья, усиление ветра порывами до 15-20 метров в секунду.||«Ухудшение погоды неблагоприятно сказывается на дорожной обстановке, возрастает число аварий с материальным ущербом, видимость на дороге снижается, асфальтовое покрытие становится скользким, что провоцирует ДТП», – сказали в УГИБДД Подмосковья.||В связи с этим Госавтоинспекция просит водителей соблюдать правила дорожного движения, учитывать метеорологические условия и состояние проезжей части. Водителям следует избегать резких м',6),(11,1,'ГИБДД Подмосковья просит водителей быть внимательными',6),(12,2,'12/01/2012',6),(13,3,'Петербург||криминал||полиция',7),(14,5,'Следственный комитет по Петербургу проводит доследственную проверку по факту задержания офицера полиции в угнанном автомобиле. Как сообщает пресс-служба СК Петербурга, 30-летний заместитель командира отдельного взвода ДПС ОГИБДД ОМВД по Василеостровскому району капитан полиции Андрей Макагоненко был задержан 10 января 2012 г. у дома № 40 по Песочной набережной на автомобиле Lexus JS-300, который числился в угоне с ноября 2011 г.||По результатам доследственной проверки будет принято процессуальное решение.||Напомним, что недавно ГИБДД Подмосковья обзавелась беспилотником для поиска угнанных автомобилей. Беспилотный самолет позволит с воздуха выявлять места отстоя угнанных автомобилей и несанкционированной стоянки, а также разыскивать спецтехнику. На самолете установлена видеокамера, которая передает изображения оператору. Дальность действия беспилотника составляет около 5 км, максимальный потолок около двух километров.',7),(15,1,'Капитан полиции ездил по Петербургу на угнанном Lexus',7),(16,2,'12/01/2012',7),(17,3,'статистика продаж автомобилей||российский авторынок',8),(18,5,'Продажи новых легковых и легких коммерческих автомобилей в России в 2011 г. выросли по сравнению с 2010 г. на 39% и составили 2,65 млн машин. Об этом сообщил председатель комитета автопроизводителей Ассоциации европейского бизнеса (АЕБ) Дэвид Томас. Ассоциация прогнозирует рост продаж в 2012 г. до 2,8 млн автомобилей (рост менее 8%).||По данным комитета автопроизводителей АЕБ, самыми продаваемыми в России автомобилями в 2011 г. стали модели Lada. Их продажи составили 578 тыс. 378 шт. (прирост по сравнению с 2010 г. составил 11%).||На втором месте автомобили Chevrolet – 173 тыс. 484 шт. (+49%), на третьем месте по итогам 2011 г. находится Hyundai – 163 тыс. 447 шт. (+88%). Четвертое место занимает Renault – 154 тыс. 734 шт. (+60%). Пятерку замыкают автомобили KIA – 152 тыс. 873 (+47%).||Первые четыре места среди самых продаваемых в России моделей автомобилей занимает продукция АвтоВАЗа: Lada Kalina – 142 тыс. 930 шт. (+31%), Lada Priora – 138 тыс. 697 шт. (+11%), Lada Samara – 122 тыс. ',8),(19,1,'Продажи автомобилей в России в 2011 г. выросли на 40%',8),(20,2,'12/01/2012',8),(21,3,'статистика продаж автомобилей',9),(22,5,'Объем продаж автомобилей в КНР (включая легковые авто и коммерческий транспорт) по итогам 2011 г. вырос на 2,5% по сравнению с 2010 г. Показатель составил 18,5 млн единиц против 18 млн годом ранее, передает (С) Associated Press со ссылкой на сообщение Китайской ассоциации автопроизводителей (CAAM), аффилированной с правительством страны.||Темпы роста продаж существенно замедлились по сравнению с 2010 г., когда показатель вырос на 32%. Причиной стало решение властей страны об отмене налоговых льгот для производителей малолитражных машин. Поддержки лишились также фермеры, закупающие грузовую технику для сельскохозяйственных нужд. Кроме того, власти ограничили продажи машин в крупнейших городах страны в целях разгрузки транспортных узлов.||Тем не менее китайский авторынок остается крупнейшим в мире. В то же время США, уступившие КНР первое место в 2009 г., впервые за последние несколько лет опередили Китай по темпам роста данного сегмента. По итогам 2011 г. американский авторынок вырос на',9),(23,1,'За 2011 год в Китае было продано 18 500 000 автомобилей',9),(24,2,'12/01/2012',9),(25,3,'Chrysler||Jeep||внедорожники',10),(26,5,'Концерн Chrysler Group LLC намерен в первом полугодии 2012 года окончательно определиться с планами по выпуску внедорожников Jeep в России. Об этом сообщает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на интервью главы Jeep Майка Мэнли агентству Bloomberg. «Мы находимся на этапе окончательного определения того, как выпускать Jeep в России», – сказал М. Мэнли.||Намерение Chrysler организовать производство в РФ является частью плана по увеличению продаж автомобилей за пределами Северной Америки. Выпуск Jeep может быть налажен в Китае в 2013-2014 годах, сообщил М. Мэнли. Производство внедорожников также будет запущено в 2013 году на заводе Fiat в Турине.||Как известно, власти Санкт-Петербурга в первом квартале 2012 года планируют подписать с Fiat меморандум о намерениях строительства завода на территории города. По словам источника, знакомого с планами Fiat, итальянский автопроизводитель будет производить на заводе под Санкт-Петербургом автомобили Jeep, в час',10),(27,1,'Chrysler может запустить производство Jeep в России',10),(28,2,'12/01/2012',10),(29,3,'миовой автопром',11),(30,5,'Динамика глобальной автомобильной промышленности в 2012 году сохранит умеренные темпы прошлого года, когда рынок вырос на 3%. Таков прогноз развития мирового автопрома на 2012 год, сделанный экспертами компании Ernst&Young.||Одним из основных источников беспокойства, по мнению аналитиков, станет охлаждение развивающихся рынков в 2012 году, однако долгосрочный прогноз развития отрасли остается благоприятным.||На автомобильных рынках индустриально развитых европейских стран следует ожидать вялую реализацию, обусловленную кризисом суверенного долга. Перспективы увеличения реализации на рынке США не очевидны. Несмотря на сделанные ранее позитивные прогнозы о восстановлении в текущем году производства и реализации автомобилей до уровня предкризисного 2007 года, ожидать его теперь приходится не раньше середины текущего десятилетия.||В результате в 2012 году следует ожидать сохранения основных тенденций, проявившихся в отрасли в прошедшем году, прогнозируют в Ernst&Young.',11),(31,1,'Мировой авторынок вырастет в 2012 году всего на 3%',11),(32,2,'12/01/2012',11),(33,3,'Москва||дороги||стихия',12),(34,5,'В Москве за последние сутки с улиц было вывезено 400 тыс. куб. м снега, с начала зимнего сезона – более 6,5 млн куб. м. Как сообщил ИА «РБК» пресс-секретарь заместителя мэра Москвы по вопросам жилищно-коммунального хозяйства и благоустройства города Игорь Пергаменщик, минувшей ночью в уборке города было задействовано 6,5 тыс. единиц спецтехники, в том числе 2,6 тыс. самосвалов.||С начала текущей недели после периода оттепели в столице вновь начались снегопады, и коммунальная техника активно задействуется в уборке снега и вывозе его на снегосплавные пункты.||Ранее заместитель мэра Москвы по вопросам жилищно-коммунального хозяйства и благоустройства Петр Бирюков дал поручение городским коммунальным службам продолжить уборку города. Снег вывозится с лотковой части дорог, тротуаров, остановок общественного транспорта, подходов к социальным объектам.',12),(35,1,'С начала зимы с улиц Москвы вывезли 6,5 млн куб. м снега',12),(36,2,'12/01/2012',12),(37,3,'техосмотр||Москва',13),(38,5,'В Москве число пунктов техосмотра машин в 2012 г. будет увеличено с 199 до 270 штук, появятся передвижные комплексы проведения ТО. Как сообщает пресс-служба Департамента транспорта и развития дорожно-транспортной инфраструктуры, в 2011 г. процедуру технического осмотра в Москве прошли 1 048 000 автомобилей. По сравнению с предыдущими периодами это значительно меньше, так как с 1 июня по 31 декабря 2011 г. в РФ действовал мораторий на проведение техосмотра.||В настоящее время в городе задействовано 199 линий техосмотра с пропускной способностью 1 750 000 машин в год. По прогнозам специалистов, из-за роста числа автомобилей в 2012 г. желающих пройти техосмотр будет существенно больше – от 2 000 000 до 2 400 000. В связи с этим правительство Москвы приняло решение об увеличении количества линий, принимающих транспортные средства на освидетельствование.||Помимо стационарных пунктов контроля в эту систему войдут 20 передвижных комплексов. Первый из них, рассчитанный на две линии, заработает',13),(39,1,'Количество пунктов техосмотра в Москве вырастет до 270',13),(40,2,'12/01/2012',13),(41,3,'автоспорт||звезды',14),(42,5,'Роберт Кубица повторно сломал правую ногу, пишет сайт <a href=\"http://f1news.ru\" target=\"_blank\">F1news.ru</a>. Год назад, в феврале 2011 года, польский пилот попал в страшную аварию на раллийной трассе. Полученные им тяжелые травмы исключили его выступление в «Формуле-1». Его место в Lotus Renault занял Ник Хайдфельд.||Теперь гонщик упал, поскользнувшись на льду, в итальянском городе Пьетрасанта, где он живет в настоящее время. После падения Р. Кубица попросил доставить его в клинику Pietra Ligure, где проходил лечение после прошлогодней аварии.||Как сообщает сайт, Р. Кубица получил повторный перелом правой большеберцовой кости, и лечение займет как минимум несколько недель. Официальных заявлений от менеджеров гонщика пока не поступало. Однако, похоже, вернуться в большой спорт поляк сможет еще нескоро.',14),(43,1,'Автоспорт: Р. Кубица сломал травмированную ногу',14),(44,2,'12/01/2012',14),(45,3,'Toyota||Lexus||статистика продаж автомобилей',15),(46,5,'ООО «Тойота Мотор» в 2011 году реализовало в России 133 203 автомобиля марок Toyota и Lexus. В частности, за прошедший год на российском рынке было продано 119 505 автомобилей Toyota, что на 51% превышает аналогичный показатель 2010 года. Доля бренда на российском автомобильном рынке составила 4,5%. Лучшие результаты продаж показали модели RAV4 (27 206 автомобилей), Corolla (27 007 автомобилей) и Camry (21 443 автомобиля). Внедорожники составили 52,7% от общих продаж автомобилей Toyota в России.||Продажи Lexus в 2011 году составили 13 698 автомобилей, что на 25% больше, чем годом ранее. По результатам прошлого года доля бренда Lexus в премиум-сегменте составляет 8,8%. Наиболее популярной моделью по-прежнему остается Lexus RX (6395 автомобилей). В тройку самых популярных моделей также вошли Lexus LX 570 (3186 автомобилей) и Lexus GX 460 (1955 автомобилей). На внедорожные модели в 2011 году пришлось 84,2% продаж Lexus в России.||В 2011 году российские клиенты приобрели 2074 автомобиля To',15),(47,1,'Toyota за год увеличила продажи в России в полтора раза',15),(48,2,'12/01/2012',15),(49,3,'бензин||цены',16),(50,5,'Президент России Дмитрий Медведев поручил руководителю Федеральной антимонопольной службы РФ Игорю Артемьеву обеспечить проведение мониторинга и анализа рыночных цен на бензин и другие виды топлива и при выявлении их необоснованного завышения оперативно принимать меры в соответствии с законодательством Российской Федерации, сообщает пресс-служба главы государства. О результатах работы, в соответствии с поручением, необходимо будет доложить до 1 июля 2012 г.||Отметим, что за прошлый год цены на бензин в России поднялись более чем на 13%. За период с января по декабрь 2011 г. прирост цен составил 13,7%. В ноябре 2011 г. стоимость бензина достигла 26,62 руб./л. Цена бензина марки А-76 за январь-ноябрь 2011 г. выросла на 19,3% и составила 24,61 руб./л, Аи-92 – на 12,2% – 26,65 руб./л, Аи-95 – на 11,4% – 28,54 руб./л. Дизельное топливо стало дороже на 28,1% и в ноябре стоило 27,52 руб./л.',16),(51,1,'Д. Медведев поручил главе ФАС РФ провести мониторинг цен на бензин',16),(52,2,'12/01/2012',16),(53,3,'Opel||General Motors||мировой автопром||автобизнес',17),(54,5,'Концерн General Motors Co., являющийся владельцем компании Opel, рассматривает возможность перевода некоторых своих производственных мощностей на европейские заводы, что может помочь спасти Opel от банкротства. Об этом сообщает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на агентство (С) Reuters.||Так, по данным источников зарубежного информагентства, в настоящее время концерн ведет переговоры с крупнейшим профсоюзом промышленных рабочих Германии IG Metall о частичном переносе производства Chevrolet из Южной Кореи на заводы Opel в Европе, в частности в Польше. General Motors, в свою очередь, получит возможность применения мер по сокращению затрат, которые необходимы для спасения бренда от банкротства. По словам источников, американский концерн планирует разработать четкую стратегию для Opel к концу марта.',17),(55,1,'General Motors ведет переговоры о реструктуризации Opel',17),(56,2,'12/01/2012',17),(57,3,'Ford||отзывы',18),(58,5,'Ford отзывает почти 450 000 автомобилей по причине различных технических неисправностей в ряде моделей, говорится в сообщении компании. Как передает (С) Associated Press, почти половина отозванных автомобилей – мини-фургоны Ford Freestar и Mercury Monterey 2004-2005 гг., в работе трансформаторов у которых часто наблюдаются неисправности. Еще половина – внедорожники Ford Escape 2001-2002 гг. Неплотно закрывающаяся крышка резервуара для тормозной жидкости у этих автомобилей может привести к коррозии металлических элементов и потенциальному возгоранию.||Ford сообщает, что оповестит владельцев автомобилей, попадающих под отзыв, и бесплатно произведет замену неисправных частей.',18),(59,1,'Ford отзывает полмиллиона автомобилей',18),(60,2,'12/01/2012',18),(61,3,'Renault',19),(62,5,'Парижский суд отказался рассматривать иск наследников основателя Renault, требовавших от государства выплаты компенсации за национализацию компании в 1945 г.||В мае 2011 года семеро внуков Рено обратились с соответствующим иском в суд в Париже. Их дед Луи Рено в сентябре 1944 г. был арестован и обвинен в сотрудничестве с нацистами. Спустя месяц он умер в тюрьме, не дождавшись суда. В январе 1945 г. власти Франции решили национализировать Renault.||Наследники Рено заявили, что национализация компании, крупнейшим акционером которой является французское государство, была неконституционной и могла быть проведена лишь в случае, если бы владелец Renault был признан виновным в суде, передает Би-би-си.',19),(63,1,'Суд отказался рассматривать иск наследников Renault',19),(64,2,'12/01/2012',19),(65,3,'Audi||новинки',20),(66,5,'Вcлед за рассекречиванием облика салона новой A3 Audi опубликовала наконец официальные фото автомобиля, до сих пор виденного только на шпионских фото.||На опубликованных фотографиях A3 представлен в кузове хэтчбек. Напомним, ранее в компании сообщали, что модель будет выходить в четырех типах кузова: седан, трехдверный хэтчбек, пятидверный хэтчбек и кабриолет. Автомобиль получил более угловатый кузов и массивную радиаторную решетку в духе концепта, дебютировавшего на прошлогоднем Женевском автосалоне. Ожидается, что в этом году Audi представит серийную модель A3 там же, в Женеве.||Audi пока не сообщила официальных технических характеристик A3, но можно предположить, что новинка будет оснащаться широкой линейкой бензиновых двигателей мощностью 105-210 л.с. и турбодизелей (до 170 л.с.). Несколько позже ожидается и появление гибридной версии.',20),(67,1,'Audi опубликовала официальные фото A3 нового поколения в кузове хэтчбек',20),(68,2,'11/01/2012',20),(69,3,'Renault||финансы||автобизнес',21),(70,5,'Концерн Renault SA может отложить крупные вливания денежных средств в развитие бизнеса, чтобы избежать трудностей в 2012 году. Об этом пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на заявление операционного директора компании Карлоса Тавареса.||Выступая перед финансовой комиссией Национального собрания Франции, К. Таварес отметил, что руководство Renault в настоящее время находится в процессе рассмотрения вариантов сокращения расходов. Что касается переноса сроков инвестиций, то пока на этот счет нет полной уверенности, однако нельзя исключать подобного сценария, добавил он.||Среди крупнейших по объему инвестиций проектов Renault в текущем году – развитие предприятия по сборочному производству в городе Дуэ, а также перемещение производства фургонов из Барселоны в Сандувиль. На их реализацию потребуется около 650 млн евро.||Отметим, что Renault и другие европейские автопроизводители ожидают трудности в развитии бизнеса в текущем году. В частности, объе',21),(71,1,'Renault отложит развитие бизнеса из-за нехватки финансов',21),(72,2,'11/01/2012',21),(73,3,'АвтоВАЗ||статистика продаж автомобилей',22),(74,5,'Продажи ОАО «АвтоВАЗ» в России в 2011 г. выросли на 10,6% по сравнению с предыдущим годом и составили 578 387 автомобилей. Об этом сообщила пресс-служба компании.||По итогам 2011 г. в России было продано 142 930 автомобилей Lada Kalina (на 31,1% больше, чем в 2010 г.), 138 701 экземпляр Lada Priora (+10,5%), 122 473 машины Lada Samara (+16,1%). Также было продано 86 940 автомобилей Lada 2105/2107 (продажи снизились на 36,1% по сравнению с 2010 г.), 60 738 штук Lada 4&#215;4 (+36,1%) и 1023 авто Lada Granta.||В декабре 2011 г. на российском рынке было продано 43 172 автомобиля Lada, что на 2,7% больше, чем в ноябре 2011 г. На экспорт в декабре 2011 г. АвтоВАЗ отправил 6175 автомобилей Lada, всего за 2011 г. – 56 010 автомобилей.',22),(75,1,'АвтоВАЗ продал в России более полумиллиона авто',22),(76,2,'11/01/2012',22),(77,3,'ДТП||регионы',23),(78,5,'В Волгограде в результате дорожно-транспортного происшествия с участием маршрутного такси и фургона пострадали девять человек. Об этом сообщает пресс-служба главного управления МЧС РФ по Волгоградской области.||Инцидент произошел на проспекте Жукова в Дзержинском районе. Водитель маршрутного такси, следуя в сторону центра Волгограда, не справился с управлением, выехал на полосу встречного движения и столкнулся с фургоном «Газель», после чего обе машины отбросило на обочину.||Все пострадавшие доставлены в больницу, состояние одного из них медики оценивают как тяжелое. С пострадавшими и их родственниками работают психологи МЧС.',23),(79,1,'Маршрутка врезалась в «Газель», 9 человек пострадали',23),(80,2,'11/01/2012',23),(81,3,'Москва||дороги',24),(82,5,'В Москве в 2012 году начнется реконструкция семи вылетных магистралей, сообщает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на столичный департамент по конкурентной политике. Напомним, что в общей сложности в Москве планируется реконструировать все 19 вылетных магистралей. По девяти из них уже готова проектная документация. Это Варшавское, Каширское, Рублевское, Щелковское, Можайское, Ленинградское шоссе (от МКАД до станции метро «Сокол») и шоссе Энтузиастов, Волгоградский и Рязанский проспекты. Общая протяженность реконструируемой сети по 19 магистралям составляет 270 км.||«В 2012 году начнется реконструкция семи вылетных магистралей из первых девяти, по которым уже готовы проектные документы. Также в следующем году начнется сооружение развязок на пересечении основных городских трасс с МКАД. Сейчас там движение тормозят, как правило, либо светофоры, либо неудобно спроектированные съезды на «кольцо» и заезды с него. Именно из-за такой сложной и неудобной р',24),(83,1,'В Москве начинается реконструкция семи магистралей',24),(84,2,'11/01/2012',24),(85,3,'статистика||российский авторынок',25),(86,5,'На каждую тысячу жителей России приходится 250 автомобилей, то есть по статистике авто имеется у каждого четвертого жителя страны. Именно такие цифры приводит агентство «Автостат» по данным на начало 2012 года. По данным агентства, общий парк легковых автомобилей в РФ – около 35,5 млн единиц.||Как рассказал директор «Автостата» Сергей Целиков, автомобилизация населения в России в настоящее время находится на уровне автомобилизации европейских стран в 70-х годах прошлого столетия. В настоящее время на тысячу жителей стран Европы приходится от 480 до 600 легковых автомобилей.||Стоит отметить, что 52% российских машин по-прежнему старше 10 лет. Зато процент иномарок продолжает увеличиваться: по предварительной оценке, сейчас на их долю приходится около 46% автопарка страны.',25),(87,1,'«Автостат»: у каждого четвертого жителя России есть автомобиль',25),(88,2,'11/01/2012',25),(89,3,'KIA||статистика продаж автомобилей',26),(90,5,'Продажи KIA Motors Corporation за 2011 год составили рекордные 2 478 959 автомобилей. Таким образом, рост продаж по сравнению с прошлым годом составил 18,6%. Компания впервые в своей истории преодолела отметку в 2,4 млн проданных автомобилей.||Продажи марки в России составили 152 873 автомобиля. По сравнению с результатами, показанными по итогам 2010 года, продажи компании выросли на 46,6%. Таким образом, Россия стала самым быстрорастущим рынком для KIA в мире.||Самой продаваемой моделью марки в России стал Rio с результатом в 49 858 автомобилей. В том числе было продано 17 306 экземпляров Rio нового поколения (старт продаж состоялся 1 октября 2011 года). За ним следует cee’d – 28 868 автомобилей. Замыкает тройку лидеров Sportage с показателем 23 880.',26),(91,1,'KIA за год увеличила продажи автомобилей в полтора раза',26),(92,2,'11/01/2012',26),(93,3,'Москва||дороги',27),(94,5,'На севере столицы началась масштабная реконструкция Большой Академической улицы. Работы продлятся около года: по плану они должны быть завершены в I квартале 2013-го. Как сообщает пресс-служба департамента строительства столицы, проектом реконструкции предусмотрено: строительство и реконструкция 5 км дорожного полотна (будет организовано по три полосы движения транспорта в каждом направлении); строительство тоннеля длиной 410 м на пересечении улиц Михалковская и Прянишникова; три подземных пешеходных перехода и пять надземных пешеходных мостов.||В попадающих в зону реконструкции жилых домах проведут плановую замену оконных блоков на шумозащитные, здесь также появятся два шумозащитных экрана суммарной длиной 2,9 км.||В процессе реконструкции будут построены и модернизированы инженерные сооружения – дождевая канализация, водопровод, городская канализация, теплосеть. В результате проведенных работ магистраль будет обеспечена автоматизированной системой управления дорожным движением, прове',27),(95,1,'В Москве приступили к реконструкции Большой Академической улицы',27),(96,2,'11/01/2012',27),(97,3,'АвтоВАЗ||автобизнес||российский автопром',28),(98,5,'Акции АвтоВАЗа стали расти в цене на фоне позитивных корпоративных новостей, сообщает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на ММВБ. На 11:10 мск обыкновенные и привилегированные акции компании поднялись в цене на 8,4% и 12% соответственно.||На рост акций в цене повлияло в том числе то, что в конце прошлого года АвтоВАЗ договорился с Новолипецким металлургическим комбинатом (НЛМК) и Магнитогорским металлургическим комбинатом (ММК) о пролонгации годовых контрактов.||Кроме того, стало известно, что 30 декабря 2011 года крупнейший российский автопроизводитель завершил первый этап размещения допэмиссии в пользу своих основных акционеров – государственной корпорации «Ростехнологии», компании Troika Dialog Investments Limited («Тройка Диалог») и компании Renault.||По мнению трейдеров, на положительную динамику котировок акций повлияло и назначение директором по качеству компании Лорана Фофана из Renault.',28),(99,1,'Акции АвтоВАЗа стали расти в цене',28),(100,2,'11/01/2012',28),(101,3,'Honda||Acura||Детройт-2012||российский авторынок||мировой авторынок',29),(102,5,'В 2014 году Honda выведет на российский рынок автомобили принадлежащего ей бренда Acura. Об этом заявил президент и генеральный директор Honda Таканобу Ито на автосалоне в Детройте.||Acura настроена на завоевание новых рынков: согласно принятому плану развития, несколько раньше, в 2013 году, ее автомобили появятся в Объединенных Арабских Эмиратах и Саудовской Аравии, а затем произойдет и выход на рынки России и Украины. Напомним, бренд Acura изначально создавался как премиум-бренд – именно это, по мнению управления компании, и делает его как нельзя более подходящим для выбранных рынков. Во всех этих странах присутствует реальный спрос на большие и роскошные автомобили, то есть именно такие, на которых специализируется Acura.',29),(103,1,'В 2014 году на российском авторынке появятся автомобили Acura',29),(104,2,'11/01/2012',29),(105,3,'Volvo||концепты||гибриды||Детройт-2012',30),(106,5,'Президент и главный управляющий Volvo Cars Штефан Якоби лично представил гибридный концепт XC60, за его рулем выехав на сцену автосалона в Детройте. Несмотря на наличие слова «концепт» в названии новинки, автомобиль практически готов к запуску в серийное производство.||Силовая установка автомобиля аналогична силовой установке V60 с той разницей, что в последней используется дизельный ДВС. Заднюю ось XC60 вращает электромотор мощностью 70 л.с., питающийся от литиево-ионной батареи, а переднюю – четырехцилиндровый бензиновый турбодвигатель мощностью 280 л.с., что дает автомобилю суммарную мощность 350 л.с. и позволяет разгоняться с нуля до 100 км за 5,8 секунды. Его максимальная скорость составляет 209 км/ч.||Внедорожник может проехать 45 км на одной электротяге. Расход топлива в гибридном режиме – всего 2,3 л/100 км. Запас хода XC60 тоже впечатляет: 960 км.',30),(107,1,'Volvo представил гибридный концепт XC60 в Детройте',30),(108,2,'11/01/2012',30),(109,3,'Audi||A6||A6 Allroad||универсалы||Детройт-2012',31),(110,5,'На международном автосалоне в Детройте Audi представила новый «внедорожный» универсал A6 Allroad. У этой модели дорожный просвет на 6 см больше, чем у стандартного универсала A6. Кроме того, «внедорожная» версия получила решетку радиатора и передний бампер нового дизайна, релинги на крыше и защиту выхлопной трубы.||Audi A6 Allroad комплектуется пневматической подвеской, которую можно занизить на 15 мм для улучшения аэродинамики на высоких скоростях или повысить на 35 мм на бездорожье.||Универсал будет предлагаться с четырьмя моторами: бензиновым V6 объемом 3 л и мощностью 306 л.с., крутящий момент которого составляет 440 Н&#183;м, и тремя дизельными 3-литровыми двигателями мощностью 201, 242 и 309 л.с. Последний агрегатируется с восьмиступенчатой автоматической коробкой передач, а все остальные моторы – с семискоростной КПП.',31),(111,1,'Audi показала в Детройте универсал A6 Allroad',31),(112,2,'11/01/2012',31),(113,3,'ГИБДД-ГАИ||криминал',32),(114,5,'Следственный комитет Республики Алтай возбудил уголовное дело в отношении 38-летнего Сергея Егузекова, подозреваемого в попытке убить двух сотрудников правоохранительных органов (ст. 317 УК РФ). По данным следствия, вечером 1 января 2012 года Егузеков, находясь в состоянии алкогольного опьянения, произвел два прицельных выстрела в инспекторов ДПС группы ДПС ОГИБДД ММО МВД России «Майминский», которые находились в служебном автомобиле. Инцидент произошел в районе моста через р. Катунь вблизи села Аскат Чемальского района.||Егузеков был задержан и допрошен, в настоящее время он находится под стражей. В сообщении отмечается, что ранее он работал в ГИБДД. Расследование уголовного дела продолжается. Причины, побудившие задержанного стрелять в инспекторов ДПС, пока не уточняются.',32),(115,1,'Возбуждено дело на бывшего гаишника, пытавшегося застрелить своих коллег',32),(116,2,'11/01/2012',32),(117,3,'Hyundai||кадры',33),(118,5,'Новым гендиректором «Хендэ Мотор СНГ» с 10 января 2012 года стал Лим Бюн Квон. Как сообщили в пресс-службе компании, Лим Бюн Квон работает в компании Hyundai Motor с января 1987 года. В 2008 году он занял должность исполнительного директора департамента планирования и координации бизнеса, в 2010-м стал первым вице-президентом группы международного маркетинга, а в 2011-м – первым вице-президентом европейского дивизиона компании.||Прежний генеральный директор российского представительства компании Кюн Рэ Чо покинул свой пост и вступил в должность главы европейского департамента компании Hyundai Motor.||Напомним, созданная в 1967 году Hyundai Motor Co. в настоящее время преобразована в автостроительную группу Hyundai-KIA, которая объединяет более двух десятков филиалов и подразделений, связанных с производством автомобилей. По всему миру в компании заняты свыше 75 тыс. человек. Автомобили Hyundai продаются в 193 странах мира.||«Хендэ Мотор Мануфактуринг Рус» – автомобильный завод Hyundai,',33),(119,1,'В «Хендэ Мотор СНГ» назначен новый генеральный директор',33),(120,2,'11/01/2012',33),(121,3,'Audi',34),(122,5,'В Audi до последнего не разглашали информацию о новом A3, который должен появиться в продаже уже в этом году. Вчера, 10 декабря, Audi наконец рассекретила внешний вид салона A3 на Международной выставке потребительской электроники (CES) в Лас-Вегасе.||Салон автомобиля получил новый дизайн. Приборная панель наклонена к водителю, что обеспечивает удобный доступ ко всем системам. ||Появился подъемный 7-дюймовый экран развлекательно-информационной системы, разработанной совместно с Nvidia. В сердце системы – двухъядерный процессор Tegra 2. В дальнейшем она станет основой системы MMX, отвечающей за навигацию, связь с интернетом и телефонную связь в автомобиле. Пока же для телефонной связи и SMS A3 дополнительно оснащают док-станцией, подходящей для всех моделей телефонов.||О дате первого показа автомобиля пока не сообщается, но ожидается, что Audi представит новинку на автосалоне в Женеве. В компании подтвердили, что запуск модели в производство должен произойти в самом ближайшем будущем.',34),(123,1,'Audi показала салон обновленной модели A3',34),(124,2,'11/01/2012',34),(125,3,'ДТП',35),(126,5,'В Курганской области один человек погиб и семеро получили травмы в результате ДТП, причиной которого стала невнимательность при совершении обгона по встречной полосе. Как сообщает областное управление ГИБДД, среди пострадавших – двое детей.||Как было установлено, девушка 1986 года рождения на «Жигулях» 6-й модели ехала со стороны Кургана в сторону г. Шадринска. На 242-м км автодороги Екатеринбург – Курган водитель при совершении обгона не убедилась в безопасности этого маневра и допустила столкновение со встречным автомобилем «ВАЗ-21101», за рулем которой находился водитель 1987 года рождения.||В результате происшествия госпитализированы оба водителя, два пассажира «шестерки» (женщина 1975 г. рождения, мальчик 1999 г. рождения) и три пассажира «десятки» (женщина 1967 г. рождения, девушка 1990 г. рождения и девочка 2001 г. рождения). Еще один пассажир «ВАЗ-21061» – мужчина 1950 г. рождения – от полученных травм скончался на месте.||«Водитель «шестерки» имеет стаж всего 2,5 года. За врем',35),(127,1,'Невнимательность при обгоне стала причиной ДТП, 1 человек погиб и 7 пострадали',35),(128,2,'11/01/2012',35),(129,3,'ДТП',36),(130,5,'В Республике Алтай в результате ДТП погибли пять человек, в том числе сотрудники Онгудайской сельской администрации. Как сообщили в правительстве республики, сегодня утром на Чуйском тракте в Шебалинском районе Республики Алтай произошла крупная автомобильная катастрофа.||Служебная «Волга» Онгудайской сельской администрации, в которой находились четыре пассажира, направлявшиеся в служебную командировку в Горно-Алтайск, столкнулась с грузовым фургоном Hino. В результате аварии погибли водитель и все пассажиры «Волги». В числе погибших – глава Онгудайского сельского поселения Андрей Письменных. Водитель и пассажир грузового фургона госпитализированы в Майминскую больницу с травмами различной степени тяжести.||По поручению главы Республики Алтай Александра Бердникова создана правительственная комиссия по расследованию обстоятельств катастрофы под руководством вице-премьера Роберта Пальталлера, который выехал на место происшествия. Родственникам погибших будет оказана материальная помощь.',36),(131,1,'В ДТП с участием служебной «Волги» и грузовика погибло пять человек',36),(132,2,'11/01/2012',36),(133,3,'Chevrolet||концепты||Детройт-2012',37),(134,5,'На автосалоне в Детройте были представлены два спортивных концепта: Chevrolet Code 130R и Tru 140S. Многие посетители отметили эмблему, ассоциирующуюся с Chevrolet Corvette, – два скрещенных флага. Как пояснил Николас Дэвид, ведущий дизайнер Gеneral Motors, спроектировавший Tru 140S, в прошлом эта эмблема появлялась на самых разных спортивных моделях Chevrolet, от Convair до Corvette, но в настоящее время, действительно, сохранилась только как элемент дизайна последнего. Новые концепты возрождают старую традицию, возвращая спортэмблему.||Code 130R и Tru 140S ориентированы на одну и ту же целевую аудиторию. Это преимущественно молодежь, заинтересованная в стильных спорткарах, но предпочитающая не слишком дорогие модели.||Оба автомобиля представляют собой четырехместные купе. Code 130R получил задний привод, а трехдверный Tru 140S – передний, но на этом основные различия в технических характеристиках и заканчиваются: под капотом обоих концептов оказался 1,4-литровый турбодвигатель Ecotec',37),(135,1,'Chevrolet показал в Детройте два спортивных концепта',37),(136,2,'11/01/2012',37),(137,3,'Renault-Nissan||мировой авторынок||статистика||Детройт-2012',38),(138,5,'В 2011 году группа Renault-Nissan продала 8,03 млн автомобилей – на 750 000 больше, чем за предыдущий год. Об этом сообщил исполнительный директор альянса Карлос Гон. Всего за год было продано 2,72 млн автомобилей Renault, 4,67 млн – Nissan и 0,64 млн – АвтоВАЗа, передает (C) Reuters.||«Альянс достиг повышения объема продаж на 10,3%, несмотря на все трудности, с которыми мы столкнулись в 2011 году. Среди них и мартовское землетрясение в Японии, и наводнение в Таиланде, и ненормальное усиление иены на фоне нестабильности в экономике Европы», – рассказал К. Гон. По его словам, в ближайшем будущем альянс намерен сконцентрировать усилия на растущем китайском рынке, который играет значительную роль в планах по развитию группы Renault-Nissan до 2016 года. В речи, которую руководитель компании произнес на стартовавшем автосалоне в Детройте, он подчеркнул, что опыт и подход, используемый в Nissan, во многом поможет альянсу упрочить позиции на азиатском рынке.',38),(139,1,'Группа Renault-Nissan в 2011 году продала 8,03 млн автомобилей',38),(140,2,'11/01/2012',38),(141,3,'Toyota||гибриды||новинки||Детройт-2012',39),(142,5,'На автосалоне в Детройте Toyota показала серийную версию гибрида Prius c, прототип которого был продемонстрирован здесь же год назад. Новинка представляет собой пятидверный хэтчбек. Гибридная силовая установка включает 1,5-литровый четырехцилиндровый двигатель и вспомогательный электрический мотор. Их суммарная мощность составляет 99 л.с. – немного, но для данной модели разработчики ставили во главу угла не мощность, а экономичность. Действительно, автомобиль довольно экономичен: расход топлива составляет 4,4 л/100 км в режиме городской езды и 5,1 л/100 км на шоссе. Никель-металлгидридная батарея на 144 вольта позволяет автомобилю проезжать небольшое расстояние (около 1,6 км) на одном электричестве на скорости не выше 40 км/ч.||Салон оснащен автоматическим климат-контролем и 3,5-дюймовым экраном развлекательно-информационной системы, поддерживающей подключение по Bluetooth, USB и iPod. Она может быть заменена на более продвинутую систему Entune с 6,1-дюймовым экраном и GPS-навигацией.|',39),(143,1,'Toyota представила серийную версию гибрида Prius c',39),(144,2,'11/01/2012',39),(145,3,'Mercedes-Benz||spy photo',40),(146,5,'В интернете появились шпионские фото Mercedes-Benz SLS AMG Black Series. Автомобиль проходит дорожные тексты в жестких условиях: кадры сделаны сделаны неподалеку от границы полярного круга.||Ожидается, что разработчики поместили под капот автомобиля модифицированный 6,3-литровый двигатель V8, мощность которого может достигать 641 л.с. Вероятно, силовая установка будет аналогична той, что была разработана для купе C63 AMG Black Series. В этом случае двигатель будет работать в паре с семиступенчатой автоматической коробкой передач Speedshift MCT. Такая силовая установка позволит автомобилю ускоряться с нуля до 100 км/ч примерно за 3,8 секунды и достигать максимальной скорости в 322 км/ч.||Mercedes-Benz SLS AMG Black Series должен появиться на рынке в будущем году. По неподтвержденным данным, его цена будет начинаться от 260 000 евро.',40),(147,1,'Mercedes-Benz SLS AMG Black Series засняли во время тестов в лютый мороз',40),(148,2,'11/01/2012',40),(149,3,'ДТП',41),(150,5,'На совершенствование системы оказания медицинской помощи пострадавшим при ДТП в 2012 г. в РФ будет выделено 5 млрд 933 млн 500 тыс. руб. Соответствующее распоряжение правительства РФ от 26 декабря 2011 г., которым установлен порядок предоставления регионам субсидий из федерального бюджета на софинансирование мероприятий, направленных на совершенствование медицинской помощи пострадавшим при ДТП, подписал премьер-министр Владимир Путин.||Как говорится в материалах Министерства здравоохранения и социального развития (Минздравсоцразвития) РФ, в 2012 г. в программу помощи при ДТП был отобран 21 регион РФ.||В 2008-2010 гг. в реализации мероприятий программы принимали участие 49 субъектов РФ, в том числе в 2008 г. – 14, 2009 г. – 13, 2010 г. – 22 субъекта. На эти цели были предусмотрены средства федерального бюджета в объеме 9 млрд 180 млн 200 тыс. руб.||В результате реализации мероприятий по совершенствованию организации медицинской помощи пострадавшим при ДТП планируется снизить смертность ',41),(151,1,'На оказание помощи пострадавшим в ДТП выделят 6 млрд руб.',41),(152,2,'10/01/2012',41),(153,3,'АвтоВАЗ||кадры',42),(154,5,'Бывший директор программы легких грузовых автомобилей Лоран Фофана сменил Паскаля Фелтена на посту директора по качеству ОАО «АвтоВАЗ». Об этом говорится в сообщении пресс-службы компании. Приказ о назначении Л. Фофана подписал президент АвтоВАЗа Игорь Комаров.||Возглавлявший ранее дирекцию по качеству П. Фелтен заканчивает свою работу на АвтоВАЗе в связи с завершением трехлетнего контракта. Он возглавит предприятие по производству крупногабаритных автомобилей в компании Renault.||Л. Фофана с 1994 г. работал в Renault. С 1994 г. по 2000 г. был заместителем директора по производству силовых агрегатов; в 2000-2005 гг. занимал пост директора проекта двигателей (занимался улучшением качества существующих двигателей в сотрудничестве со службами проектирования и производства, а также работал с поставщиками). В 2005-2011 гг. был назначен директором программы легких грузовых автомобилей.',42),(155,1,'АвтоВАЗ меняет директора по качеству',42),(156,2,'10/01/2012',42),(157,3,'Nissan||Juke||спецверсии||кроссоверы',43),(158,5,'Пока внимание автомобильной общественности сосредоточенно на детройтских новинках, в Брюсселе стартовал свой автосалон. На бельгийском мотор-шоу Nissan впервые показал спецверсию Juke Shiro. Интересно, что название машины переводится с японского как «белый», однако представленный автомобиль выкрашен в черный цвет.||В салоне спецверсии установлена белая центральная консоль, прострочка на черных кожаных сиденьях и руле также белого цвета. Снаружи от стандартного кроссовера Juke спецверсия отличается только колесными дисками темно-серого цвета диаметром 17 дюймов и серебристой отделкой зеркал и дверных ручек.||Недавно Nissan представил «заряженную» версию той же модели – Juke-R. Этот автомобиль получил силовой агрегат, трансмиссию и элементы подвески от суперкара GT-R. Juke-R оснащен 3,8-литровой «шестеркой», выдающей 485 л.с., шестиступенчатой роботизированной трансмиссией, а также полным приводом. «Заряженная» версия способна развивать 100 км/ч уже спустя 3,7 сек. после старта, а максим',43),(159,1,'Nissan впервые показал «белую» спецверсию Juke Shiro',43),(160,2,'10/01/2012',43),(161,3,'мировой автопром||Fiat-Chrysler||Peugeot Citroen',44),(162,5,'Альянс Fiat-Chrysler рассмотрит возможность объединения с концерном Peugeot Citroen, пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на газету Corriere Della Sera. «Для достижения цели по продажам 6 млн автомобилей в год к 2014 году необходима консолидация автомобильной отрасли в Европе и за ее пределами», – приводит газета слова главы альянса Fiat-Chrysler Серджио Маркионне.||В то же время аналитик компании LMC Automotive Джефф Шустер выразил мнение, что кроме Peugeot Citroen среди претендентов на создание альянса с Fiat-Chrysler могут быть Volvo Car Corp. и Opel.||Напомним, С. Маркионне сообщил, что окончательное слияние группы Fiat с Chrysler будет возможно не ранее 2015 года. По его словам, 2012 год станет очень сложным для Chrysler, а крупные перемены начнутся для компании только в 2013-м. Имя нового генерального директора и преемника С. Маркионне в Chrysler также станет известно не ранее 2015 года.',44),(163,1,'Альянс Fiat-Chrysler может объединиться с Peugeot Citroen',44),(164,2,'10/01/2012',44),(165,3,'Mercedes-Benz||новинки||Детройт-2012',45),(166,5,'Mercedes-Benz представил более легкую версию родстера SL на автосалоне в Детройте. Новинка на на 55 мм длиннее и на 57 мм шире, чем нынешняя модель SL R230. Тем не менее она на 140 кг легче. Такого результата удалось достичь благодаря тому, что весь кузов выполнен из алюминия. В компании отмечают, что SL шестого поколения больше соответствует своему названию – сокращению от Sport Leicht, «спортивный и легкий».||Родстер выйдет в двух вариантах: SL 350 и SL 500 (в США последний будет выпускаться как SL 550). Вес SL 350 составляет 1685 кг, более мощный SL 500 окажется чуть тяжелее: 1785 кг. Под капотом первой версии окажется двигатель V6 мощностью 306 л.с., а второй достанется V8 мощностью 425 л.с. Оба агрегата устанавливаются в паре с семиступенчатой АКПП. В дальнейшем планируется выпустить версию AMG и, возможно, SL с дизельным мотором.||Особый интерес представляет крыша: стеклянная ее часть может быть изготовлена как из обычного стекла, так и с технологией Magic Sky Control, позволяюще',45),(167,1,'Mercedes-Benz SL стал на 140 кг легче',45),(168,2,'10/01/2012',45),(169,3,'Falcon||F7||суперкары||Детройт-2012',46),(170,5,'Тюнинговая компания Falcon F7 из Мичигана представила на международном мотор-шоу в Детройте суперкар F7. Отметим, что серийная версия модели дебютировала ровно через год после премьеры концепта Falcon Mach 7.||Установленный на концептуальную версию двигатель Chevrolet V8 LS7 объемом 7 литров был доработан, в том числе получил новый блок управления ECU и впускной коллектор из углепластика. В результате мотор производственного варианта выдает 600 л.с. и 800 Н&#183;м.||Подвеску Falcon F7 позаимствовал у спорткупе Corvette последнего поколения. Кузов машины выполнен из карбона. Вес авто равен 1263 кг.||В салоне установлены спортивные кожаные сиденья, интерьер отделан алюминием.||Производство модели стартует уже в этом году. Всего Falcon планирует выпускать по 20 суперкаров F7 в год. Стоимость машины составит 225 000 долларов.',46),(171,1,'В Детройте дебютировал суперкар Falcon F7',46),(172,2,'10/01/2012',46),(173,3,'АвтоВАЗ||российский авторынок',47),(174,5,'АвтоВАЗ получил 4 млрд 859 млн 244 тысяч 859 рублей в рамках размещения дополнительной эмиссии акций, которое проводилось с марта 2011 года. Об этом говорится в материалах компании. Акции размещались по закрытой подписке, размещение было завершено 30 декабря 2011 года.||Всего компания разместила 434 173 411 акций номиналом 5 рублей по цене 40,24 руб. Из общего количества бумаг денежными средствами оплачено 120 756 582 акции (28% от общего числа бумаг). Остальные акции оплачены путем зачета денежных требований.||В результате ГК «Ростехнологии» увеличила свой пакет в АвтоВАЗе на 313 416 829 акций. Компания Renault, которой принадлежит 25% акций АвтоВАЗа, по итогам дополнительной эмиссии получила 108 543 355 акций (на 4 млрд 368 млн руб.). ИК «Тройка Диалог» по итогам дополнительной эмиссии приобрела 10 854 335 акций (на 437 млн руб.).||Напомним, основные совладельцы АвтоВАЗа – ГК «Ростехнологии», «Тройка Диалог» и Renault – подписали в июле 2010 г. соглашение об увеличении уставного капи',47),(175,1,'Дополнительные акции принесли АвтоВАЗу почти 5 млрд рублей',47),(176,2,'10/01/2012',47),(177,3,'ДТП',48),(178,5,'В Нижегородской области полицейский, по вине которого в ДТП погибли двое его коллег, приговорен к трем годам лишения свободы в колонии-поселении. Об этом сообщает следственное управление Следственного комитета РФ по Нижегородской области.||Владимир Лыков, полицейский Дивеевского отдела МВД России, 2 сентября 2011 года ехал на служебном автомобиле ГАЗ-31105 из г. Первомайска в с. Дивеево. На 53-м км автодороги при обгоне другого автомобиля В. Лыков выехал на полосу встречного движения и не справился с управлением. В результате автомобиль врезался в световую опору. Находившиеся в салоне машины пассажиры – начальник полиции отдела МВД РФ «Дивеевский» и начальник одного из отделений – погибли.||Суд признал В. Лыкова виновным по ч. 5 ст. 264 Уголовного кодекса РФ (нарушение лицом, управляющим автомобилем, правил дорожного движения, повлекшее по неосторожности смерть двух лиц).',48),(179,1,'Полицейский, по вине которого в ДТП погибли двое его коллег, осужден на 3 года',48),(180,2,'10/01/2012',48),(181,3,'Linkoln||концепты||новинки||Детройт-2012',49),(182,5,'На автосалоне в Детройте представили концепт Lincoln MKZ. Стоит отметить, что это первая новинка автопроизводителя с тех пор, как в Ford сообщили о намерении инвестировать миллиард долларов в Lincoln к 2014 году.||Автомобиль получил светодиодные фары, стеклянную крышу и фирменную решетку радиатора в виде крыльев, оставшуюся неизменной с 1938 года, когда вышел классический Lincoln Zephyr.||Салон выполнен в лучших традициях бренда и представляет собой сочетание высоких технологий и роскошных материалов. На передней панели установлен 9-дюймовый сенсорный экран, автомобиль оснащен последней версией коммуникативно-развлекательной системы MyLincoln Touch.||Вице-президент Ford Деррик Кьюзак рассказал, что Lincoln MKZ – не просто новый концепт: он знаменует появление нового бренда. Это первый шаг на пути к возвращению Lincoln.||В продажу новый седан поступит уже в текущем году.',49),(183,1,'На автосалоне в Детройте представили концепт Linkoln MKZ',49),(184,2,'10/01/2012',49),(185,3,'Chevrolet||Code 130R||купе||концепты||Детройт-2012||автосалоны',50),(186,5,'General Motors делает ставку на молодых покупателей. Привлечь их интерес компания планирует с помощью новой компактной модели с яркой внешностью, которая выйдет под маркой Chevrolet.||На открывшемся в Детройте международном автосалоне компания показала прообраз будущей новинки – концепт-кар Chevrolet Code 130R, который представляет собой четырехместное купе, укомплектованное системой заднего привода. По данным General Motors, серийная версия автомобиля будет потреблять менее 6 л бензина на 100 км пути.||Под капотом Chevrolet Code 130R установлен турбированный четырехцилиндровый двигатель объемом 1,4 л мощностью 150 л.с., крутящий момент которого составляет 200 Н&#183;м. Мотор снабжен системой eAssist, подключающей электричество в случае необходимости. Двигатель может агрегатироваться как с шестиступенчатой механической, так и с шестискоростной автоматической трансмиссиями.||По данным, обнародованным General Motors, стоимость автомобиля составит 25 000 долларов.',50),(187,1,'Chevrolet готовит купе стоимостью 25 000 долларов',50),(188,2,'10/01/2012',50),(189,3,'Москва||дороги',51),(190,5,'В Москве на пересечении Волгоградского проспекта и Люблинской улицы будет построен тоннель длиной в полкилометра, сообщила сегодня пресс-служба столичной префектуры Юго-Восточного административного округа.||Строительство тоннеля позволит отказаться от светофора на данном участке и повысит пропускную способность. Ширина тоннеля составит около 30 м – предусмотрено по две полосы в каждом направлении. Выезд транспорта с Люблинской улицы в направлении центра будет осуществляться через транспортный узел на пересечении с Волжским бульваром, где также запланировано строительство новых левоповоротных эстакад.||Они призваны в первую очередь разгрузить движение по Волгоградскому проспекту из области и в центр от Волжского бульвара. Ширина эстакад составит 9 м – по две полосы движения в каждом направлении. Строительство еще одной эстакады шириной 9,5 метра позволит отказаться от светофора на участке между Волжским бульваром и Окской улицей.||Кроме того, на участке Волгоградского проспекта от Любли',51),(191,1,'С пересечения Волгоградского проспекта и Люблинской улицы уберут светофор',51),(192,2,'10/01/2012',51),(193,3,'гибриды||новинки||Toyota||Yaris',52),(194,5,'Toyota рассказала о гибридной версии Yaris. Для того чтобы адаптировать свою фирменную силовую установку под компактный автомобиль, инженерам компании пришлось уменьшить ее в размерах. Гибридный привод для Yaris включает новый бензиновый двигатель объемом 1,5 л. В результате изменений вес установки уменьшился на 20%. Мощность гибридной установки составляет 98 л.с.||Внешний вид гибридного Yaris был доработан, в том числе автомобиль получил новые передний и задний бамперы. Как отметили в Toyota, передний бампер измененного дизайна станет новой визитной карточкой марки.||Мировая премьера Toyota Yaris Hybrid состоится на международном мотор-шоу в Женеве в марте этого года. Вскоре после этого стартуют мировые продажи модели.',52),(195,1,'Toyota весной запустит продажи гибридного Yaris',52),(196,2,'10/01/2012',52),(197,3,'Acura||Детройт-2012',53),(198,5,'Хотя представленная на автосалоне в Детройте модель Acura RDX нового поколения и названа концептом, можно предположить, что серийная версия будет мало чем отличаться. Ее продажи начнутся уже через несколько месяцев.||Как обещают разработчики, RDX второго поколения станут более комфортными и экономичными. Под капотом новинки – 3,5-литровый V6 мощностью 273 л.с. (на 33 л.с. больше, чем у предыдущего поколения). Несмотря на увеличение мощности, автомобиль потребляет мало топлива: 11,7 л/100 км в городском режиме и всего 8,3 л/100 км на трассе, что делает его самым экономичным в своем классе.||Внедорожник получит шестиступенчатую автоматическую трансмиссию с новой системой полного привода и электроусилитель руля. В комплектацию также войдут система бесключевого доступа, кнопка запуска двигателя, камера заднего вида, навигатор с голосовым управлением, система голосовой отправки SMS, коммуникационная система AcuraLink Satellite, система оповещения о погодных условиях и мультимедийная система',53),(199,1,'В Детройте представили концепт Acura RDX нового поколения',53),(200,2,'10/01/2012',53),(201,3,'Dodge||новинки||Детройт-2012',54),(202,5,'Dodge официально представил новый Dart на автосалоне в Детройте. Автомобиль, призванный заменить на рынке модель Caliber, появится в продаже уже во втором квартале текущего года.||Новый седан построен на платформе Alfa Romeo Giulietta и отличается высокими аэродинамическими характеристиками.  В линейке его двигателей – 2,0-литровый агрегат Tigershark мощностью 160 л.с., 1,4-литровый турбо MultiAir аналогичной мощности и 2,4-литровый атмосферный MultiAir мощностью 184 л.с.||Салон автомобиля достаточно просторен, приборная панель снабжена подсветкой, а по центру передней панели установлен 8,4-дюймовый сенсорный экран информационно-развлекательной системы.||Цена новинки уже известна: Dart будет стоить от 15 995 долларов.',54),(203,1,'В Детройте состоялось официальное представление Dodge Dart',54),(204,2,'10/01/2012',54),(205,3,'Audi||концепты||Детройт-2012',55),(206,5,'На автосалоне в Детройте Audi показала концепт на базе своего компактного кроссовера – Q3 Vail. Он назван в честь популярного курорта в штате Колорадо и предназначен для любителей зимних видов спорта. Клиренс у новинки больше, чем у стандартного Q3, а на крыше появились светодиоды для освещения пространства вокруг машины. На дне багажника разместился коврик с подогревом: по словам разработчиков, на него удобно сесть, чтобы переобуться в лыжные ботинки. Автомобиль оснащен целым рядом систем, которые могут быть полезны в экспедициях: датчик высоты, функция определения точных координат, усовершенствованный навигатор MMI Plus, запоминающий пройденный маршрут.||Под капотом «зимнего» кроссовера – 2,5-литровый пятицилиндровый двигатель мощностью 310 л.с., работающий в паре с семиступенчатой трансмиссией S-tronic. Такая силовая установка позволяет автомобилю разгоняться с нуля до 100 км/ч за 5,5 секунд и развивать скорость до 262 км/ч.',55),(207,1,'Audi показала в Детройте концепт «зимнего» кроссовера',55),(208,2,'10/01/2012',55),(209,3,'Москва||дороги||стихия',56),(210,5,'За последние сутки на снегоплавильные пункты с улиц Москвы поступило 400 тыс. куб. м снега. Как сообщил сегодня журналистам пресс-секретарь заместителя мэра Москвы по вопросам жилищно-коммунального хозяйства и благоустройства Игорь Пергаменщик, с начала зимнего сезона коммунальщики убрали почти 6 млн куб. м снега.||Прошедшей ночью в уборке города было задействовано 7 тыс. единиц техники, в том числе 2,7 тыс. самосвалов. Вечером 9 января, в 22:00, проводилось сплошное прометание проезжей части. Сегодня в 4:00 было проведено повторное прометание дорог и обработка проезжей части реагентами. Всего с начала снегопада, то есть за последние двое суток, в Москве выпало около 12 см снега.||Заместитель мэра Москвы по вопросам жилищно-коммунального хозяйства и благоустройства Петр Бирюков дал поручение городским коммунальным службам продолжить уборку города. Снег будет вывозиться с лотковой части дорог, уборка продолжится на тротуарах, остановках общественного транспорта, подходах к социальным об',56),(211,1,'За последние сутки с улиц Москвы вывезли 400 тыс. куб. м снега',56),(212,2,'10/01/2012',56),(213,3,'Acura||Детройт-2012',57),(214,5,'Acura представила концепт ILX на автосалоне в Детройте. В компании уже подтвердили, что автомобиль пойдет в серийное производство этой весной. Он будет создан на базе Honda Civic.||Габариты новинки – 4555 мм в длину, 1890 мм в ширину и 1425 мм в высоту при колесной базе 2670 мм. Покупатели смогут выбрать одну из трех силовых установок: 2,0-литровый четырехцилиндровый двигатель с пятиступенчатой АКПП, более мощный 2,4-литровый двигатель с шестиступенчатой механической коробкой передач и экономичную гибридную версию включающую 1,5-литровый двигатель.||Автомобиль будет оснащаться кнопкой зажигания, светодиодными противотуманными фарами, системой опознания голоса, интернет-радио, системой голосовой отправки СМС, камерой заднего вида и аудиосистемой с 10 динамиками.',57),(215,1,'Концепт Acura ILX станет серийной моделью уже этой весной',57),(216,2,'10/01/2012',57),(217,3,'ДТП',58),(218,5,'В Кишиневе 11 пассажиров троллейбуса, следовавшего по 21-му маршруту, пострадало в ДТП по причине невнимательности водителя. Обстоятельства аварии расследует полиция. По предварительным данным, водитель отвлекся на разговоры с пассажиром или кондуктором и умудрился не заметить, что троллейбус движется прямо на столб.||Всего в троллейбусе ехало около 30 пассажиров. Из них 11 получили травмы различной степени тяжести, девятеро в настоящее время находятся в больнице. Мэр Кишинева Дорин Киртоакэ отметил, что все граждане, пострадавшие в результате данного ДТП, получат материальную помощь. В то же время глава Управления электрического транспорта Кишинева Георгий Моргоч заявил, что водитель, из-за невнимательности которого произошла эта авария, будет уволен, поскольку это уже второе ДТП с его участием за пять месяцев в троллейбусном парке Кишинева.',58),(219,1,'Водитель троллейбуса отвлекся на разговор и врезался в столб, 11 человек пострадало',58),(220,2,'10/01/2012',58),(221,3,'Volkswagen||электрокары||концепты||Детройт-2012',59),(222,5,'На автосалоне в Детройте Volkswagen представил E-Bugster – концепт электрической версии Beetle последнего поколения. Автомобиль получил двигатель мощностью 114 л.с., питающийся от литиево-ионной батареи весом 315 кг, дающей впечатляющий запас хода: 161 км. При подключении к устройству быстрой подзарядки батарея заряжается на 80% всего за полчаса.||От обычного Beetle концепт E-Bugster отличает новая приборная панель, разработанная специально для электрокаров. Разработчики также дали экологичной новинке светодиодные фары дневного света и 20-дюймовые легкосплавные колесные диски в стиле дисков Fuchs, которыми могут похвастаться многие ретромодели Beetle.||<center><object style=\"height: 390px; width: 640px\"><param name=\"movie\" value=\"http://www.youtube.com/v/j5n7yZn2TME?version=3&feature=player_detailpage\"><param name=\"allowFullScreen\" value=\"true\"><param name=\"allowScriptAccess\" value=\"always\"><embed src=\"http://www.youtube.com/v/j5n7yZn2TME?version=3&feature=player_detailpage\" type=\"appl',59),(223,1,'Volkswagen представил концепт электромобиля E-Bugster. ВИДЕО',59),(224,2,'10/01/2012',59),(225,3,'ДТП',60),(226,5,'В Свердловской области в результате дорожно-транспортного происшествия получили травмы шесть человек, в том числе трое детей. Об этом сегодня сообщила пресс-служба регионального управления ГИБДД. Установлено, что водитель автомобиля ВАЗ-2115, мужчина 1980 года рождения, не справился с управлением на скользкой дороге. Автомобиль занесло, он выехал на полосу встречного движения и столкнулся с Chevrolet Niva.||В момент ДТП в автомобиле ВАЗ-2115 находилась семья из пяти человек, в том числе два мальчика, восьми и двух лет, и девочка в возрасте 14 лет. Все они получили травмы и были госпитализированы. Водитель Chevrolet Niva также был доставлен в больницу с переломом ноги.||В ГИБДД отмечают, что более трагических последствий удалось избежать благодаря тому, что все дети были пристегнуты ремнями безопасности, а младший находился в детском кресле.',60),(227,1,'Ремни безопасности и детское кресло спасли жизнь трем детям',60),(228,2,'10/01/2012',60),(229,3,'Fiat||Chrysler||мировой авторынок',61),(230,5,'Слияние группы Fiat с Chrysler будет возможно не ранее 2015 года. Именно такой срок назвал глава Fiat и генеральный директор Chrysler Group LLC Серджио Маркионне в интервью Detroit Free Press. По его словам, 2012 год станет очень сложным для Chrysler, а крупные перемены начнутся для компании только в 2013-м. Имя же нового генерального директора, преемника Маркионне в Chrysler, станет известно не ранее 2015 года.||Напомним, Fiat получил начальные 20% акций и управленческий контроль в Chrysler в 2009 году после процедуры банкротства американской компании. Увеличение доли Fiat в акционерном капитале Chrysler стало результатом достижения американской компанией второго из трех целевых ориентиров, оговоренных в партнерских соглашениях между автоконцернами. Fiat автоматически увеличил долю на 5% после того, как Chrysler достиг накопленной после своего банкротства выручки от продаж за пределами Северной Америки в 1,5 млрд долларов, а также заключил дилерское соглашение. В соответствии с ним, F',61),(231,1,'Окончательное слияние Fiat и Chrysler произойдет не ранее 2015 года',61),(232,2,'10/01/2012',61),(233,5,'Шведский автопроизводитель анонсировал новый XC60 Plug-in Hybrid, который будет продемонстрирован широкой публике на грядущем автосалоне в Детройте.||Основным мотором кроссовера станет турбированный бензиновый агрегат объемом 2,0 л и мощностью 280 л.с., весь момент которого будет передаваться на переднюю ось. Помимо ДВС, на XC60 Plug-in Hybrid будет установлен и 70-сильный электромотор, который будет отвечать за вращение задних колес. Сообщается, что силовая установка будет иметь три режима работы: Pure, Hybrid и Power.||В первом режиме автомобиль будет передвигаться только благодаря электромотору. Подобный вариант езды позволит преодолеть не более 56 километров пути без подзарядки. Что касается режима Hybrid, то здесь XC60 способен проехать на одной заправке до 960 километров, при этом средний расход топлива составит около 2,3 л на сто километров. Третий режим Power предлагает задействовать всю мощность кроссовера, обеспечивая динамичный разгон с нуля до сотни всего за 6,1 сек.',62),(234,1,'Volvo анонсировала гибридный XC60',62),(235,2,'08/01/2012',62),(236,5,'В Истринском районе Подмосковья произошла серьезная авария, в результате которой один человек погиб, еще трое получили серьезные травмы. На 40-м километре Новорижского шоссе в районе часа ночи столкнулись восемь легковых машин и один грузовик. По некоторой информации, в аварию попали Volkswagen Polo, Toyota Camry, грузовой автомобиль Renault Clio, BMW X3, Citroen С5, Range Rover, KIA cee’d, Honda Civic и Honda Accord.||В результате сильного удара один из водителей легкового автомобиля – 27-летний мужчина – скончался по дороге в больницу. Еще три человека получили тяжелые травмы, состояние одного из них врачи оценивают как тяжелое.||До сих пор на месте аварии работают сотрудники полиции, которые пытаются установить причины серьезного ДТП. По неофициальной информации, столкновение могло произойти из-за неправильного выбора скоростного режима водителем одной из иномарок.',63),(237,1,'На Новорижском шоссе в ДТП попали сразу девять автомобилей',63),(238,2,'08/01/2012',63),(239,5,'В Ростовской области выдвинут законопроект, согласно которому подрядчики будут отвечать за каждый участок плохо отремонтированных дорог денежной компенсацией, сообщает <a href=\"http://donnews.ru\" target=\"_blank\">«DonNews»</a>. Кроме того, планируется наложить ответственность на дорожников и за ДТП, которые произойдут из-за плохого состояния дорожного полотна. Источник сообщает, что законопроект будет рассмотрен уже на ближайшем заседании правительства Ростовской области.||Примечательно, что штраф будут взимать не за всю плохо отремонтированную дорогу, а за каждый ее километр, где будут обнаружены дефекты после проведения дорожных работ. Планируется, что данная система штрафов заставит подрядчика более добросовестно относиться к работе.||Кроме того, в проекте будут зафиксированы суммы, необходимые для ремонта дорог области. Сообщается, что капитальный ремонт километра дороги областного значения будет стоить 13,698 млн рублей. На обычный ремонт километра дороги будут расходовать не более',64),(240,1,'Правительство Ростовской обл.  будет штрафовать подрядчика за плохие дороги',64),(241,2,'08/01/2012',64),(242,3,'BMW||CUV||новинки||мировой авторынок',65),(243,5,'В BMW сообщили о намерении за ближайшие три года инвестировать 990 млн долларов в завод в Спартанбурге, Южная Каролина, что позволит выпустить до 350 000 автомобилей ежегодно. Эксперты компании прогнозируют увеличение спроса на кроссоверы в ближайшие годы, и BMW рассчитывает увеличить свое присутствие на этом рынке: в Спартанбурге производятся Х3, Х5 и Х6.||В компании также сообщили, что серийное производство кроссовера X4 будет запущено на этом же заводе. Четырехместный кроссовер займет место в линейке между X3 и X6. Он появится в продаже в 2014 году. ||Стоит отметить, что в настоящее время 70% автомобилей, произведенных на заводе в Спартанбурге, идет на экспорт.',65),(244,1,'BMW делает ставку на кроссоверы',65),(245,2,'13/01/2012',65),(246,3,'BMW',66),(247,5,'BMW представила две модификации купе и кабриолета 1-й серии. В модификации Edition Exclusive автомобиль получил хромированную решетку радиатора, матированную серебристую отделку зеркал и шесть вариантов окраски кузова. В салоне появились кожаные кресла Boston и спортивный руль. Для этой модификации доступны два варианта отделки салона алюминием или деревом.||Edition Sport получил спортивный тюнинг-пакет от BMW M, блестящую черную решетку радиатора и эксклюзивную окраску черный металлик. Стоит отметить спортивную настройку подвески и два комплекта легкосплавных колесных дисков на выбор: 17- и 18-дюймовые. Рулевое колесо – еще одна разработка BMW M. Помимо него дизайнеры добавили стальные педали и алюминиевую отделку салона, а также уникальные коврики и контрастную синюю прострочку на обшивке кресел.',66),(248,1,'BMW представила две модификации купе и кабриолета 1-й серии',66),(249,2,'13/01/2012',66),(250,3,'ГИБДД-ГАИ||криминал',67),(251,5,'В ночь с 12 на 13 января в Москве произошло необычное ограбление. Как передает пресс-служба столичного ГУ МВД России, пострадавшая от грабителей женщина позвонила в участок и сообщила, что ее только что ограбили на Краснопресненской набережной. По ее словам, мужчина в светоотражающем жилете с надписью ДПС остановил ее Peugeot и попросил открыть багажник. Она вышла из автомобиля, и тут внезапно появившийся второй мужчина кавказской внешности залез в салон через незакрытую дверь, схватил сумку и бросился бежать. Лжегаишник бросился догонять похитителя, после чего женщина их больше не видела.||В украденной сумке находились два сотовых телефона и личные документы владелицы. Пострадавшая оценила материальный ущерб в 10 500 тысяч рублей. По факту кражи возбуждено уголовное дело в соответствии с ч. 2 ст. 161 УК РФ (грабеж, совершенный группой лиц по предварительному сговору). Максимальное наказание по этой статье – семь лет лишения свободы со штрафом до десяти тысяч рублей.',67),(252,1,'Мошенник ограбил москвичку, прикинувшись гаишником',67),(253,2,'13/01/2012',67),(254,3,'Москва',68),(255,5,'В Москве все масштабнее разворачиваются работы по созданию новой велотранспортной инфраструктуры. Как сообщает пресс-служба Центрального административного округа столицы, управы десяти районов округа внесли предложения по размещению новых велодорожек и велосипедных парковок около объектов социальной сферы – предприятий общественного питания, развлекательных и культурных центров, а также крупных торговых комплексов. Учитывается также возможность строительства велодорожных трасс в рамках реконструкции столичных вылетных магистралей.||«Мы последовательно создаем условия для активного отдыха жителей и гостей центра Москвы. В теплый сезон на велосипеде можно не только кататься для здоровья в парках, скверах и на стадионах, но и достаточно удобно пересекать небольшие расстояния, не стоя при этом в пробках, – отметил префект Центрального округа Сергей Байдаков. – Создавая новые спортивные объекты и дополнительные возможности для временного хранения велотранспорта, мы хотим повысить качество ж',68),(256,1,'Власти Москвы выступают за велотранспорт',68),(257,2,'13/01/2012',68),(258,3,'MINI||spy photo',69),(259,5,'В интернете появились шпионские фото нового MINI Cooper. Прототип автомобиля, который должен появиться на рынке в 2013 году, проходит дорожные тесты в Европе. Несмотря на основательный камуфляж, можно рассмотреть, что новое поколение MINI Cooper порядком отличается от предыдущего обводами кузова, массивной решеткой радиатора и углом наклона лобового стекла.||В салоне можно рассмотреть некоторые детали из линеек BMW. Впрочем, вряд ли они будут перенесены в серийную версию модели: до сих пор в компании не было подобной практики.||Напомним, ранее сообщалось, что MINI Cooper следующего поколения получит новую платформу с передним приводом. Габариты автомобиля будут несколько больше, чем у последней версии модели. В компании пока не уточняли технических характеристик будущей новинки, но можно предположить, что линейка двигателей будет аналогична линейке BMW 1-й серии: 1,5-литровый трехцилиндровый мотор, 1,6-литровый четырехцилиндровый турбо и 2,0-литровый четырехцилиндровый агрегат.',69),(260,1,'В интернете появились шпионские фото нового MINI Cooper',69),(261,2,'13/01/2012',69),(262,3,'Москва||дороги',70),(263,5,'В Москве в ближайшее время будут подведены итоги торгов по реконструкции Можайского шоссе, Волгоградского и Рязанского проспектов. На первые два лота (реконструкция Можайского шоссе и Волгоградского проспекта) претендуют по две компании, на участие в торгах по третьему лоту подано три заявки. Начальная (максимальная) цена контракта на выполнение подрядных работ по Можайскому шоссе составляет 10,9 млрд рублей, по Волгоградскому проспекту – 4,7 млрд рублей, по Рязанскому проспекту – 9,5 млрд рублей. Срок проведения работ жестко ограничен: реконструкция должна длиться не более 24 месяцев.||Напомним, столичный департамент по конкурентной политике ранее объявил открытый конкурс на выполнение подрядных работ по реконструкции Можайского шоссе, Волгоградского и Рязанского проспектов. В настоящее время межведомственная городская конкурсная комиссия проводит рассмотрение поступивших заявок. Государственным заказчиком по торгам выступает департамент строительства Москвы.||В соответствии с проекта',70),(264,1,'В Москве подводят итоги торгов по реконструкции трех вылетных магистралей',70),(265,2,'13/01/2012',70),(266,3,'Volkswagen||российский авторынок||статистика',71),(267,5,'По итогам 2011 года продажи легковых автомобилей Volkswagen в России выросли в 2 раза и составили 118 тыс. 3 единицы. Как сообщила пресс-служба российского представительства компании, доля рынка легковых автомобилей марки Volkswagen также выросла и составила 4,8% (против 3,4% в 2010 году).||Лидером продаж ушедшего года стала модель Polo в кузове седан: за год был продан 47 491 автомобиль (против 7710 машин в 2010-м, когда данная модель только появилась на российском рынке). Автомобилей Tiguan в 2011 году было продано 18 385 штук (+40,7% к показателям 2010-го), Golf – 13 281 автомобиль (в 2 раза больше по сравнению с 2010 годом). За 2011-й было также продано 4893 Golf Plus, что на 86% больше прошлогодних показателей (2633 автомобиля).||Продажи модели Passat нового поколения, которая была представлена на российском рынке в мае 2011 года, составили 9911 экземпляров, а продажи Jetta нового поколения (поступила на российский рынок в сентябре 2011-го) составили 3094 автомобиля.||Дилерская се',71),(268,1,'Продажи Volkswagen на российском рынке выросли в 2 раза',71),(269,2,'13/01/2012',71),(270,3,'криминал||регионы',72),(271,5,'В Челябинской области задержаны участники организованной преступной группы, на счету которой разбойные нападения и убийства водителей большегрузного транспорта и автомобилистов, занимающихся частным извозом. Как сообщает пресс-служба Главного управления МВД России по Челябинской области, группа, состоящая из жителей региона – мужчин в возрасте от 20 до 43 лет, была хорошо вооружена и отличалась сплоченностью.||«Сотрудники полиции изъяли три пистолета, боеприпасы, холодное оружие, камуфлированную форму. Цель нападений – завладение автотранспортным средством и имуществом потерпевших», – говорится в сообщении.||В результате проведения оперативно-розыскных мероприятий были установлены места захоронения потерпевших, а также обнаружены фрагменты и комплектующие похищенных автомобилей.',72),(272,1,'В Челябинской обл. поймана банда, убивавшая автомобилистов',72),(273,2,'12/01/2012',72),(274,3,'BMW||промсборка||Автотор',73),(275,5,'BMW намерен выпустить на заводе «Автотор» в Калининграде по итогам 2012 г. более 20 000 автомобилей. Об этом сообщил журналистам президент российского подразделения BMW Петер Кроншнабель. «В 2012 г. мы планируем собрать на «Автоторе» более 20 000 автомобилей», – сказал он, отметив, что в 2011 г. было собрано 17 000 машин.||П. Кроншнабель отметил, что продажи автомобилей группы BMW на российском рынке в 2012 г. могут вырасти с 30 167 автомобилей в 2011 г. до 33 000 штук (+9,4%).||При этом он отказался комментировать планы по возможности создания нового производства полного цикла в Калининградской области совместно с «Автотором». Ранее, в сентябре 2011 г., директор по развитию завода Александр Сорокин сообщал, что стороны подписали меморандум о реализации проекта по созданию полного цикла производства автомобилей BMW. Общие инвестиции оценивались в 200 млн евро, мощности могут составить 80 000 автомобилей в год.',73),(276,1,'BMW планирует собрать в России за год 20 000 автомобилей',73),(277,2,'12/01/2012',73),(278,3,'ДТП||регионы',74),(279,5,'Водитель «Газели», насмерть сбивший на пешеходном переходе школьницу, задержан. Как сообщает региональное управление Министерства внутренних дел РФ, трагедия произошла в Абакане (Республика Хакасия) сегодня, 13 января, на перекрестке улицы Дружбы Народов с улицей Кати Перекрещенко. Три школьницы, учащиеся пятого класса, переходили дорогу на зеленый свет светофора по пешеходному переходу. В это время двигавшаяся в сторону аэропорта по маршруту Абакан – Черногорск пассажирская «Газель» сбила одну из них.||От полученных травм девочка скончалась на месте. Остальные школьницы не пострадали, но пребывают в шоковом состоянии. По данным ведомства, девочки переходили дорогу по правилам, а микроавтобус проехал на запрещающий сигнал светофора. В настоящее время 30-летний водитель задержан, из «Газели» изъята флеш-карта видеорегистратора, решается вопрос о возбуждении уголовного дела. В сообщении отмечается, что погибшая школьница – дочь сотрудника дежурной части республиканского управления МВД.',74),(280,1,'Водитель «Газели» насмерть сбил пятиклассницу, переходившую улицу на зеленый свет',74),(281,2,'13/01/2012',74),(282,3,'статистика||мировой авторынок||российский авторынок',75),(283,5,'Импорт легковых автомобилей в РФ за 11 месяцев 2011 года вырос на 45,57% по сравнению с аналогичным периодом предыдущего года и составил 901 500 автомобилей. Об этом сообщили в Федеральной таможенной службе (ФТС) России.||За отчетный период в РФ было ввезено легковых автомобилей на 17 млрд 51,6 млн долларов, в том числе 775 900 автомобилей на 16 млрд 227,3 млн долларов из стран дальнего зарубежья и 125 600 автомобилей на 824,3 млн долларов из стран СНГ.||Экспорт легковых автомобилей из РФ в январе-ноябре 2011 года также увеличился на 45,8% и составил 57 тысяч единиц на 430,3 млн долларов. В том числе в страны дальнего зарубежья было экспортировано 5700 автомобилей на сумму 49,6 млн долларов, в страны СНГ – 51 300 машин на 380,7 млн долларов.',75),(284,1,'Импорт и экспорт легковых автомобилей в России выросли почти на 50%',75),(285,2,'13/01/2012',75),(286,3,'Volkswagen||отзывы',76),(287,5,'Volkswagen сообщил, что объявленный ранее отзыв автомобилей с 2,0-литровым дизельным двигателем коснется 300 тысяч автомобилей. Напомним, в октябре американское подразделение автоконцерна заявило об отзыве 168 275 автомобилей Golf, Jetta и Audi A3 из-за обнаруженного риска утечки топлива, которое может вызвать возгорание двигателя. ||Как указано на сайте Национального управления безопасностью движения на трассах США (NHTSA), шланг впрыска этого дизельного двигателя подвержен появлению микротрещин, которых при некоторых обстоятельствах может быть достаточно для утечки топлива. Случаев возгорания по причине этого брака не зарегистрировано, но Volkswagen произведет замену шланга и установит гасители колебаний.',76),(288,1,'Volkswagen отзывает 300 тысяч автомобилей из-за риска возгорания двигателя',76),(289,2,'17/01/2012',76),(290,3,'BMW||отзывы',77),(291,5,'BMW начинает отзыв 235,5 тысяч автомобилей принадлежащей ей марки MINI по всему миру. Причина отзыва - опасность возгорания: обнаруженные в системе электроники автомобиля неполадки приводят к чрезмерному нагреванию печатных плат в системе дополнительного охлаждения, передает Reuters. Автопроизводитель признает: известен как минимум один случай подобного возгорания. К счастью, никто не пострадал.||В список «пожароопасных» автомобилей попали модели MINI Cooper, Cooper S, Clubman, Cooper JCW, Countryman и Convertible. Большая часть автомобилей - около 89 тысяч - будет отозвана в США, на втором месте оказалась Германия – здесь отзыву подлежат еще 22 тысячи авто. Предполагается, что первая партия автомобилей будет отозвана уже в феврале 2012 года. ',77),(292,1,'BMW отзывает 235,5 тысяч автомобилей MINI по всему миру',77),(293,2,'17/01/2012',77),(294,3,'ДТП',78),(295,5,'Во Владивостоке шестиколесный длинномер протаранил четыре легковых автомобиля. ДТП произошло на улице Шефнера. К настоящее время сотрудники ГИБДД расследуют причины аварии. По всей видимости, у 10-тонного грузовика отказали тормоза.||Грузовик, везущий стройматериалы, почти спустился на Светланскую, но не сумел остановиться на крутом спуске. В это время внизу на светофоре остановились четыре легковых автомобиля. Большегруз налетел на них и вывернул вправо на тротуар, где ему, наконец, удалось остановиться.||О пострадавших не сообщается, погибших, по-видимому, нет. Существует угроза возникновения затруднений в движении автотранспорта во всем районе, если автомобили не будут убраны с проезжей части в ближайшее время.',78),(296,1,'Десятитонный грузовик протаранил 4 автомобиля, не сумев затормозить на спуске',78),(297,2,'17/01/2012',78),(298,3,'курьезы||угоны',79),(299,5,'В Китае преступник пытался угнать автомобиль на загруженной трассе, полицейским пришлось догонять горе-угонщика бегом. На видеозаписи, запечатлевшей инцидент, видно, как двое полицейских пытаются догнать бегом машину. После того, как угонщик попадает в затор, сотрудники полиции настигают его и вместе с другими участниками дорожного движения окружают автомобиль. Тогда преступник сдает назад, сбивает одного из мотоциклистов и продолжает попытки скрыться.||Далее злоумышленник пытается на авто протиснуться сквозь ряды машин, таранит несколько транспортных средств, делает еще несколько попыток сбежать, но в итоге останавливается.||Сообщается, что всего в результате инцидента пострадали 13 автомобилей.',79),(300,1,'Преступник пытался угнать автомобиль в пробке',79),(301,2,'17/01/2012',79),(302,3,'Fiat||Петербург||заводы',80),(303,5,'Строительство завода Fiat в России может быть завершено через полтора-два года, сообщил на пресс-конференции директор департамента особых экономических зон и проектного финансирования Министерства экономического развития РФ Дмитрий Левченков. \"По практике строительство идет около 1,5-2 лет, если сравнивать с другими проектами, которые реализуются в России последние 5-6 лет\", - сказал Д. Левченков. \"Если компания приступит к строительству предприятия в этом году, то через полтора-два года\", - ответил он на вопрос журналистов, к какому году строительство завода будет завершено, напомнив, что в качестве площадки для производства Fiat выбрал Санкт-Петербург.||По словам Д. Левченкова, окончательный модельный ряд Fiat, производимых в Санкт-Петербурге, еще будет уточняться.||Напомним, меморандум о строительстве завода Fiat в Санкт-Петербурге планируется подписать до конца I квартала 2012 г. Об этом cообщил председатель комитета по инвестициям и стратегическим проектам Санкт-Петербурга Алексей',80),(304,1,'Завод Fiat в России может быть построен за полтора-два года',80),(305,2,'16/01/2012',80),(306,3,'Москва||дороги',81),(307,5,'Подготовлен проект единой концепции цветочного оформления Московской кольцевой автодороги, сообщает пресс-служба государственного учреждения \"Мосзеленхоз\". На территории МКАД в оперативном управлении \"Мосзеленхоза\" находится свыше 1,5 га цветников, общая площадь цветочного оформления составляет 16 231 кв. м.||В 2011 году по инициативе \"Мосзеленхоза\" был проведен анализ состояния цветников на всей территории МКАД, который показал неудовлетворительный уровень цветочного оформления. В результате \"Мосзеленхоз\" занялся разработкой концепции цветочного оформления в едином стилевом и композиционном решении. На сегодняшний день уже подготовлены эскизные варианты оформления различных типов цветников в зависимости от смысловой нагрузки: цветники-надписи для размещения на некрутых откосах; типовые цветники для размещения на плоскостных озелененных территориях развязок и съездов со МКАД; цветники-символы федеральных трасс для размещения на откосах при выездах со МКАД на автомагистраль.||В 2012г. н',81),(308,1,'В 2012 году МКАД получит новое цветочное оформление в едином стиле',81),(309,2,'16/01/2012',81),(310,3,'Lexus||GS||цены||седаны||новинки',82),(311,5,'Lexus объявляет российские цены на седан GS четвертого поколения. Стоимость модели начинается от 1 730 000 рублей. В России автомобиль доступен с двумя двигателями, задним или полным приводом и в пяти комплектациях.  ||Уже в базовой комплектации на GS устанавливаются светодиодные ходовые огни, биксеноновые фары с автоматической корректировкой угла наклона, 10 подушек безопасности, камера заднего вида с динамической разметкой, системы курсовой устойчивости (VSC) и интегрированного управления динамикой автомобиля (VDIM) с селектором выбора режимов движения, а также AUX-, USB- или Bluetooth-интерфейсы.||В первом полугодии 2012 года Lexus GS будет представлен на российском рынке двумя моделями: GS 250 с задним приводом и полноприводным GS 350 AWD. Обе модели доступны также в версии F-Sport.||Lexus GS 250 оснащен экономичным бензиновым 2,5-литровым V6 Dual VVTi мощностью 209 л.с. Устанавливаемый на GS 350 AWD 3,5-литровый V6 развивает мощность 317 л.с. Обе версии комплектуются 6-ступенчат',82),(312,1,'Lexus объявляет российские цены на новый седан GS',82),(313,2,'16/01/2012',82),(314,3,'бензин||статистика',83),(315,5,'Объем производства бензина в РФ за первую неделю 2012 года (со 2 по 8 января) снизился на 7,8% по сравнению с предыдущей неделей и составил 691 тысяч тонн, сообщает Федеральная служба государственной статистики РФ (Росстат). При этом выпуск дизельного топлива сократился на 0,1% - до 1,332 млн тонн.||Производство бензина в Москве снизилось по сравнению с предыдущей неделей на 22,3%, дизтоплива - на 57,5%. В Ленинградской области производство бензина выросло на 17,3%, дизтоплива снизилось на 2%. Абсолютные показатели Росстат не приводит.||В ноябре 2011г. в РФ было произведено 3,016 млн т бензина, что на 0,8% меньше по сравнению с предыдущим месяцем и на 0,7% больше по сравнению с ноябрем 2010г. Объем производства дизтоплива в ноябре 2011г. составил 5,606 млн т, увеличившись на 1,6% по сравнению с предыдущим месяцем и сократившись на 2,4% против показателя аналогичного периода прошлого года.',83),(316,1,'За первую неделю 2012 года объем производства бензина упал почти на 8%',83),(317,2,'16/01/2012',83),(318,3,'ДТП||пьянство за рулем',84),(319,5,'В Волгоградской области возбуждено уголовное дело в отношении заместителя начальника полиции по охране общественного порядка районного отдела МВД по области, сбившего четверых пешеходов. ||В минувшую субботу, 14 января, находившийся в состоянии алкогольного опьянения майор полиции за рулем личного автомобиля не справился с управлением и сбил пешеходов, двигавшихся в попутном ему направлении по краю проезжей части. В результате четыре человека получили травмы различной степени тяжести. \"Одна из пострадавших - 57-летняя женщина - от полученных травм скончалась в реанимационном отделении больницы\", - говорится в сообщении пресс-службы областного МВД.||Полицейскому инкриминируется ч.4 ст.264 Уголовного кодекса РФ (нарушение правил дорожного движения и эксплуатации транспортных средств, совершенное лицом, находящимся в состоянии опьянения, повлекшее по неосторожности смерть человека).',84),(320,1,'Пьяный полицейский на автомобиле сбил четырех шедших по тротуару пешеходов',84),(321,2,'16/01/2012',84),(322,3,'KIA||новинки||россиский автопром||российский авторынок',85),(323,5,'Сегодня, 16 января 2012 года в России сошел с конвейера первый KIA Rio в кузове хетчбэк. Производство стартовало на заводе «Хендэ Мотор Мануфактуринг Рус» под Санкт-Петербургом. ||Как отмечают в компании, новый Rio хетчбэк отличается спортивным экстерьером, он более компактный и маневренный по сравнению с седаном этой модели. При этом его салон остался таким же просторным, ведь колесная база у обеих версий автомобиля одна и та же.||Покупатели Rio в кузове хетчбэк смогут выбрать один из двух двигателей объемом 1,4 или 1,6 литра и механическую либо автоматическую коробку передач. К доступным для КIA Rio опциям в ближайшее время прибавятся навигаторы, которые можно будет установить в официальных дилерских центрах KIA.',85),(324,1,'В России началось производство нового KIA Rio в кузове хетчбэк',85),(325,2,'16/01/2012',85),(326,3,'российский авторынок||автокредиты',86),(327,5,'В 2011 году в России было выдано более 263 тысяч льготных автокредитов. Именно такую цифру называет Минпромторг. В начале года предполагалось, что за год объем кредитов составит 200 тысяч. Еще осенью министерство скорректировало прогноз до 250 тысяч. Стоит отметить, что всего за год было подано 582,5 тысяч заявок.||По оценкам агентства «Автостат», более 10% проданных в России автомобилей были куплены по льготному кредитованию. Субсидированные государством автокредиты составляют более 30% всех автокредитов в стране.||Напомним, в 2010 году по рассмотрении 354,5 тысяч заявок было выдано 165 708 льготных автокредитов, а в 2009 в 2,3 раза меньше - всего 71,5 тысяч. Таким образом, за время существования программы льготного кредитования субсидированные кредиты на автомобили получило более 500 тысяч автолюбителей.',86),(328,1,'За 2011 год 263 тысяч россиян получили льготные автокредиты ',86),(329,2,'16/01/2012',86),(330,3,'статистика продаж автомобилей||российский авторынок',87),(331,5,'В России в 2011 г. было продано более 2,5 млн новых легковых автомобилей, что примерно на 41% больше, чем в 2010 г. Об этом сообщил директор департамента особых экономических зон и проектного финансирования Министерства экономического развития (МЭР) РФ Дмитрий Левченков. При этом он подчеркнул, что данный прогноз является его экспертной оценкой. ||Д. Левченков напомнил, что по итогам 11 месяцев 2011 г. Россия заняла второе место в Европе после Германии по объему продаж новых легковых автомобилей (2 млн 247 тыс. машин).||Д. Левченков отметил, что 70% всех проданных автомобилей произведено в России. При этом объем производства в режиме промышленной сборки вырос по сравнению с показателем предыдущего года на 91% и превысил 700 тыс. автомобилей.||Также Д. Левченков сообщил, что к концу 2012 г. продажи легковых автомобилей в РФ могут составить 2,6-2,8 млн единиц, а через пять лет - вырасти до 3,5 млн машин.',87),(332,1,'За прошлый год в России продали 2,5 млн автомобилей',87),(333,2,'16/01/2012',87),(334,3,'бензин||статистика||цены',88),(335,5,'Потребительские цены на автомобильный бензин за период со 2 по 9 января 2012 года в среднем по России снизились на 0,1% и на 10 января составили 27 руб./л., сообщает Федеральная служба государственной статистики (Росстат) РФ.||Цена бензина марки Аи-92 уменьшилась на 0,1% - до 26,32 руб./л, марки Аи-95 - также на 0,1% - до 28,35 руб./л. Цену на бензин марки А-76 Росстат не приводит. Цены на дизельное топливо выросли на 0,1% - до 27,84 руб./л. Средняя цена рассчитана исходя из цен на марки бензина, включенные в наблюдение в 2012 году, пояснили в ведомстве.||Удешевление бензина было зафиксировано в 29 центрах субъектов Российской Федерации, в том числе в Брянске и Белгороде - на 1,0%. В 39 центрах субъектов РФ цены на бензин остались на уровне предыдущей недели. Увеличение средних цен на бензин зарегистрировано в 15 центрах регионах. Наиболее существенно они выросли в Южно-Сахалинске и Кургане - на 2,0% и 1,8% соответственно. В Москве цены на бензин в среднем за неделю снизились на 0,2% -',88),(336,1,'За первую неделю января цены на бензин снизились на 0,1%',88),(337,2,'16/01/2012',88),(338,3,'ДТП',89),(339,5,'В минувшее воскресенье, 15 января, в Пензенскной области на сложном участке дороги столкнулись шесть автомобилей. Об этом сообщает Главное управление МЧС по Пензенской области.||ДТП произошло в 10:13 мск на 15 километре автодороги Пенза-Шемышейка. Этот  участок дороги отличается сложным рельефом с крутым спуском и подъемом. Всего в пяти автомобилях – «Ниве», Renout Logan и трех «Жигулях» (ВАЗ-2112, ВАЗ-2110 и ВАЗ-2109) находилось 23 человека. Семь из них получили ранения. ||Трое, получив медицинскую помощь на месте происшествия, отказались от госпитализации, четверо же были доставлены в Пензу в Городскую клиническую больницу скорой медицинской помощи N6 им. Г.А.Захарьина. Один из этих четырех смог уйти домой в тот же день, жизни троих оставшихся госпитализированных вне опасности.',89),(340,1,'Пять автомобилей столкнулись на сложном участке дороги, пострадали 7 человек ',89),(341,2,'16/01/2012',89),(342,3,'Honda||промсборка||заводы||автопроизводство',90),(343,5,'Honda возобновила переговоры по строительству завода в России. Соглашение может быть заключено на альтернативных старому режиму промсборки условиях, пишет РИА Новости со ссылкой на директора департамента особых экономических зон и проектного финансирования Минэкономразвития Дмитрия Левченкова. ||\"В прошлом году переговоры возобновились\", - сказал он, отметив, что соглашение уже не будет заключено по старому режиму промсборки. По словам Д. Левченкова, будут использованы другие альтернативные варианты, как, например, таможенные льготы или создание инфраструктуры.||Осенью прошлого года появилась информация о том, что Honda планирует организовать в РФ производство автомобилей полного цикла на условиях старого режима промсборки. Старый режим промсборки подразумевал локализацию производства компонентов в объеме 30%. Новый режим промсборки, вступивший в силу в феврале 2011 года, предполагает повышение уровня локализации до 60%, а также выход производства через несколько лет на мощность 300 00',90),(344,1,'Honda может запустить в России производство полного цикла',90),(345,2,'16/01/2012',90),(346,3,'Maybach||Bentley||luxury',91),(347,5,'Bentley хочет переманить клиентов Maybach, в том числе короля Испании Хуана Карлоса, актера Самуэля Джексона и хип-хоп-исполнителя Jay-Z. Британский автопроизводитель рассылает письма VIP-клиентам Maybach с целью убедить их покупать его автомобили. ||Применить подобную тактику в Bentley решили после того, как стало известно о том, что Daimler AG собирается ликвидировать свой премиальный бренд. \"Уход Maybach с рынка открывает возможность для нас, и мы собираемся ее использовать\", - заявил глава британской марки Вольфганг Дюрхаймер. По его словам, некоторые состоятельные клиенты уже ответили, причем положительно. ||Напомним, в ноябре 2011 года Daimler заявил о преркащении производства автомобилей под брендом Maybach. ',91),(348,1,'Bentley пытается соблазнить клиентов Maybach',91),(349,2,'16/01/2012',91),(350,3,'Mercedes-Benz||полиция',92),(351,5,'Центр специального назначения в области безопасности дорожного движения УВД России получил тринадцать автомобилей Mercedes-Benz E 350 4MATIC. Передача машин состоялась на территории Центра специального назначения БДД УВД России в поселке Чулково Московской области. Сотрудники гарантийного автосервиса ЗАО «Мерседес-Бенц РУС», российского подразделения компании Daimler AG,  установили на автомобили полицейское оборудование. Кроме того, еще три автомобиля Mercedes-Benz E 350 4MATIC были переданы УГИБДД МВД по Владимирской области. ||Стоит отметить, что автомобили Е-Класса отлично зарекомендовали себя на российском рынке. Модель E 350 4MATIC, оснащенная самыми современными системами активной и пассивной безопасности, несомненно, будет с успехом нести службу в полиции.',92),(352,1,'Mercedes-Benz E350 будут нести службу в Центре специального назначения  УВД России',92),(353,2,'16/01/2012',92),(354,3,'Opel',93),(355,5,'Opel предложил европейским студентам, обучающимся любому виду дизайна, придумать облик автомобиля Ampera версии 2020 года. Желающие поучаствовать про в проекте должны предложить дизайн как внешнего вида авто, так и салона, при создании обоих следует учитывать предложенную брэндом тему слияния скульптурного изящества с немецкой точностью. В компании также поощряют максимально подробное описание высокотехнологичных систем, которые должны, по мнению студентов, появиться в этом электромобиле будущего.||Вице-президент Европейского дизайнерского бюро General Motors Марк Адамс рассказал, что в компании с большим энтузиазмом ожидают возможности ознакомиться с предложенными работами и считают, что активный фидбэк от представителей молодого поколения поможет выбрать правильное направление. Предложивший лучший дизайн студент сможет пройти стажировку в компании.',93),(356,1,'Opel предложил студентам придумать дизайн автомобиля будущего',93),(357,2,'16/01/2012',93),(358,3,'ДТП',94),(359,5,'В Краснодарском крае произошло крупное ДТП, в котором погибли два сотрудника ДПС из Чечни. Как сообщает пресс-служба следственного управления Следственного комитета РФ по республике, авария произошла сегодня на трассе \"Дон\" (М4) в районе села Раздольное. Полицейские ехали в командировку из Чечни в Ростов-на-Дону на служебном автомобиле Lada Priora.||В районе Невинномысска на дороге рядом с ними оказался автомобиль Toyota Land Cruiser. Какое-то время они ехали бок о бок, затем водитель Toyota обогнал машину полиции и на большой скорости продолжил движение. При этом на расстоянии 17 км от станицы Кущевской на трассе из-за ДТП уже имелось скопление автомобилей, на которых были включены огни аварийной остановки. Не сумев вовремя остановиться, водитель машины Toyota выехал на обочину и, не справившись с управлением, столкнулся с КАМАЗом. Между тем водитель служебного автомобиля полиции также не успел остановить машину и столкнулся с другим автомобилем. От удара прицеп грузовой машины сдвину',94),(360,1,'Два сотрудника ДПС погибли в крупной автомобильной аварии',94),(361,2,'16/01/2012',94),(362,3,'Multivan Match||Multivan||Volkswagen||спецверсии',95),(363,5,'Volkswagen выпустил специальную версию Multivan по случаю чемпионата Европы по футболу-2012. Созданный на базе комплектации Multivan Comfortline спецвариант Match серийно оснащается 3-зонным автоматическим кондиционером Climatronic и CD-магнитолой RCD 310 с цифровым радиотюнером. Также для авто предлагается парковочный ассистент с передними и задними датчиками, круиз-контроль, противотуманные фары с функцией адаптивного освещения, подогрев форсунок стеклоомывателя и пакет «Свет и обзор».||В салоне Multivan Match появились новые варианты обивки сидений и текстильные коврики с контрастными швами. Снаружи специальная версия отличается 17-дюймовыми легкосплавными дисками Cascavel и тонированными стеклами. ||Multivan Match можно заказать с короткой (до 7 посадочных мест) или длинной колесной базой (до 8 посадочных мест). Цена на Multivan Match с короткой базой и бензиновым двигателем мощностью 116 л.с. начинается с 44 137 евро, что на 1600 евро дешевле, чем серийная модель аналогичной компл',95),(364,1,'Volkswagen выпустил спецверсию Multivan Match к ЧЕ-2012',95),(365,2,'16/01/2012',95),(366,3,'Audi||технологии',96),(367,5,'Audi планирует оснащать свои автомобили системой, значительно упрощающей управление в пробках. Новый автопилот, получивший название Traffic Jam Assistant, был представлен на выставке потребительской электроники Consumer Electronics Show (CES). Он работает на низких скоростях (от 0 до 60 км/ч) и работает аналогично адаптивному круиз-контролю: самостоятельно поддерживает дистанцию до едущего впереди автомобиля и в случае надобности тормозит.||Ситуацию на дороге система определяет при помощи широкоугольной видеокамеры, двух радаров (диапазон сканирования каждого - 21 градус) и восьми ультразвуковых датчиков.||Самостоятельно рулить автопилот пока не умеет, но на прямой трассе или городской улице вполне способен обеспечить должный уровень надежности. Радары сканируют не только впередиидущую машину, но и двигающиеся в потоке перед ней, что поможет заблаговременно предсказать торможение.||Новинка появится на борту флагманского седана A8, который должен появиться на рынке в ближайшую пару лет.',96),(368,1,'Audi разработала автопилот для перегруженной трассы',96),(369,2,'16/01/2012',96),(370,3,'Mercedes-Benz||общество',97),(371,5,'Глава компании Daimler AG, владеющей брэндом Mercedes-Benz, Дитер Цетше принес официальные извинения за использование образа Эрнесто Че Гевары в презентации на Международной выставке потребительской электроники в Лас-Вегасе. ||Фоном для презентации, рассказывающей о последних достижениях компании, послужила самая известная фотография в мире - снимок знаменитого революционера, сделанный Альберто Кордой в 1960. В видеоматериалах также фигурировало изображение логотипа Mercedes-Benz, украшенного беретом Че Гевары. ||Узнав об этом, целый ряд кубино-американских организаций выразил свое возмущение. Некоторые активисты даже обещали избавиться от своих автомобилей или не рассматривать приобретение продукции этой марки в дальнейшем.  ||В опубликованном заявлении подчеркивается, что компания «не оправдывает жизнь или действия этой исторической фигуры, а также политическую философию, которую он исповедовал».',97),(372,1,'В Mercedes-Benz извинились за Че Гевару',97),(373,2,'16/01/2012',97),(374,3,'Ferrari||новинки||шпионские фото',98),(375,5,'В Интернет попало шпионское видео автомобиля Ferrari, который придет на смену модели 599. В настоящее время известно только его рабочее обозначение - F152. На видео автомобиль проходит дорожные тесты в Маранелло. Основательный камуфляж не позволяет судить о дизайне, но под капотом, судя по звуку, скрывается крайне мощный мотор. Вероятно, 6,3-литровый V12 мощностью 700 л.с.  ||В Ferrari о будущей новинке практически ничего не говорили, но известно, что ее кузов будет изготовлен не из популярного у многих производителей суперкаров углепластика, а из алюминия. В компании объясняют такой выбор дороговизной технологии, которая не окупается с точки зрения технических характеристик авто: разница получается не столь уж велика.||Ожидается, что Ferrari представит новинку в марте на автосалоне в Женеве.  ||<center><object style=\"height: 390px; width: 640px\"><param name=\"movie\" value=\"http://www.youtube.com/v/bgCKxirhc9Y?version=3&feature=player_detailpage\"><param name=\"allowFullScreen\" value=\"tru',98),(376,1,'Шпионы засняли новую Ferrari, которая придет на смену модели 599',98),(377,2,'16/01/2012',98),(378,3,'ДТП',99),(379,5,'Полицейские штата Вашингтон задействовали громкоговорители, сирены и даже прожекторы в попытке остановить 60-летнюю женщину за рулем. Памела Дроусби проехала около 30 км по встречной полосе на пятой федеральной автостраде, причем ее скорость временами превышала 160 км/ч. ||Попадавшиеся ей на пути водители чудом успевали уйти от столкновения. Многие из них, в том числе находившийся за рулем восьмиколесной фуры Том Элкинс, стали звонить в полицию. Применив все вышеописанные способы, полицейские в итоге были вынуждены перекрыть движение на трассе. Увидев, что дорога перегорожена, женщина остановилась.||Как выяснилось, Памела запила алкоголем не сочетающиеся с ним таблетки и не осознавала своих действий. В момент ареста она даже не понимала, где находится. Она должна в скором времени предстать перед судом. В ходе ее безумной гонки, к счастью, никто не пострадал. В полиции отмечают, что лобовое столкновение на таких скоростях неизбежно привело бы к смерти.||<center><iframe width=\'560\' heigh',99),(380,1,'Женщина проехала по встречной полосе 30 км на скорости более 160 км/ч. ВИДЕО',99),(381,2,'16/01/2012',99),(382,3,'Москва||дороги',100),(383,5,'В Москве ночной снегопад стал причиной многокилометровых пробок. Несмотря на большое количество снегоуборочной техники, всю ночь работавшей на улицах города, из-за продолжающегося обильного снегопада скорость движения на многих трассах существенно снизилась. Аналитический центр «Yandex.Пробки» сообщил о 7-балльных заторах: в час пик были загружены все основные магистрали, средняя скорость движения на них составила порядка 10-15 км/ч. ||Наиболее сложная ситуация сложилась на проспекте Мира, шоссе Энтузиастов, Рязанском и Волгоградском проспектах, а также на Рублевском и Рублево-Успенском шоссе по направлению к центру. В настоящее время ситуация несколько улучшилась, но, как сообщает Гидрометцентр, снегопад продлится весь день, так что вечером в час пик ситуация, вероятно, повторится.',100),(384,1,'Утро понедельника «порадовало» москвичей многокилометровыми пробками',100),(385,2,'16/01/2012',100),(386,3,'Jaguar',101),(387,5,'В Аризоне с торгов будет продана исключительно редкая модель Jaguar XJ220S. Стандартные XJ220S были выпущены ограниченной серией в 350 автомобилей, но в данном случае речь идет о еще более редком суперкаре: в этой модификации было выпущено всего пять автомобилей. Они были созданы при участии команды Том Уокиншо Рейсинг (TWR): специалисты по гоночным авто демонтировали большую часть алюминиевого кузова суперкаров и заменили их еще более легким углепластиком. Автомобили также получили передний сплиттер и задний спойлер.||Выставленный на торги автомобиль не менял владельца предыдущие восемь лет. Мощность его двигателя V6 изначально составляла 680 л.с., но новая выхлопная система от TWR и выполненная ими же доводка двигателя увеличили мощность до 700 л.с. Как ожидается, Jaguar XJ220S будет продан за сумму между 225 и 300 тысячами долларов.',101),(388,1,'В Аризоне с торгов будет продан исключительно редкий Jaguar XJ220S ',101),(389,2,'16/01/2012',101),(390,3,'Porsche||гибриды||новинки',102),(391,5,'В Porsche  подтвердили: серийная версия гибрида 918 Spyder будет показана на автосалоне во Франкфурте  в 2013 году. В этом же году начнутся и продажи новинки.||Силовая установка серийной версии автомобиля порядком отличается от силовой установки показанного в 2010 году концепта: вместо 3,4-литрового бензинового ДВС автомобиль получит 4,6-литровый агрегат мощностью 550 лошадиных сил, что на 50 л. с. больше. Этот восьмицилиндровый двигатель будет построен на базе блока, разработанного для гоночного RS Spyder, и получит инновационные системы впрыска топлива и смазки.||Помимо ДСВ автомобиль получит три электромотора, в результате чего суммарная мощность силовой установки составит 720 л.с., что позволит гибридному суперкару разгоняться с нуля до 100 км/ч за 3,1 секунды.||Заказы на новинку все еще принимаются, ее цена начинается от 645 000 евро. Компания планирует выпустить гибрид ограниченной серией в 918 автомобилей.',102),(392,1,'Porsche покажет серийную версию гибрида 918 Spyder во Франкфурте',102),(393,2,'16/01/2012',102),(394,3,'ДТП',103),(395,5,'В Приморском крае в результате ДТП погибла 14-летняя девочка. Водитель легкового автомобиля уснул во время движения, сообщает Управление МВД РФ по региону.||ДТП произошло в минувшее воскресенье на 490 км автодороги М-60. Водитель автомашины Honda HR-V, 30-летний мужчина, ехал в сторону Хабаровска. Не справившись с усталостью, он уснул за рулем, и неуправляемый автомобиль совершил наезд на двух девочек, шедших по обочине дороги во встречном направлении. Далее автомашина съехала в правый кювет и остановилась.||В результате аварии 14-летняя девочка скончалась на месте происшествия, до приезда врачей. 20 января 2012 года ей должно было исполниться 15 лет. Вторая пострадавшая была доставлена в больницу с серьезной травмой бедра. Обе девочки - воспитанницы детского дома, расположенного в г.Спасск-Дальний. Также при ДТП пострадал 4-летний мальчик - пассажир Honda, в момент удара он находился на заднем сиденье в детском кресле. В настоящее время его жизнь вне опасности. Стаж вождения автомобил',103),(396,1,'Уснувший за рулем водитель насмерть сбил девочку, еще два ребенка пострадали',103),(397,2,'16/01/2012',103),(398,5,'На севере Мексики в субботу туристический автобус столкнулся с пассажирским поездом, передает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>. В результате аварии, ранения получили 20 человек.||Местная полиция сообщает, что практически все обстоятельства ДТП выяснены. Водитель автобуса пытался проскочить железнодорожный переезд прямо перед поездом. К счастью, скорость движения электрички была невысокой, машинист прибегнул к экстренному торможению. В результате столкновения, автобус отбросило к оврагу. ||Как заявили очевидцы происшествия, автобус буквально повис в воздухе, зацепившись за дерево. В местной службе спасения предположили, что если автобус упал бы в овраг, жертв избежать не удалось бы. В автобусе находилось 40 туристов. ',104),(399,1,'В Мексике туристический автобус столкнулся с поездом',104),(400,2,'15/01/2012',104),(401,5,'Японский автопроизводитель намерен представить на грядущем автосалоне в Женеве спортивный автомобиль с гибридным двигателем, информируют немецкие СМИ.||В настоящее время название модели держится в секрете, однако известно, что под капотом новинки будет находиться бензиновый мотор объемом 1,2 л., а также электродвигатель. Сообщается, что оба агрегата «будут максимально эффективно взаимодействовать». ||Также известно, что автомобиль продемонстрирует новое направление дизайна компании Infiniti. Представители марки заявляют, что концепт запомнится всем посетителям автосалона в Женеве своей яркостью, привлекательностью и необычной компоновкой салона. Автомобиль будет интересен еще и тем, что его электромотор будет самым мощным в классе гибридов. Агрегат сможет обеспечивать  солидную производительность при нулевых выбросах в атмосферу. ',105),(402,1,'Infiniti покажет гибридный спорткар',105),(403,2,'15/01/2012',105),(404,5,'ГИБДД столицы обращается ко всем водителям с просьбой быть внимательными и осторожными на дорогах в воскресенье в связи с ухудшением погодных условий. ||«Водителям следует избегать беспорядочного маневрирования, резкого торможения и увеличения скорости, необходимо соблюдать безопасную дистанцию, выбирать скоростной режим с учетом неблагоприятных погодных условий. Осторожность следует соблюдать при движении по эстакадам, мостам и путепроводам, а также мало оживленным улицам», - говорится в обращении. ||Помимо этого, ГИБДД настоятельно просит соблюдать правила остановки и стоянки в целях бесперебойной работы снегоуборочной техники. «Государственная инспекция безопасности дорожного движения Москвы обращается к водителям с убедительной просьбой не препятствовать своими действиями работе коммунальных служб города», - подчеркивается в обращении. ',106),(405,1,'ГИБДД Москвы просит водителей быть внимательными на дорогах',106),(406,2,'15/01/2012',106),(407,5,'Ижевский автомобильный завод в 2011 году сумел достичь приемлемых показателей, сообщает <a href=\" http://www.autostat.ru \" target=\"_blank\">аналитическое агентство «Автостат»</a>.||В период с января по декабрь прошлого года завод выпустил 70176 автомобилей. Интересно, что показатели превышают прошлогодние результаты более чем в десять раз. Напомним, в течение 2010 года ИжАвто произвел лишь 6980 машин.||Согласно официальным данным, «Объединенная автомобильная группа» произвела более 31 тысячи экземпляров Lada 2107, около 30 тысяч универсалов Lada 2104, порядка 5,2 тысяч фургонов ИЖ-27175, а также 1680 автомобилей Kia Spectra и 800 единиц Kia Sorento. В декабре прошлого года было выпущено еще 3,8 тысячи автомобилей. ||Что касается планов завода на текущий год, то приоритетной задачей стала постановка на конвейер новинки отечественного автопрома – Lada Granta. При этом речь о снятии с производства моделей Lada 2107 и 2104 не идет. ',107),(408,1,'ИжАвто сумел увеличить производство в десять раз ',107),(409,2,'15/01/2012',107),(410,5,'Серьезное ДТП произошло сегодня в центре Новосибирска, передает <a href=\"http://www.vesti.ru\" target=\"_blank\">«Вести ФМ»</a>.  Иномарка, в которой находилась компания молодых людей, врезалась в остановку, после чего отлетела в фонарный столб. По последним данным, погибли четверо, еще двое были госпитализированы в тяжелом состоянии. Возраст погибших составляет от 17 до 22 лет. ||Сообщается, что водитель автомобиля Nissan на скользкой дороге превысил скорость и не справился с управлением. Очевидцы сообщают, что машину буквально выбросило с проезжей части на остановку общественного транспорта. К счастью, в этот момент она была пуста.||На месте аварии в эти минуты работают сотрудники полиции, которые пытаются установить точную картину ДТП.  В ГИБДД сообщили, что пока неизвестно, в каком состоянии находился водитель автомобиля. Назначены все необходимые экспертизы.  ',108),(411,1,'В аварии в Новосибирске погибли четыре человека ',108),(412,2,'15/01/2012',108),(413,5,'Новое поколение популярного южнокорейского кроссовера впервые будет представлено на международном Нью-Йоркском автосалоне в начале апреля текущего года, сообщают американские СМИ. ||По некоторым данным, серьезные изменения коснутся интерьера и линейки моторов автомобиля. В салоне появятся более дорогие материалы отделки, а также ряд новых опций. Под капотом Hyundai Santa Fe получит 2,0-литровый турбированный агрегат мощностью в 274 л.с. Сообщается, что именно этот двигатель заменит 3,5-литровый атмосферный мотор в линейке силовых агрегатов корейского автомобиля. ||Помимо него в Европе будет доступен еще 2,0-литровый турбодизель. ||Еще покупатель получит возможность выбрать между 6-ступенчатым «автоматом» и «механикой», а также полным и передним приводом кроссовера. ||Что касается внешних изменений, то их минимум. Новый Hyundai Santa Fe получит светодиодные фары, выполненные на спортивный манер бамперы, а также новые литые диски. ',109),(414,1,'Новый Hyundai Santa Fe покажут в апреле',109),(415,2,'15/01/2012',109),(416,5,'Серьезная автокатастрофа произошла в субботу на юго-востоке Бангладеш, передает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>. ||По информации источника, ДТП произошло в 250 километрах к юго-востоку от столицы страны. Автобус, в котором находились, по меньшей мере, 40 пассажиров, упал в глубокое ущелье. По информации местной полиции, причиной аварии стала плохая видимость и несоблюдение скоростного режима водителем автобуса. ||Согласно последним данным, 13 человек скончались на месте происшествия, еще четверо умерли по дороге в больницу. ||Напомним, в июле прошлого года в Бангладеш пассажирский автобус также сорвался в ущелье. Тогда в аварии погибли 53 ребенка. Примечательно, что ДТП произошло на том же участке дороги. ',110),(417,1,'В Бангладеш автобус упал в ущелье, 17 человек погибли ',110),(418,2,'14/01/2012',110),(419,5,'По сообщениям американских СМИ, концерн General Motors активно работает над новым седаном Chevrolet Impala. Планируется, что производство модели будет осуществляться на заводе в Канаде. Согласно некоторой информации, новый Impala будет представлен уже в апреле текущего года. ||Сообщается, что десятое поколение модели будет построено на базе Epsilon II, которая также лежит в основе Cadillac XTS. Chevrolet Impala получит достаточно широкую линейку моторов, при этом базовым  двигателем для автомобиля станет 3,5-литровый бензиновый агрегат мощностью в 300 л.с. Что касается трансмиссий, то будет доступен лишь шестиступенчатый «автомат». ||Напомним, девятое поколение автомобиля выпускается в Канаде с 2005 года. В последнее время варианты комплектаций и двигателей были значительно ограничены, а продажи модели продолжают снижаться. Самым удачным годом для Chevrolet Impala считается 2007-й, когда по всему миру было реализовано более 300 тысяч автомобилей. ',111),(420,1,'General Motors работает над новым Chevrolet Impala',111),(421,2,'14/01/2012',111),(422,5,'По итогам 2011 года автомобиль от корейского производителя стал самым востребованным на отечественном рынке. Согласно официальной статистике, за весь прошлый год на территории России было реализовано 97,2 тысячи экземпляров этой модели. ||Что касается положения Solaris в общем зачете, то машина заняла пятое место, уступив автомобилям от АвтоВАЗ. Отметим, что в 2011 году лидером продаж стала Lada Kalina (142,9 тыс.), второе место заняла Lada Priora (138,7 тыс.). ||Напомним, в декабре компания «Хендэ Мотор СНГ» обновила свою самую бюджетную модель Solaris. Помимо незначительных внешних изменений и доступного оснащения, были изменены комбинации двигателей, комплектаций и пакетов опций. Теперь седан и хэтчбек Hyundai Solaris доступны в 34 вариациях каждый.||Примечательно, что и цены на модель выросли минимум на 10 тысяч рублей в базовой комплектации.',112),(423,1,'Hyundai Solaris стал самой продаваемой иномаркой в России',112),(424,2,'14/01/2012',112),(425,5,'Крупное ДТП произошло в субботу утром в столице, сообщает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>. На северо-западе Москвы на Живописном мосту столкнулись сразу 15 легковых машин. По последним данным, пострадавших нет.   Обстоятельства происшествия устанавливаются. ||По одной из версий, причиной аварии мог стать гололед, однако пресс-секретарь заместителя мэра по вопросам жилищно-коммунального хозяйства и благоустройства  опроверг эту информацию. Игорь Пергаменщик заявил, что дорожное полотно на Живописном мосту было своевременно обработано реагентами, это подтверждает и соответствующее заключение экспертной комиссии. ||По другой версии, столкновение машин произошло из-за превышения скоростного режима одним из водителей-участников аварии. ||Напомним, что накануне МЧС предупреждало участников движения о сложных погодных условиях. Вечером в пятницу шел мокрый снег, а в ночь на субботу по всей территории региона установилась минусовая температура, что способствовало',113),(426,1,'В Москве на Живописном мосту столкнулись 15 машин ',113),(427,2,'14/01/2012',113),(428,5,'Немецкий концерн продемонстрировал новый  концепт DICE, имитирующий автомобиль ближайшего будущего. По мнению производителя, в скором времени основным отличием машин от транспортных средств настоящего станет система по распознаванию жестов. ||Концепт DICE, показанный на прошлой неделе, представляет собой лишь имитацию салона автомобиля будущего. На передней панели нет кнопок, регуляторов и прочих органов управления. Сообщается, что специальные датчики будут отслеживать положение рук водителя и переднего пассажира. В зависимости от жеста или движения, можно будет управлять различными системами машины. Например, климат-контролем, аудиосистемой или навигатором. ||Интересно, что Mercedes-Benz реализовал в концепте DICE так называемую связь с технологиями дополненной реальности. Она заключатся в получении оперативной и полной информации о зданиях, улицах, достопримечательностях и других объектах, которые находятся рядом с местом движения автомобиля. ',114),(429,1,'Mercedes-Benz показал автомобиль будущего',114),(430,2,'14/01/2012',114),(431,5,'Серьезное ДТП произошло в субботу в Оренбургской области, передает  <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>. По информации источника, на трассе Оренбург-Орск около восьми часов утра столкнулись ВАЗ-2112 и ВАЗ-2121. ||В результате сильнейшего лобового удара, пять пассажиров автомобилей погибли на месте аварии, еще трое с серьезными травмами были доставлены в ближайшую больницу. Отмечается, что среди погибших и пострадавших детей нет. На месте аварии работают сотрудники полиции, обстоятельства автокатастрофы выясняются. По некоторой информации, причиной ДТП могла стать плохая видимость. ||Напомним, в октябре прошлого года в Оренбургской области происходило похожее ДТП. На этом же шоссе автомобиль марки BMW выехал на полосу встречного движения и допустил столкновение с Lada Priora. В результате инцидента, тогда погибли пять человек, в том числе двое детей. ',115),(432,1,'В Оренбургской обл. в аварии погибли пять человек',115),(433,2,'14/01/2012',115),(434,5,'Отечественный автопроизводитель по результатам прошедшего года сумел реализовать за рубежом  более 56 тысяч автомобилей, что больше показателей 2010 года на 32,9%, сообщает <a href=\" http://www.autostat.ru \" target=\"_blank\">аналитическое агентство «Автостат»</a>. При этом отмечается, что крупнейшими рынками сбыта для АвтоВАЗа стала Украина, Казахстан и Азербайджан. ||Примечательно, что более 30% поставок автомобилей Lada за рубеж приходится на модель Samara. Помимо реализации самих машин, АвтоВАЗ в 2011 году поставил более 162 тысяч сборочных автокомплектов. Среди ведущих партнеров предприятия источник отмечает «ИжАвто» (72 тысячи шт.), «GM-АвтоВАЗ» (57,7 тысяч шт.), а также сборочное производство в Черкассах (163 тысячи шт.). ||Что касается планов на 2012 год, то автопроизводитель планирует увеличить объемы поставок автокомплектов на внешние конвейеры до 160 тысяч экземпляров. ',116),(435,1,'АвтоВАЗ добился увеличения экспорта на 33% ',116),(436,2,'14/01/2012',116),(437,3,'ДТП||Петербург',117),(438,5,'В Выборгском районе Санкт-Петербурга легковой автомобиль сбил двух детей на пешеходном переходе, водитель скрылся с места ДТП. Инцидент произошел на перекрестке улицы Шостаковича и проспекта Энгельса.||Оба ребенка остались живы, но получили тяжелые травмы и сейчас находятся в больнице. Ведется розыск водителя.||Похожее ДТП произошло сегодня в столице Хакасии Абакане: маршрутка на пешеходном переходе сбила трех пятиклассниц. Один ребенок погиб.||Установить все обстоятельства трагедии должны записи с камер наружного наблюдения. Кроме того, полиция изъяла запись с видеорегистратора, установленного в маршрутном такси. Уже известно, что дети переходили дорогу на зеленый сигнал светофора. Сейчас решается вопрос о возбуждении уголовного дела в отношении водителя.',117),(439,1,'В Петербурге машина сбила двух детей на переходе и скрылась',117),(440,2,'13/01/2012',117),(441,3,'коррупция||власть||мигалки',118),(442,5,'Дагестанские прокуроры через суд помогли вернуть в бюджет республики 2,9 млн рублей, незаконно потраченных чиновниками на покупку иномарки, сообщает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на прокуратуру Дагестана.||«Прокуратурой в Тарумовский районный суд направлено заявление о признании незаконными действий администрации Тарумовского района и районного управления образования по приобретению Toyota Land Cruiser 200 стоимостью 2,9 млн рублей», – указано в релизе. Суд обязал чиновников вернуть потраченные деньги в местный бюджет.||Отметим, что в марте 2011 года власти республики разместили на сайте госзакупок заявку на покупку бронированного седана Audi A8L за 8,5 млн рублей, но после появления информации в прессе поспешили отказаться от заявки, назвав ее технической ошибкой.',118),(443,1,'Чиновников обязали вернуть потраченные на авто бюджетные деньги',118),(444,2,'13/01/2012',118),(445,3,'ДТП||скандалы||ДТП на Ленинском||мигалки',119),(446,5,'Срок следствия по делу о ДТП на Ленинском проспекте с участием автомобиля вице-президента ЛУКОЙЛа Анатолия Баркова продлен до 28 марта 2012 года, сообщает агентство РАПСИ со ссылкой на адвоката Игоря Трунова, представляющего интересы родственников погибших в аварии врачей.||«Сегодня две новости: срок следствия продлен до 28 марта и готова комплексная автотехническая экспертиза. Мы пока не знаем, отличается ли она от того безобразия, которое было представлено ранее», – сказал адвокат, добавив, что следственные действия назначены на следующую среду.||Громкое ДТП с участием автомобиля Mercedes-Benz вице-президента ЛУКОЙЛа Анатолия Баркова и Сitroen, в котором находились две женщины – врачи Ольга Александрина и Вера Сидельникова, произошло 25 февраля 2010 г. в районе площади Гагарина в Москве. В результате аварии управлявшая Сitroen врач-гинеколог О. Александрина скончалась на месте. Ее свекровь, заслуженный врач России профессор В. Сидельникова, от полученных травм умерла в больнице. Вино',119),(447,1,'Срок следствия по делу о ДТП на Ленинском пр. продлили до конца марта',119),(448,2,'13/01/2012',119),(449,3,'Chevrolet',120),(450,5,'Chevrolet представил самый мощный Corvette, получивший название 427 Collector Edition. Данная модификация приурочена к 60-летнему юбилею модели Corvette.||Под капотом суперкара – 7,0-литровый восьмицилиндровый двигатель мощностью 505 л.с., работающий в паре с шестиступенчатой МКПП. С такой силовой установкой автомобиль способен разгоняться с нуля до 100 км/ч за 3,8 секунды, а максимальная его скорость составляет 305 км/ч.||Покупатели смогут также заказать и специально разработанный к юбилею модели спортпакет Corvette 60th Anniversary Package. В этом случае автомобиль получит белую окраску, контрастную синюю складную крышу и кожаную обивку кресел того же цвета. На колесных дисках, руле и подголовниках появится эмблема «60th» – «60-й». Вдобавок можно заказать дополнительную \"гоночную\" маркировку: синие полоски.||В компании отмечают, что модификация Collector Edition и юбилейный спортпакет впервые выходят в один и тот же год.',120),(451,1,'Chevrolet представил кабриолет Corvette 427 Collector Edition',120),(452,2,'13/01/2012',120),(453,3,'ДТП||регионы||общественный транспорт',121),(454,5,'На 13-м километре автотрассы Осташков – Торжок в Осташковском районе Тверской области произошло крупное ДТП с участием ВАЗа и рейсового автобуса, следовавшего из Осташкова в Тверь. В результате инцидента погибли пять человек, сообщили ИА «РБК» в пресс-службе ГУ МЧС по региону.||По предварительной информации, причиной происшествия стало грубое нарушение правил дорожного движения водителем легкового автомобиля. Никто из пассажиров автобуса не пострадал. На место инцидента выехали оперативная группа ГУ МЧС России по Тверской области и психологи ведомства.||По предварительным данным, в рейсовом автобусе в момент ДТП находились водитель и два пассажира, которые продолжили движение на попутном транспорте.',121),(455,1,'Под Тверью в ДТП с участием автобуса погибли 5 человек',121),(456,2,'13/01/2012',121),(457,3,'техосмотр',122),(458,5,'Российский союз автостраховщиков аккредитовал уже три организации (сто пунктов), которые будут проводить техосмотр транспортных средств, сообшает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на вице-президента РСА Светлану Никитину.||Напомним, с 1 января 2012 года вступил в силу закон о передаче обязательного техосмотра из ведения МВД коммерческим техцентрам, об отмене этой процедуры для новых автомобилей и о заключении договора о страховании автогражданской ответственности (ОСАГО) только при условии прохождения ТО. Теперь РСА проводит аккредитацию станций техосмотра, далее будет вести их реестр, обеспечивать бланками операторов ТО, а также контролировать их деятельность и проводить проверки.||Пункты прохождения техосмотра, которые раньше работали по договорам с МВД, были внесены в реестр операторов ТО автоматически после получения от них заявлений. По данным С. Никитиной, в настоящее время в реестр операторов ТО внесено более 1,9 тыс. таких операторов техн',122),(459,1,'РСА аккредитовал сто пунктов проведения техосмотра',122),(460,2,'13/01/2012',122),(461,3,'общество',123),(462,5,'Владелец четырех автомобилей, один из которых – Lexus RX 300, пытался любой ценой избежать транспортного налога. Житель Нижнего Новгорода задолжал государству 108 тысяч рублей – сумма немаленькая, но вряд ли неподъемная для владельца такого авто.||Еще в 2010 в отношении автолюбителя было возбуждено исполнительное производство, но и это не заставило должника заплатить налог. Теперь судебный пристав-исполнитель принял более суровые меры: должник лишился права выезда из страны, на регистрационные действия со всеми его автомобилями был наложен запрет, а главное – Lexus RX 300 был арестован. Угроза потери автомобиля все-таки убедила злостного неплательщика явиться в службу судебных приставов. Он заплатил долг в полном объеме, а заодно и исполнительский сбор.',123),(463,1,'Владелец премиального Lexus RX 300 чуть не лишился авто, пытаясь избежать налога',123),(464,2,'13/01/2012',123),(465,3,'Москва||пробки||власть',124),(466,5,'Российский миллиардер Михаил Прохоров предложил свой вариант борьбы с пробками в Москве. По мнению бизнесмена, транспортную ситуацию в столице можно улучшить за счет вывода транзитного потока из города и переноса растаможивания грузов на границу страны. Об этом пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>.||«Решение транспортных проблем Москвы лежит в другой плоскости: 45% машин, которые проходят через Москву, – это транзит. Первое, что нужно сделать, – увести транзит из Москвы. Для этого нужно построить две дороги, которые шли бы в обход Москвы», – сказал кандидат в президенты.||«Вторая проблема, которая удивляет, – вот вы знаете, что 45% грузов таможится в Москве, что они здесь делают? Надо все таможни вынести на границу», – цитирует М. Прохорова издание. Он заявил, что грузы поступают не только для Москвы, но и для всей страны. При этом олигарх отметил, что надо продолжать строить дороги и развязки.',124),(467,1,'М. Прохоров предложил свой способ борьбы с пробками в Москве',124),(468,2,'13/01/2012',124),(469,3,'McLaren||суперкары',125),(470,5,'В интернете появился видеоролик суперкара McLaren MP4-12C в новой модификации High Sport. У автомобиля необычный «профиль»: скошенная передняя часть позволяет максимально увеличить прижимную силу и в то же время обеспечивает более мощный воздушный поток, увеличивая мощность турбонаддува.||Изменились также задний бампер, диффузор и антикрыло. Под капотом суперкара – 3,8-литровый восьмицилиндровый турбодвигатель мощностью 675 л.с. О динамических характеристиках авто в этой модификации не сообщается, но стандартный MP4-12C, располагающий «всего лишь» 600 л.с., разгоняется с нуля до 100 км/ч за 3,1 секунды и способен развить скорость до 330 км/ч.||McLaren MP4-12C High Sport выйдет ограниченным тиражом: будет выпущено лишь пять автомобилей. Владельцем одного из них станет руководитель McLaren Рон Деннис, а еще один суперкар достанется принцу Бахрейна.||<center><object style=\"height: 390px; width: 640px\"><param name=\"movie\" value=\"http://www.youtube.com/v/SCSeSzWp9_I?version=3&feature=player',125),(471,1,'В McLaren создали спортивную модификацию суперкара MP4-12C. ВИДЕО',125),(472,2,'13/01/2012',125),(473,3,'General Motors||российский автопром||Детройт-2012',126),(474,5,'В ближайшие годы General Motors планирует инвестировать 1 млрд долларов в российский автопром. Об этом сообщил представитель компании Джеймс Бовензи на автосалоне в Детройте.||В GM считают российский авторынок крайне перспективным: здесь существует стабильный спрос, уже налажена дилерская сеть, а средний возраст автомобилей в настоящее время составляет более десяти лет. Следовательно, в ближайшие годы можно ожидать обновления более чем половины российского автопарка.||К 2015 году концерн планирует увеличить объем производства в России более чем в два раза: если в 2010 году было выпущено 232 000 автомобилей, то к 2015 году их количество должно составить как минимум 520 000.||В 2012 году GM начнет продажи Chevrolet Cruze в кузове хэтчбек, нового Opel Zafira, Chevrolet Aveo и Chevrolet Camaro. На начало 2013 года намечен старт продаж бюджетного Chevrolet Cobalt, а в 2015-м «GM-Автоваз» планирует выпуск новых Chevrolet Niva и Lada 4&#215;4.',126),(475,1,'General Motors инвестирует 1 млрд долларов в российский автопром',126),(476,2,'13/01/2012',126),(477,3,'Петербург||криминал||парковка',127),(478,5,'Сегодня, 13 января 2012 г., утром возле здания администрации Кировского района на проспекте Стачек в Петербурге взорвалась граната. По предварительным данным, причиной инцидента стала ссора двух водителей, передает «РБК-Петербург».||Двое мужчин сидели в автомобиле «Жигули», когда между ними произошел конфликт. Выйдя из автомобиля, один из участников склоки бросил гранату в окно автомобиля.||В результате взрыва спорщики пострадали, но от госпитализации отказались. По некоторым данным, поводом для ссоры послужило место на парковке, которое соседи не могут поделить уже месяц.',127),(479,1,'Петербуржец взорвал соседа за место на парковке',127),(480,2,'13/01/2012',127),(481,3,'ГИБДД-ГАИ||Москва',128),(482,5,'За новогодние праздники сотрудники ГИБДД поймали более 250 нетрезвых водителей, еще около 300 отказались пройти медицинское освидетельствование. Об этом сегодня, 13 января, сообщил замглавы УГИБДД по Москве Владимир Томчак.||«Двести пятьдесят восемь водителей управляли транспортными средствами в состоянии алкогольного опьянения. Двести девяносто семь отказались от прохождения медицинского освидетельствования», – рассказал он.||Томчак также сообщил, что за период с 1 по 9 января в столице произошло 172 серьезных ДТП, в которых погибло 15 человек и еще 233, в том числе трое детей, получили травмы различной степени тяжести. Кроме того, отметил он, произошло свыше 6,3 тысячи дорожно-транспортных происшествий без пострадавших.',128),(483,1,'За время праздников столичные инспекторы поймали 600 пьяных водителей',128),(484,2,'13/01/2012',128),(485,3,'ДТП',129),(486,5,'Трагедия произошла сегодня, 18 января, в центре Магадана. Водитель Suzuki Grand Vitara не справился с управлением и врезался в другой автомобиль. От удара его отбросило на светофорный столб, затем автомобиль продолжил движение и выехал на тротуар, где сбил четверых человек, по инерции вылетел на противоположную улицу и сбил там еще двоих.||Один из пешеходов, молодой мужчина 1983 года рождения, скончался на месте. Остальные пятеро были госпитализированы с травмами различной степени тяжести, двое из них находятся в тяжелом состоянии.||Как сообщают в ГИБДД, виновник аварии был абсолютно трезв. По его словам, у него случился приступ, из-за которого он полностью потерял управление. Против него возбуждено уголовное дело по ч. 3 ст. 264 УК РФ (нарушение правил дорожного движения, повлекшее смерть двух или более лиц). Максимальное наказание в случае признания виновности составляет до пяти лет лишения свободы. Водитель задержан и помещен в следственный изолятор.',129),(487,1,'Водитель Suzuki сбил на тротуаре шестерых человек',129),(488,2,'18/01/2012',129),(489,3,'ИжАвто||российский автопром',130),(490,5,'Производство на заводе «ИжАвто» возобновилось сегодня, 18 января, после окончания плановой проверки оборудования, значительная часть которой совпала с периодом новогодних каникул. Об этом сообщает пресс-служба предприятия.||Плановая проверка оборудования началась еще 26 декабря. В последнее время объем производства на заводе превысил показатели за последние 20 лет: здесь производилось более 10 000 автомобилей в месяц. По итогам столь интенсивной работы было решено временно остановить все конвейеры для тотальной технической проверки.||За отведенные на нее 22 дня были произведены работы по техническому перевооружению производственного комплекса, плановый осмотр и наладка технологического оборудования, энергетического и логистического комплексов. Была подготовлена площадка для монтажа сварочного оборудования производства автомобилей Lada Granta – монтаж начнется в ближайшее время.||В настоящее время на «ИжАвто» идет подготовка к обучению персонала для производства новых для автозавода мод',130),(491,1,'«ИжАвто» снова в строю',130),(492,2,'18/01/2012',130),(493,3,'мировой авторынок||Renault',131),(494,5,'Мировые продажи группы Renault в 2011 году выросли на 3,6% и составили 2 722 062 автомобиля. При этом продажи за пределами Европы выросли на 19,2% и сегодня составляют 43% от общего объема, сообщила пресс-служба компании. В Европе Renault сохранила за собой второе место среди производителей легковых автомобилей и коммерческого транспорта, занимая 8,6% рынка. Однако в Европе объем продаж Renault упал на 5,7% (до 1 549 376 автомобилей). Впрочем, это не вызывает удивления на фоне общего спада на абсолютном большинстве авторынков Европы.||Комментируя эти результаты, исполнительный вице-президент группы Renault по продажам, маркетингу и коммерческому транспорту Жером Столл отметил: «В 2011 г. группа Renault достигла уровня продаж в 2,7 млн автомобилей за пределами Европы. Несмотря на неоднородные результаты в Европе, Renault остается второй маркой на рынке и занимает первое место в сегменте коммерческого транспорта четырнадцатый год подряд».',131),(495,1,'Мировые продажи группы Renault в 2011 году выросли на 3,6%',131),(496,2,'18/01/2012',131),(497,3,'ГИБДД-ГАИ',132),(498,5,'Автолюбитель, который напал на инспектора ДПС и покалечил его, приговорен к двум годам лишения свободы в колонии общего режима. Об этом сообщает прокуратура Ижевска, города, где и произошел данный инцидент.||Владимир Смирнов, будучи в нетрезвом состоянии, 24 июня 2011 года ехал на своем автомобиле. Видя его манеру управления автомобилем, инспектор ДПС заподозрил, что водитель находится в состоянии алкогольного опьянения, остановил его и потребовал предъявить документы. В ответ В. Смирнов нанес инспектору удар локтем по колену, разорвав связки, и укусил за палец правой руки. Сотрудник более пяти месяцев находился на лечении, и неизвестно, как долго еще ему понадобится терапия и какого результата удастся добиться врачам: клинический прогноз неблагоприятный.||Октябрьским районным судом Ижевска злоумышленник признан виновным по ч. 2 ст. 318 Уголовного кодекса РФ (применение насилия, опасного для здоровья, в отношении представителя власти в связи с исполнением им своих должностных обязаннос',132),(499,1,'Остановленный гаишником пьяный водитель напал на него',132),(500,2,'18/01/2012',132),(501,3,'дороги||финансы',133),(502,5,'Общественные организации должны следить за целевым использованием регионами денег, выделенных на дорожное строительство, заявил премьер-министр России Владимир Путин. Сегодня, 17 января 2012 г., в ходе встречи с представителями организаций и общественных объединений автомобилистов глава правительства напомнил о планах по существенному пополнению региональных дорожных фондов.||«За счет налога 90 миллиардов получат регионы в дорожные фонды, а за счет акцизов еще 94 миллиарда», – цитирует В. Путина <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>. Он подчеркнул, что это большие финансовые средства и такие деньги в региональные бюджеты не выделялись уже давно.||«И общественные организации, и заксобрания на местах, в регионах должны самым строгим образом следить за тем, чтобы деньги были истрачены исключительно целевым образом, чтобы граждане почувствовали изменение ситуации с качеством наших дорог», – сказал В. Путин.',133),(503,1,'Путин: траты на дороги должна контролировать общественность',133),(504,2,'17/01/2012',133),(505,3,'техосмотр||ТО',134),(506,5,'Пункты технического осмотра автомобилей при отсутствии бланков талонов ТО смогут выдавать их в простой письменной форме. Об этом сообщил первый вице-премьер РФ Игорь Шувалов на заседании Общероссийского народного фронта по проблемам автомобилистов. И. Шувалов отметил, что в связи с переходом на новые стандарты техосмотра не на всех станциях есть необходимые бланки.||«Мы приняли решение, что эти пункты, где бланки отсутствуют по какой-либо причине, могут в простой письменной форме заполнить характеристику автомобиля, подписать и направить страховым организациям», – сказал первый вице-премьер РФ. После этого страховые органы будут уполномочены выдать полис ОСАГО. И. Шувалов добавил, что Союз автостраховщиков это предложение поддержал.||Первый вице-премьер РФ также отметил, что при переходе на новую систему прохождения техосмотра не был сокращен ни один пункт техобслуживания, при этом было открыто 13 новых. И. Шувалов напомнил, что в планах правительства РФ было аккредитовать профессионал',134),(507,1,'Талоны техосмотра будут писать от руки',134),(508,2,'17/01/2012',134),(509,3,'BMW||M6||6-я серия||6-Series||GranCoupe||шпионские фото||spy photo',135),(510,5,'Новый BMW M6 GranCoupe попал в объективы фотографов-шпионов во время дорожных тестов в Германии. Этот автомобиль представляет собой четырехдверную версию купе M6. Новинка оснащена тем же турбированным двигателем V8 объемом 4,4 л, что и BMW M5. Однако ожидается, что для M6 GranCoupe мотор будет форсирован и станет выдавать порядка 590 л.с. вместо 550.||«Пойманный» прототип отличается от представленного недавно BMW 6-й серии GranCoupe двумя сдвоенными патрубками выхлопной системы, более агрессивными передним и задним бамперами и колесами большего размера.||Серийная версия модели появится ближе к концу текущего года, после того как немецкий автопроизводитель представит купе и кабриолет M6.',135),(511,1,'BMW тестирует четырехдверное купе M6 GranCoupe',135),(512,2,'17/01/2012',135),(513,3,'Москва||техосмотр||ТО||власть',136),(514,5,'Стоимость техосмотра автомобилей, которая в Москве составляет 690 рублей, будет постепенно пересматриваться, сказал столичный градоначальник Сергей Собянин. Вместе с премьером России Владимиром Путиным он побывал сегодня, 17 января 2012 г., на одном из пунктов техосмотра в Москве. Беседуя с главой правительства, столичный мэр признал, что «тариф надо потихоньку пересматривать», пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>.||Для корректировки тарифа надо будет посмотреть доходность работы пунктов техосмотра, пояснил С. Собянин. При этом он подчеркнул, что прибыльность техосмотра вряд ли будет низкой, поскольку московский автопарк ежегодно увеличивается примерно на 700 000 машин.||В 2011 году тариф техосмотра в московском регионе составлял 990 рублей, однако 1 января 2012 года он был снижен на 300 рублей в связи с исключением из него госпошлины. По мнению операторов техосмотра, нынешний тариф в Москве нужно увеличить, поскольку работать становится невыгодно. Предп',136),(515,1,'Собянин пообещал, что цена техосмотра будет пересмотрена',136),(516,2,'17/01/2012',136),(517,3,'ДТП||Петербург',137),(518,5,'В аварии на Кольцевой автодороге в Санкт-Петербурге погиб директор петербургского завода «Русский стандарт» Сергей Андреев. Данную информацию информагентству «РБК» подтвердили в пресс-службе компании.||ГИБДД уточняет, что авария произошла еще на прошлой неделе, вечером 14 января 2012 г. Внедорожник Volkswagen Touareg на большой скорости врезался в стоявшую на трассе фуру. Находившийся за рулем внедорожника С. Андреев скончался на месте до приезда «скорой помощи».||Завод компании «Русский Стандарт Водка» (входит в группу компаний «Русский стандарт») был открыт в Петербурге в октябре 2006 г. Корпорация «Русский стандарт» объединяет бизнесы по производству и дистрибуции алкогольной продукции, банковское дело и страхование.',137),(519,1,'В ДТП в Петербурге погиб директор «Русского стандарта»',137),(520,2,'17/01/2012',137),(521,3,'техосмотр||ТО||власть',138),(522,5,'Премьер-министр РФ Владимир Путин заявил, что при передаче права проведения техосмотра частным компаниям нельзя допустить монополизации данного рынка. Он напомнил, что с 2012 г. применяется новый порядок прохождения техосмотра – постепенно он передается от ГИБДД на станции, уполномоченные Российским союзом автостраховщиков.||«Здесь хотел бы обратить внимание на другую возможную засаду – возможность монополизации рынка, чего допустить нельзя. Так же как нельзя занижать рентабельность предприятий, занимающихся этим видом деятельности», – подчеркнул В. Путин.||Премьер-министр РФ рассказал, что вместе с первым вице-премьером РФ Игорем Шуваловым он побывал на одной из таких станций. По словам главы правительства, он остался доволен тем, что там увидел. «В целом за 30 минут можно пройти техосмотр и получить уже талон», – отметил премьер.',138),(523,1,'Путин назвал «засадой» возможность монополизации рынка ТО',138),(524,2,'17/01/2012',138),(525,3,'Volkswagen||ГАЗ||заводы||российский автопром||промсборка||автопроизводство',139),(526,5,'Volkswagen готовится к запуску производства автомобилей в Нижнем Новгороде. Пресс-служба компании объявила, что с 1 февраля 2012 г. проект возглавят Оливер Грюнберг и Петр Линхарт. О. Грюнберг будет отвечать за обеспечение качества, предсерийную подготовку производства и производство, а П. Линхарт – за планирование, логистику и управление запуском производства.||О. Грюнберг работает в концерне Volkswagen с 1995 г. С 2007 г. по 2009 г. он руководил автомобильным производством и был членом правления, а также заместителем генерального директора на российском заводе Volkswagen в Калуге. С 2010 г. по 2012 г. О. Грюнберг являлся управляющим и техническим директором индийского подразделения Skoda в Аурангабаде (Индия).||П. Линхарт с 1995 г. исполнял различные функции по работе с сервисной и дилерской сетью. В 2006-2009 гг. он руководил службой логистики на заводе Volkswagen в Калуге. С 2009 г. по 2011 г. работал в Млада Болеславе (Чехия), где возглавлял подразделение логистики марки Skoda и ц',139),(527,1,'Volkswagen готовится к запуску производства в Нижнем Новгороде',139),(528,2,'17/01/2012',139),(529,3,'Москва||общественный транспорт||пробки',140),(530,5,'В Москве из-за неправильно припаркованных машин за последние сутки произошло 282 сбоя в движении наземного общественного транспорта. Как сообщает пресс-служба департамента транспорта и развития дорожно-транспортной инфраструктуры столицы, скопление личного автотранспорта и неправильно припаркованные машины стали основной причиной задержек движения автобусов, троллейбусов и трамваев. Средняя продолжительность задержек движения общественного транспорта составила от 10 до 45 минут.||В то же время жители Москвы стали чаще отдавать предпочтение общественному транспорту. В 2011 году число пассажиров, перевезенных городскими автобусами, увеличилось по сравнению с 2010 годом. Вероятнее всего, это связано с введением выделенных полос для общественного транспорта: благодаря данному нововведению эксплуатационная скорость пассажирских автобусов и троллейбусов увеличилась на 30%.',140),(531,1,'Припаркованные авто блокировали движение автобусов в Москве',140),(532,2,'17/01/2012',140),(533,3,'Ford||Sollers||автопроизводство',141),(534,5,'Завод Ford Sollers во Всеволожске в 2012 году планирует выпустить 119 000 автомобилей. Об этом пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на президента Ford Sollers Теда Канниса. «В 2011 году завод выпустил около 99 000 автомобилей, если дела пойдут хорошо, то мы произведем в этом году более 119 000», – сказал он.||«Мы продолжаем производство автомобилей Focus и Mondeo. Они хорошо продаются. В связи с успешными продажами в следующем году, возможно, нам потребуется увеличение производства», – добавил Т. Каннис.||В свою очередь вице-президент по маркетингу Ford Sollers Марк Симпсон сообщил, что согласно планам компании продажи автомобилей Ford в России в 2012 году составят 140 000 штук.',141),(535,1,'Ford увеличит производство в России на 20 000 авто в год',141),(536,2,'17/01/2012',141),(537,3,'Москва||дороги',142),(538,5,'В 2011 году число пассажиров, перевезенных городскими автобусами, увеличилось по сравнению с 2010 годом, сообщил пресс-центр мэрии Москвы. Вероятнее всего, это связано с введением выделенных полос для общественного транспорта: благодаря данному нововведению эксплуатационная скорость пассажирских автобусов и троллейбусов увеличилась на 30%.||Городские власти называют дальнейшую организацию выделенных полос одной из приоритетных задач на ближайшие годы. Так, уже в начале февраля будет введена в эксплуатацию выделенная полоса на Ленинском проспекте. Кроме того, будет произведена закупка современного подвижного состава общественного транспорта. Весь комплекс нововведений направлен на увеличение скорости и повышение комфортности перевозок. Ожидается, что в 2012 году пассажиропоток возрастет до 1 млрд 617 млн человек.',142),(539,1,'Все больше москвичей выбирают общественный наземный транспорт',142),(540,2,'17/01/2012',142),(541,3,'Москва||дороги||стихия',143),(542,5,'После еще одного из череды почти непрекращающихся в последнее время снегопадов коммунальные службы Москвы приступили к уборке улиц города от снега. Снегоуборочная техника и самосвалы для вывоза снега заняли правые крайние ряды дорог, что привело к образованию огромных заторов в целом ряде районов столицы. В настоящее время серьезные затруднения движения из-за скопления коммунальной техники наблюдаются на Третьем транспортном кольце, Профсоюзной улице по направлению в область, Сиреневом бульваре и других улицах города. Аналогичные затруднения наблюдаются и на МКАД.||Напомним, за один только вчерашний день с улиц города на снегоплавильные пункты было вывезено свыше 360 тысяч кубометров снега. Снегопад, начавшийся в выходные, продолжается с небольшими перерывами. За два дня в столичном регионе выпало десять сантиметров осадков, в уборке улиц принимает участие более одиннадцати тысяч единиц снегоуборочной техники.',143),(543,1,'Снегоуборочная техника стала причиной пробок в столице',143),(544,2,'17/01/2012',143),(545,3,'ДТП',144),(546,5,'На железнодорожном переезде легковой автомобиль неудачно попытался проскочить перед поездом. В результате столкновения пострадал водитель автомобиля. Инцидент произошел в Забайкалье накануне, 16 января, на неохраняемом железнодорожном переезде в районе станции Березовский-Восточный, сообщает пресс-служба Забайкальской железной дороги.||Как установила комиссия по расследованию происшествия, легковой автомобиль Mazda выехал на железнодорожный переезд перед приближающимся поездом № 6602. Локомотивная бригада применила экстренное торможение, однако избежать столкновения не удалось. В результате ДТП с травмами различной степени тяжести госпитализирована женщина, управлявшая автомобилем. Среди пассажиров пострадавших нет, на локомотиве повреждены метельник и скоростемер. Инфраструктура пути не пострадала. Задержка поезда составила 23 минуты.',144),(547,1,'Поезд столкнулся с автомобилем на железнодорожном переезде',144),(548,2,'17/01/2012',144),(549,3,'Peugeot||российский авторынок||новинки',145),(550,5,'На российский рынок выходит Peugeot 508 в кузове седан. В продажу он поступит с 1 февраля 2012 года. Покупатели смогут выбрать один из двух бензиновых двигателей: 1,6-литровый мощностью 120 л.с. и 1,6-литровый мощностью 150 л.с. В линейке есть и дизельные агрегаты: 2,0-литровый (136 л.с.) и 2,2-литровый (204 л.с.). Все они устанавливаются в паре с шестиступенчатыми автоматическими трансмиссиями.||В базовую комплектацию Access входят шесть подушек безопасности, система динамической стабилизации ESP, передние и задние электростеклоподъемники, двухзонный климат-контроль, аудиосистема CD/MP3, USB/Bluetooth и многофункциональное рулевое колесо. Ее стартовая цена составляет 839 000 рублей.||Комплектация Active включает также круиз-контроль, датчики света и дождя, подогрев передних сидений, аудиосистему с двумя дополнительными динамиками (восемь вместо шести). Ее стартовая цена составляет 894 000 рублей. Среди доступных опций – ксенон, светодиодные огни дневного света, омыватели фар и кожаные',145),(551,1,'Peugeot анонсировал цены на флагманскую модель 508',145),(552,2,'17/01/2012',145),(553,3,'мировой авторынок||статистика',146),(554,5,'Объем продаж легковых автомобилей в странах Европейского союза сокращается по сравнению с показателями предыдущего года. В декабре 2011 года он снизился на 6,4% по сравнению с декабрем 2010 года и составил 953 108 машин. Об этом сообщается в опубликованном сегодня отчете Европейской ассоциации автопроизводителей (ACEA).||Объем продаж легковых автомобилей в декабре 2011 г. к аналогичному показателю годом ранее вырос только в Германии (на 6,1% – до 244 тыс. 501 машины). Сократился этот показатель за отчетный период в Великобритании (-3,7% – до 119 тыс. 188 машин), Испании (-3,6% – до 66 тыс. 458 машин), Франции (-17,7% – до 187 тыс. 817 машин) и Италии (на 15,3% – до 111 тыс. 212 машин).||В 2011 г. объем продаж автомобилей сократился практически на всех ключевых рынках: во Франции (-2,1%), Великобритании (-4,4%), Италии (-10,9%), Испании (-17,7%).',146),(555,1,'За декабрь 2011 года в Европе объем продаж легковых авто снизился на 6,4%',146),(556,2,'17/01/2012',146),(557,3,'ГИБДД-ГАИ',147),(558,5,'Бывший инспектор ГИБДД осужден за превышение должностных полномочий и признан виновным в 38 подлогах. В ходе следствия было установлено, что 37-летний инспектор ДПС Виталий Гвоздев на протяжении 2010 года систематически подавал фиктивные рапорты о якобы случившихся административных правонарушениях.||В надежде на премию за отличное исполнение служебных обязанностей гаишник вносил в бланки постановлений заведомо ложные сведения. Правонарушение он указывал не слишком тяжелое: якобы остановленные им граждане совершали переход проезжей части в неустановленном месте. Имена псевдонарушителей он брал из информационной базы ГИБДД. В одном из случаев он даже ухитрился составить протокол на гражданина, который погиб в ДТП шестью годами ранее, в 2004 году.||Горе-гаишник приговорен к уплате штрафа в размере 20 тысяч рублей, сообщает пресс-служба СУСК по Пермскому краю. Своей работы он, естественно, также лишился.',147),(559,1,'Ради статистики гаишник оштрафовал пешехода, погибшего несколькими годами ранее',147),(560,2,'17/01/2012',147),(561,3,'Bridgestone||шины',148),(562,5,'В Bridgestone освоили новую технологию, позволяющую печатать изображения на боковинах автомобильных шин. При такой печати используются три слоя: основа под изображение, содержащая компоненты, предотвращающие выцветание картинки, само изображение (его наносят специально разработанными чернилами) и прозрачный защитный слой.||Новый способ значительно лучше применяемой в настоящее время технологии, позволяющей наносить на боковины надписи путем внедрения слоя белой резины в основной черный массив. При этом неизбежно увеличивается вес шины, чего не требуется в новой технологии. А главное, последняя позволяет использовать огромное количество цветов и отображать не только надписи, но также рисунки и даже фотографии. Если через некоторое время покупатель захочет удалить изображение или сменить его на другое, это не составит проблемы.||Bridgestone планирует внедрить данную технологию на рынок в самое ближайшее время.',148),(563,1,'В Bridgestone научились печатать рисунки и фото на шинах',148),(564,2,'18/01/2012',148),(565,3,'Aston Martin||дилеры||luxury',149),(566,5,'Компания Aston Martin продолжает активную экспансию на рынок Китая. В Шанхае открылся крупнейший в мире дилерский центр британской марки, ставший седьмым по счету в Китае. Новый автосалон имеет шоу-рум, рассчитанный на 18 автомобилей. Здесь также предусмотрены кафе, зона отдыха и отдельные залы, где представлена продукция постоянных партнеров Aston Martin: часы Jaeger-LeCoultre и аудио- и видеотехника Bang&Olufsen.||Появившийся на китайском рынке только в 2008 году к настоящему моменту Aston Martin имеет автосалоны в Пекине, Шанхае, Ханчжоу, Гуанчжоу, Чэнду и Шеньчжэне. В 2012 году планируется открытие еще трех дилерских центров в провинциях Чунцин, Шэньян и Эрдеш. Для британской автокомпании Китай является одним из наиболее перспективных направлений развития и самым быстрорастущим рынком в Азии. В 2011 году продажи автомобилей Aston Martin в Китае выросли на 103% по сравнению с предыдущим годом.',149),(567,1,'Крупнейший в мире салон Aston Martin открылся в Шанхае',149),(568,2,'18/01/2012',149),(569,3,'Dodge||Journey||цены||внедорожники||SUV',150),(570,5,'В следующем месяце автомобили Dodge вернутся на российский рынок. С 1 февраля 2012 г. марка начинает российские продажи обновленного Journey. Цена автомобиля с полным приводом и автоматической коробкой передач в комплектации R/T составляет 1 699 500 рублей.||Journey получил новую подвеску, новый шестицилиндровый двигатель, а также новый салон с улучшенным дизайном интерьера. Модель также комплектуется информационно-развлекательной системой Uconnect Touch 8.4. В базовую комплектацию включена система Enter-N-Go, обеспечивающая возможность доступа в салон без использования ключа, а также кнопочный запуск электростартера.||Dodge Journey R/T оснащается двигателем Pentastar V6 объемом 3,6 л и мощностью 283 л.с., развивающим крутящий момент 352 Н&#183;м. В паре с этим мотором устанавливается шестиступенчатая автоматическая коробка передач. Расход топлива составляет 9,4 л/100 км в загородном цикле.||Новинка укомплектована подключаемым полным приводом. Большую часть времени автомобиль движется ',150),(571,1,'Dodge возвращается на российский рынок',150),(572,2,'18/01/2012',150),(573,3,'Ford||новинки||российский авторынок||российский автопром',151),(574,5,'Ford запустил в России производство Ford Focus в кузове универсал. Первый из этих автомобилей, сошедший с конвейера завода в Ленинградской области, стал пятисоттысячным автомобилем Ford, произведенным на территории РФ. В компании рассчитывают, что теперь, когда Ford Focus третьего поколения выпускается во всех модификациях кузова (седан, хэтчбек» и универсал), продажи марки вырастут на 20% по сравнению с 2011 годом. Эксперты в прогнозах осторожны и полагают, что доля универсалов не превысит 10%.||Цена на универсал Ford Focus III в зависимости от комплектации варьируется от 634 тыс. руб. (бензиновый двигатель объемом 1,6 л с механической коробкой переключения передач) до 897 тыс. руб. (дизельный двигатель объемом 2 л с автоматической коробкой передач).',151),(575,1,'Ford  запустил производство универсала Focus в России',151),(576,2,'18/01/2012',151),(577,3,'Формула-1||автоспорт',152),(578,5,'Не исключено, что Виталий Петров все-таки войдет в основной состав команды Caterham. Несмотря на то что Ярно Трулли уже подписал контракт с этой командой, 37-летний гонщик может лишиться места пилота, пишет немецкое издание Motor-Total.||По неподтвержденной информации, менеджменту Виталия Петрова удалось заинтересовать Caterham предложением, которое стало возможно благодаря двум российским спонсорам – ЛУКОЙЛу и АвтоВАЗу.||По слухам, Петров принесет команде 15 миллионов долларов. Если эти слухи верны, руководство команды, возможно, захочет расторгнуть договор с Трулли.||Напомним, Ярно Трулли выступал за команду, тогда носившую имя Lotus Racing, с момента ее создания в 2010 году. За все это время команда не смогла набрать ни одного очка.',152),(579,1,'Не исключено, что Петров займет в Caterham место Ярно Трулли',152),(580,2,'18/01/2012',152),(581,3,'угоны||статистика||криминал',153),(582,5,'Наибольшей популярностью у автоугонщиков пользуются автомобили ВАЗ, Toyota и Mitsubishi. Об этом сообщил журналистам начальник отдела Управления уголовного розыска Главного управления Министерства внутренних дел РФ по Москве полковник полиции Андрей Баранов. По его словам, в 2011 году было похищено 1625 автомобилей ВАЗ, 1063 автомобиля Toyota, 992 Mitsubishi, 904 Mazda, 879 Honda и 589 российских машин производства ГАЗа. Зарегистрировано также хищение 304 единиц грузового автотранспорта и спецтехники.||Всего в минувшем году на территории Москвы было совершено 10 812 краж автомобилей, что на 780 инцидентов больше по сравнению с 2010 г., 163 грабежа с целью завладения авто (рост на 61 инцидент), 70 разбойных нападений (рост на 17 случаев нападения), более 2 тыс. угонов без цели хищения (рост на 373 по сравнению с 2010 г.).||По данным полиции, чаще всего автомобили угонялись в Восточном, Северном и Юго-Восточном административных округах столицы. Ночью похищено 58% всех машин, днем – 15%. ',153),(583,1,'Московские автоугонщики предпочитают ВАЗ, Toyota и Mitsubishi',153),(584,2,'18/01/2012',153),(585,3,'ГИБДД||полиция||криминал||коррупция',154),(586,5,'Суд приговорил бывшего начальника управления ГИБДД МВД России по Республике Хакасия Вячеслава Лепшеева к 10 годам колонии строгого режима за получение взяток, общая сумма которых превышает 820 тысяч рублей. Как сообщили в управлении взаимодействия со СМИ Генеральной прокуратуры РФ, он также был оштрафован судом на 1 млн рублей. Бывший руководитель регионального управления Госавтоинспекции осужден по трем эпизодам получения взятки в крупном размере по п. «в» ч. 5 ст. 290 Уголовного кодекса РФ.||Как установило следствие, с апреля 2006 года по май 2007-го В. Лепшеев установил в Абакане необоснованно завышенные требования для получения бланков справок-счетов. За беспрепятственную выдачу таких бланков он получил взятки от индивидуального предпринимателя Александра Кучерова и руководителей ООО «Торговый дом «Самара», ООО «Автолидер» и ООО «Мегаполис», осуществлявших деятельность по оформлению документов для регистрации транспортных средств, на общую сумму более 820 тыс. руб.||Стоит отметить,',154),(587,1,'Глава управления ГИБДД приговорен к 10 годам колонии за взятки',154),(588,2,'18/01/2012',154),(589,3,'Формула-1||автоспорт',155),(590,5,'В новом сезоне Виталий Петров может оказаться в команде Caterham, сообщает финская газета Turun Sanomat. Менеджера российского гонщика Оксану Косаченко видели во время ее недавнего визита в штаб бывшей Team Lotus в британском графстве Норфолк. Основной состав пилотов Caterham давно утвержден: в грядущем сезоне за команду выступят Ярно Трулли и Хейкки Ковалайнен. Значит, речь, скорее всего, идет о включении Петрова в качестве третьего пилота.||При благоприятном для российского гонщика стечении обстоятельств такое место в команде позволило бы ему надеяться войти в основной состав в 2013 году, заняв место Ярно Трулли. Менеджмент Петрова пока отказывается комментировать эти слухи.',155),(591,1,'Автоспорт: Виталий Петров может оказаться в команде Caterham',155),(592,2,'18/01/2012',155),(593,3,'Saab',156),(594,5,'В рамках ликвидации Saab с молотка уйдут все автомобили из музея компании в Трольхеттане. В коллекции музея – более 120 классических автомобилей Saab и самых различных концептов. Возможность приобрести уникальные машины, вошедшие в историю автомобилестроения, уже привлекла многих коллекционеров.||Среди выставленных на продажу моделей попадаются самые разные: от последних разработок компании, таких как концепты 9-X BioHybrid 2009 года, 9-6X 2007-го и Aero X 2006-го, до уникальных старых концептов вроде 900 EV-1 1984 года (EV в данном случае, конечно же, Experimental Vehicle, то есть экспериментальный автомобиль) и таких ранних машин марки, как Formula Junior 1960-го, Sonett 1956 и даже созданный в 1946 году прототип Ursaab. Заявки на участие в торгах принимаются до 20 января.',156),(595,1,'Автомобили из коллекции музея Saab выставлены на продажу',156),(596,2,'18/01/2012',156),(597,3,'угоны',157),(598,5,'В ГИБДД рассказали об основных способах угона автомобилей и поделились советами, как сохранить свою машину. Начальник 7-го отдела Управления уголовного розыска ГУ МВД по Москве полковник полиции Андрей Баранов отметил, что в момент покупки автомобилей продавцы пытаются убедить граждан, что приобретение противоугонной системы убережет их авто от угона. «Это не соответствует действительности», – подчеркнул А. Баранов. Автоугонщики используют огромное количество различных хитроумных, несложных в использовании приспособлений и методик вплоть до считывания сигнала электронного ключа, находящегося в кармане владельца.||Часто элементарные механические запорные устройства куда дольше сопротивляются взлому, чем электронные. По словам А. Баранова, хорошо себя зарекомендовали и системы контроля местонахождения машины: «Если ее хорошо спрятать, то обнаружить эту шайбу практически невозможно, а поиск такой машины, скорее всего, приведет к положительному результату».||«Лучшее противоугонное средство',157),(599,1,'Лучшими противоугонными средствами названы простые механические системы',157),(600,2,'18/01/2012',157),(601,3,'Ferrari',158),(602,5,'Норвежская компания Customized.no предложила перевести Ferrari FF с бензина на биоэтанол. Это не только сделает автомобиль высокоэкологичным, но и увеличит и без того немалую мощность его двигателя V12 со стандартных 651 л.с. до фантастических 875 л.с. ||Биоэтанол E85 содержит всего 15% бензина (остальное – собственно этанол), так что выхлоп CO2 будет ничтожно мал, а заявленная мощность впечатляет еще больше, если учесть, что за такой тюнинг компания просит всего лишь 1500 евро.||На новом топливе полноприводный FF сможет разгоняться с нуля до 100 км/ч на 20% быстрее, чем прежде: меньше чем за 3 секунды.||Стоит отметить, что норвежцы не в первый раз экспериментируют с биоэтанолом. Так, при тюнинге Koenigsegg Agera R им удалось добиться мощности 1099 л.с.',158),(603,1,'На экотопливе Ferrari FF может разгоняться с нуля до 100 км/ч за 2 секунды',158),(604,2,'18/01/2012',158),(605,3,'Golf||новинки',159),(606,5,'Volkswagen покажет Golf седьмого поколения в сентябре на автосалоне в Париже. Как сообщили в компании, он будет длиннее и шире шестого поколения модели: его построят на новой платформе MQB, как и недавно представленный концепт Cross Coupe.||Новинку неоднократно удавалось сфотографировать во время дорожных тестов. Несмотря на камуфляж, ясно, что дизайн автомобиля довольно сильно изменился, причем изменения коснулись не только кузова, но и салона: автомобиль получил новую наклонную панель приборов.||Технические характеристики Golf седьмого поколения пока не сообщаются. В линейке двигателей наверняка будут как бензиновые, так и дизельные агрегаты, а Golf VII GTI может получить модифицированный 2,0-литровый четырехцилиндровый агрегат мощностью 220 л.с.',159),(607,1,'Volkswagen покажет Golf седьмого поколения в сентябре 2012 года',159),(608,2,'18/01/2012',159),(609,3,'BMW||X5||внедорожники||SUV||spy photo||шпионские фото',160),(610,5,'Фотографам-шпионам впервые удалось запечатлеть на фото внедорожник BMW X5 третьего поколения. Автомобиль появится в продаже в 2014 году, то есть одновременно с новым внедорожником Mercedes-Benz M-класса.||Прототип X5 ниже предыдущего поколения модели и получил более спортивный облик. Автомобиль построен на доработанной платформе BMW 5-й серии, созданной из легковесные материалов, что позволило сократить вес внедорожника на 150 кг. Автопроизводитель принял и другие меры по уменьшению веса авто. Ожидается, что новое поколение BMW X5 будет весить 1800 кг против 2125 кг у актуального BMW X5 xDrive30i.||Линейка двигателей, устанавливаемых на SUV, доработана с целью сокращения потребления топлива и выброса вредных веществ в атмосферу. Ожидается, что новый BMW X5 будет оснащаться и двумя новыми четырехцилиндровыми моторами: бензиновым и дизельным мощностью порядка 245 л.с. Кроме того, на модель будут устанавливаться две гибридные установки, одна из которых будет состоять из 3-литрового бензин',160),(611,1,'Фотографы впервые «поймали» BMW X5 третьего поколения',160),(612,2,'18/01/2012',160),(613,3,'Tesla||электромобили',161),(614,5,'Руководитель Tesla Элон Маск объявил, что кроссовер модели X будет представлен 9 февраля в дизайн-студии компании. Пока не ясно, будет ли Х концептом или серийной версией, первое более вероятно, так как о модели до сих пор почти ничего не сообщалось.||Автомобиль построен на модифицированной платформе модели S и получит такую же батарею. Седан развивает скорость до 88 км/ч и способен проехать 257 км на одном заряде, но кроссовер крупнее и тяжелее, так что и скорость, и запас хода, скорее всего, окажутся чуть меньше.||Ранее сообщалось, что новинка должна появиться на рынке в 2013 году, но в тех же сообщениях было указано, что ее представят на автосалоне в Детройте, чего так и не случилось. Вероятно, 9 февраля на презентации будет оглашен уточненный срок выхода модели X в продажу.',161),(615,1,'Tesla покажет новую модель X в феврале',161),(616,2,'19/01/2012',161),(617,3,'Mercedes-Benz||новинки',162),(618,5,'В Mercedes-Benz назвали стоимость самого мощного SLK – 55 AMG. Автодилеры уже принимают заказы на него по цене от 54 950 евро.||Под капотом новинки находится 5,5-литровый двигатель V8 с системой прямого впрыска и мощностью 421 л.с. Семиступенчатая автоматическая трансмиссия AMG Speedshift 7G-Tronic Plus, в паре с которой он работает, позволяет ускоряться с нуля до 100 км/ч за 4,6 секунды. Максимальная скорость ограничена электроникой на отметке 250 км/ч. Разработчикам удалось одновременно увеличить мощность и снизить выхлоп на 30%, выделение CO2 составляет 195 г/км.||SLK 55 AMG получил 18-дюймовые легкосплавные диски, адаптивную систему освещения и цифровое радио DAB. Покупатели также могут заказать специальный пакет AMG Performance Package. Он включает новую подвеску, тормозную систему и блокировку дифференциала. Его стоимость – 4105 евро. Доступен и пакет AMG Driver, в котором снимается электронное ограничение скорости.',162),(619,1,'Названа цена самого мощного Mercedes-Benz SLK 55 AMG',162),(620,2,'19/01/2012',162),(621,3,'мигалки||общество||власть||законы',163),(622,5,'ЛДПР внесла в Госдуму законопроект, предлагающий ввести уголовное наказание за незаконную установку звуковых и световых приборов на автомобиле, в том числе мигалок и стробоскопов. Об этом сообщает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на аппарат нижней палаты парламента.||ЛДПР предлагает исключить из Административного кодекса наказание, предусматривающее штраф размером 2500 рублей, и ввести дополнительную статью в Уголовный кодекс с более суровыми санкциями.||«Мы предлагаем дополнить Уголовный кодекс статьей с уже более серьезными штрафами – до 200 000 рублей и лишением права управления автомобилем от 2 до 3 лет либо без такового. Также в качестве наказания предусматривается заключение под стражу от 3 до 6 месяцев», – рассказал РИА «Новости» один из авторов законопроекта Ярослав Нилов.||По мнению депутата, эти нормы могли бы применяться на усмотрение суда в зависимости от того, насколько тяжкими являются последствия (если они есть) в результате устан',163),(623,1,'ЛДПР предлагает сажать в тюрьму за незаконные мигалки',163),(624,2,'18/01/2012',163),(625,3,'Bentley||гибриды',164),(626,5,'Bentley готова устанавливать гибридный привод на новые модели, сообщил главный инженер компании Рольф Фреч в интервью Autocar. По его словам, премиальный автопроизводитель наблюдал за разработками зеленых технологий, осуществляемых концерном Volkswagen Group, и больше всего заинтересовался гибридными установками. Как объяснил Р. Фреч, больше всего Bentley понравилось то, что у таких силовых установок высокий крутящий момент, а это именно то, чем славится британская марка.||Bentley собирается в ближайшие несколько лет экспериментировать с новыми моделями и экологичными технологиями. Работы возглавит бывший глава Porsche Вольфганг Дюрхаймер. «Мы должны экспериментировать. Мы делали это в Porsche, и у нас есть возможность заниматься этим в Bentley», – заявил Р. Фреч, также работавший ранее на Porsche.',164),(627,1,'Bentley готова выпускать гибридные автомобили',164),(628,2,'18/01/2012',164),(629,3,'стихия||дороги',165),(630,5,'Транскавказская магистраль, соединяющая Россию с Южной Осетией, вторые сутки закрыта для проезда автотранспорта из-за опасности схода лавин. Трасса может не открыться вплоть до выходных, сообщает <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на руководителя пресс-службы МЧС Северной Осетии Юлию Старченко.||По ее словам, в горах в настоящее время продолжает идти снег, сотрудники противолавинной службы пытаются обстрелять склоны вдоль Транскама, чтобы принудительно спустить снег с наиболее опасных лавинных очагов.||«Однако из-за погодных условий работа проходит медленно. Между тем сотрудники дорожной службы говорят, что из-за большого объема снега, сошедшего на дорогу, расчистка трассы может затянуться не на один день», – уточнила она.||При этом синоптики прогнозируют лавиноопасность еще на ближайшие двое суток.',165),(631,1,'Транскам откроется не раньше выходных',165),(632,2,'18/01/2012',165),(633,3,'ДТП',166),(634,5,'Сегодня, 18 января, в Иркутске произошло ДТП, в результате которого один человек погиб и пострадали еще пятеро. Как сообщили в пресс-службе ГИБДД, маршрутное такси «Газель», следовавшее по маршруту № 87 с большим количеством пассажиров, вылетело с дороги и перевернулось. Одна из пассажирок погибла на месте, остальным была по необходимости оказана медицинская помощь. Четверо были госпитализированы.||По словам пассажиров, в аварии виноват водитель: он пошел на обгон автобуса, но не справился с управлением. Водитель отрицает свою вину: по его словам, автобус прижал «Газель» к обочине, и тут у маршрутки отказали тормоза. Водитель пытался затормозить хотя бы с помощью ручника, но безуспешно. Плотные сугробы на обочинах также способствовали аварии: в этом месте установлен достаточно высокий парапет, но сейчас он полностью скрыт под слежавшимся снегом, по которому маршрутка и вылетела с трассы.||В компании, которой принадлежит «Газель», сообщили, что машина 2007 года регулярно проходила техос',166),(635,1,'Маршрутное такси на полном ходу вылетело с трассы и перевернулось',166),(636,2,'18/01/2012',166),(637,3,'техосмотр||ТО||законы',167),(638,5,'Автомобили стандарта Евро-5 в возрасте до 5 лет могут освободить от прохождения техосмотра. Глава комитета по транспорту Госдумы Евгений Москвичев планирует в весеннюю сессию внести в нижнюю палату законопроект об отмене техосмотра для «экологически чистых» автомобилей моложе пяти лет, пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>.||«Сегодня уже появились автомобили, которым достаточно проходить ТО в течение не трех, а пяти лет. Это экологически чистые автомобили Евро-5. Владельцы таких автомобилей обслуживают их в самых лучших сервисных станциях, таким автомобилям не нужно в течение пяти лет проходить ТО. Он делается в этих сервисах», – заявил Е. Москвичев.||Депутат сообщил, что соответствующая поправка в закон «О техническом осмотре транспортных средств» будет подготовлена совместно со страховщиками и автомобилистами.||Е. Москвичев также отметил необходимость ввести в России обязательные сервисные книжки на каждый автомобиль, «как на каждого гражданина – больни',167),(639,1,'Автомобили стандарта Евро-5 могут освободить от прохождения ТО',167),(640,2,'18/01/2012',167),(641,3,'Foton||китайские автомобили||грузовики||заводы',168),(642,5,'Китайский Foton Motors рассматривает возможность создания автомобильного производства на территории города-спутника Нижнего Новгорода – Дзержинска. Об этом пишет РИА Новости со ссылкой на мэра города Виктора Сопина.||«Была встреча с представителями китайской фирмы, выпускающей грузовые и легковые автомобили. Они планируют прийти к нам на территорию городского округа Дзержинск», – сказал он. По словам В. Сопина, сначала планируется организовать выпуск грузовых автомобилей, а затем легковых.||«Там достаточно большой объем инвестиций. Будет создано порядка 12 000 рабочих мест в течение четырех лет», – сказал мэр Дзержинска, не назвав сроки начала реализации этого проекта.||Компания Foton Motors основана в 1996 году, является третьей по размерам автомобильной компанией в Китае.',168),(643,1,'Китайский автопроизводитель Foton откроет завод в России',168),(644,2,'19/01/2012',168),(645,3,'Audi',169),(646,5,'В Audi осознают растущий спрос на небольшие кроссоверы, и потому, как сообщил источник в компании, «новые модели меньше, чем предыдущие, достаточно посмотреть на линейку: Q7, Q5, Q3. Теперь вполне может появиться и кроссовер на базе А1». Его не стоит ждать в непосредственном будущем, но он вполне может появиться между 2013 и 2016 годом.||А вот появление кабриолета на базе А1 тот же источник исключил: такая модель будет слишком похожа на кабриолет А3, и в компании не видят смысла в ее создании.||О технических характеристиках и оснащении Q1 пока ничего не сообщается, но можно предположить, что он получит от A1 не только базу, но и линейку двигателей, в которую входят как бензиновые, так и дизельные агрегаты мощностью от 85 до 255 л.с.',169),(647,1,'Audi готовит мини-кроссовер Q1',169),(648,2,'19/01/2012',169),(649,3,'Mitsubishi||электромобили||экология',170),(650,5,'Mitsubishi опубликовала тизер нового электромобиля. Премьера модели состоится в марте этого года на международном автосалоне в Женеве. В компании новинку называют инновационным автомобилем премиум-класса и обещают, что в продаже он появится уже в конце 2012 года.||Электромобиль Mitsubishi положит начало производству автомобилей нового поколения. Компания ставит своей целью на будущее выпуск линейки дружелюбных к окружающей среде компактных автомобилей, кроссоверов и внедорожников, предназначенных для продаж на мировом рынке. Большую часть этих «зеленых» моделей составят электромобили.||Главной новинкой Mitsubishi в ближайшем будущем станет электромобиль Mirage, представленный в виде концепта на Токийском автосалоне в 2011 году. Производство серийной версии модели может быть запущено уже в этом году. В продаже в Европе Mirage должен появиться в следующем году.',170),(651,1,'Mitsubishi представит в Женеве автомобиль будущего',170),(652,2,'19/01/2012',170),(653,3,'ГИБДД-ГАИ||Москва||дороги',171),(654,5,'На подмосковных трассах начался радиоэксперимент по борьбе с пробками. Как сообщает управление ГИБДД по Московской области, полицейские начали регулярно оповещать автолюбителей об авариях и пробках по радиосвязи. Наличие обратной связи с дальнобойщиками позволяет полицейским своевременно корректировать дорожную обстановку и более оперативно реагировать в случае надобности.||В радиоэфире инспекторы ДПС помогают водителям планировать маршрут на подъездах к столице, а те в свою очередь описывают ситуацию на дорогах, по которым едут, сообщают о возникновении непредвиденных обстоятельств и даже помогают задерживать правонарушителей.||Пока радиостанции для связи с автолюбителями установлены только на трех постах рязанского направления федеральной трассы М-5. В том случае, если эксперимент признают удачным, ими оборудуют все подразделения дорожно-патрульной службы Московского региона.',171),(655,1,'В Москве борются с пробками по радиосвязи',171),(656,2,'19/01/2012',171),(657,3,'General Motors||российский автопром||российский авторынок',172),(658,5,'На петербургском заводе General Motors в 2011 году было собрано 60 тысяч автомобилей, а на заводе в Тольятти – 57 тысяч. Как сообщил директор по связям с общественностью GM Russia Сергей Лепнухов, в 2012 году автоконцерн планирует увеличить производственную мощность петербургского завода до 230 тысяч. Мощность тольяттинского завода также будет увеличена почти вдвое и составит 120 тысяч автомобилей в год.||С. Лепнухов уточнил, что план производства GM-Auto на территории России будет зависеть от состояния и потребностей российского рынка. На 2012 год намечено начало продаж произведенных в России автомобилей Chevrolet Cruze в кузове хэтчбек, нового Opel Zafira, Chevrolet Camaro и Chevrolet Aveo. Производство последнего к концу года будет переведено с «Автотора» на ГАЗ.',172),(659,1,'General Motors вдвое увеличит объем производства на территории России',172),(660,2,'19/01/2012',172),(661,3,'ГИБДД||штрафы',173),(662,5,'Чаще всего УГИБДД Москвы штрафует автомобилистов за превышение скорости и выезд на полосу маршрутного транспорта. Об этом пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a> со ссылкой на замначальника УГИБДД Москвы Владимира Томчака.||«С октября 2011 года около 45 миллионов потратили автолюбители, нарушая ПДД. В основном постановления выписывали за (превышение) скорости на 20-40 км/ч, (превышение) на 40-60 км/ч и свыше 60 км/ч, (выезд на) полосу маршрутных транспортных средств», – сказал он.||Значительная часть нарушений была установлена благодаря работе центра автоматизированной фиксации административных правонарушений УГИБДД. Замначальника управления добавил, что сейчас в Москве в полном объеме работают 150 комплексов фиксации нарушений ПДД. А в 2012-2013 годах планируется ввести в действие еще более 800 таких комплексов, отметил он.',173),(663,1,'В ГИБДД рассказали, за что автомобилисты Москвы заплатили 45 млн руб.',173),(664,2,'19/01/2012',173),(665,3,'российский авторынок||автокредиты',174),(666,5,'В 2012 году во всех регионах России ожидается рост ставок по автомобильным кредитам, сообщил руководитель практики по оказанию услуг компаниям автомобильной отрасли PricewaterhouseCoopers (PwC) Стенли Рут. В первую очередь это коснется кредитов на покупку автомобилей, участвовавших в только что завершенной программе льготного кредитования. Подорожание кредитов может негативно сказаться на продажах автомобилей.||Напомним, в 2011 году в России было выдано более 263 000 льготных автокредитов. Каждый десятый автомобиль был куплен с привлечением такого автокредита. Всего же, по оценкам PwC, доля продаж автомобилей в кредит составила в 2011 году 44% от общего количества проданных автомобилей, что на 8% больше, чем в 2010-м (1 100 000 автомобилей в 2011 году против 637 000 автомобилей в 2010-м).',174),(667,1,'В 2012 году ставки по автокредитам вырастут',174),(668,2,'19/01/2012',174),(669,3,'Москва||дороги',175),(670,5,'В 2012 году власти Москвы ожидают увеличения пассажиропотока в общественном транспорте города на 3,3%. Как сообщил сегодня руководитель департамента транспорта и развития дорожно-транспортной инфраструктуры Москвы Максим Ликсутов, число поездок может вырасти на 120 млн по сравнению с 2011 годом – до 3,72 млрд поездок.||Это станет закономерным продолжением тенденции, обозначившейся в 2011 году, когда число поездок на общественном транспорте Москвы выросло на 100 млн – до 3,6 млрд. Об этом сообщил заместитель мэра Москвы по вопросам развития транспорта и дорожного строительства Николай Лямов. В минувшем году в Москве появились выделенные полосы для автобусов и экспресс-маршруты, что привлекло большое количество жителей столицы, не желающих стоять в пробках на личных автомобилях.',175),(671,1,' Пассажиропоток в общественном транспорте столицы может вырасти на 120 млн',175),(672,2,'19/01/2012',175),(673,3,'Москва||дороги',176),(674,5,'Интеллектуальная транспортная система (ИТС), которую планируют внедрить власти Москвы, разрабатывается на основе анализа транспортных потоков в городе. Об этом сообщил сегодня журналистам заместитель мэра Москвы по вопросам транспорта Николай Лямов.||По его словам, ИТС позволит отслеживать ситуацию на дорогах. На основных магистралях столицы уже установлено 1,3 тыс. детекторов – они стали одним из первых шагов по внедрению ИТС. «Мы уже понимаем движение, потоки машин. На сегодняшний день можем анализировать потоки по всему городу и на основе этого ведем подготовку по созданию транспортной модели движения и новой маршрутной сети», – отметил Лямов. Как ожидается, внедрение ИТС позволит снизить аварийность на дорогах столицы на четверть.||Напомним, работа по созданию ИТС была начата в 2011 году по инициативе мэра Москвы Сергея Собянина. В 2012 году она «примет масштабный характер внедрения».||Ранее Н. Лямов пояснял, что внедрение ИТС предусматривает, в частности, создание единого диспетче',176),(675,1,'Власти Москвы рассказали о ходе внедрения интеллектуальной транспортной системы',176),(676,2,'19/01/2012',176),(677,3,'российский авторынок',177),(678,5,'В 2012 году темпы увеличения объема продаж легковых автомобилей в России замедлятся по сравнению с 2011 годом, прогнозирует агентство PricewaterhouseCoopers (PwC). В соответствии с оптимистичным прогнозом продажи составят 2 млн 600 тыс. (плюс 4% по сравнению с 2011 г.).||«В дальнейшем мы ожидаем замедления темпов роста и полагаем, что в 2012 г. объемы продаж новых легковых автомобилей (без учета легких коммерческих автомобилей) не превысят 2,6 млн. А при доминировании негативных факторов объемы продаж могут снизиться и до 2,3 млн», – сообщил журналистам руководитель практики по оказанию услуг компаниям автомобильной отрасли PwC Стенли Рут.||По данным PwC, импорт новых иномарок в 2011 г. в Россию вырос на 38% и составил 840 тыс. автомобилей. Как полагает агентство, в 2012 г. можно ожидать как незначительного роста, так и снижения объемов импорта новых легковых автомобилей, конечный показатель будет зависеть от курсов валют.||На протяжении последних двух лет происходило активное оживлени',177),(679,1,'PwC: в 2012 году увеличение объема продаж на российском авторынке замедлится',177),(680,2,'19/01/2012',177),(681,3,'Москва||дороги',178),(682,5,'Власти столицы собираются ввести правило, разрешающее автомобилям проезд по выделенным полосам для общественного транспорта в выходные дни. Об этом сегодня, 19 января, сообщил руководитель столичного департамента транспорта и развития дорожно-транспортной инфраструктуры Максим Ликсутов.||По поручению мэра Москвы Сергея Собянина соответствующие документы будут разработаны до конца января. После рассмотрения можно будет приступить к технической реализации полезного для автомобилистов нововведения: на дорогах понадобятся дополнительные дорожные знаки и электронные табло.||Напомним, два дня назад городские власти называли дальнейшую организацию выделенных полос одной из приоритетных задач на ближайшие годы. Возможно, новое правило, позволяющее водителям легковых автомобилей заезжать на эти полосы хотя бы по выходным, несколько примирит с ними столичных автолюбителей.',178),(683,1,'В выходные по выделенным линиям разрешат ездить всем',178),(684,2,'19/01/2012',178),(685,3,'BMW||новинки||шпионские фото',179),(686,5,'BMW 135i M попалась фотошпионам во время дорожных тестов. Камуфляж скрывает очень немногое, можно рассмотреть почти все линии кузова. Увеличенные воздухозаборники придают модели более агрессивный вид, а сзади видны две выхлопные трубы, расположенные по обе стороны от номерной таблички.||Под капотом нового поколения BMW 1-й серии 3,0-литровый турбодвигатель мощностью 326 л.с., работающий в паре с шестиступенчатой механической коробкой передач или семиступенчатой автоматической трансмиссией с двойным сцеплением.||Напомним, на автосалоне в Женеве BMW обещает показать несколько «заряженных» автомобилей в рамках новой тюнинг-программы BMW M Performance Automobiles. BMW 135i M – самая мощная модель этого ряда.',179),(687,1,'BMW 135i M проходит дорожные тесты почти без камуфляжа. ФОТО',179),(688,2,'19/01/2012',179),(689,3,'Volkswagen||электромобили||новинки||Детройт-2012',180),(690,5,'Концепт электромобиля E-Bugster так понравился посетителям автосалона в Детройте, на котором он был представлен, что в Volkswagen всерьез рассматривают возможность запустить его в серийное производство. Во время презентации электрокара было указано, что E-Bugster создан для демонстрации новых дизайнерских и технологических решений, над которыми работают в компании.||Дизайнер Volkswagen Марк Лихтер, работавший над концептом, уверен, что появление серийного E-Bugster – только вопрос времени. В компании убедились, что автомобиль будет пользоваться спросом, осталось только разработать бизнес-план.||Напомним, под капотом концепта – электромотор мощностью 85 л.с., питающийся от литиево-ионных батарей. Запас хода электрокара составляет 160 километров, а зарядить батареи можно как от 120-вольтовой, так и от 240-вольтовой сети. На станции зарядить машину на 80% можно всего лишь за полчаса.',180),(691,1,'Volkswagen решил запустить электрокар E-Bugster в серийное производство',180),(692,2,'19/01/2012',180),(693,3,'Mercedes-Benz||новинки',181),(694,5,'В интернете появилось шпионское видео нового Mercedes-Benz A-класса, пойманного камерой во время дорожных тестов. Дизайн будущей новинки тщательно скрыт камуфляжем, но он в целом и так уже известен: концепт данной модели был представлен на автосалоне в Пекине в начале 2011 года, а позже компания опубликовала несколько изображений серийной версии.||Как ожидается, автомобиль получит линейку двигателей, аналогичную той, что получила модель В-класса: пять бензиновых двигателей мощностью от 108 до 201 л.с. и четыре дизеля мощностью от 120 до 201 л.с.||Через некоторое время после того, как новинка поступит на рынок, появится и версия AMG. Вероятно, она получит 2,0-литровый турбомотор мощностью до 350 л.с., трансмиссию с двойным сцеплением и полный привод.||<center><iframe width=\"640\" height=\"360\" src=\"http://www.youtube.com/embed/4BwtwXIBC1E?feature=player_embedded\" frameborder=\"0\" allowfullscreen></iframe></center><br><br>',181),(695,1,'Mercedes-Benz A-класса нового поколения проходит дорожные тесты',181),(696,2,'19/01/2012',181),(697,3,'Chevrolet||мировой авторынок',182),(698,5,'За 2011 год на мировом авторынке было продано 4,76 млн автомобилей Chevrolet, что является мировым рекордом. В компании уверены: секрет успеха марки в том, что в модельном ряду есть автомобили, предназначенные для самых разных условий вождения и отвечающие требованиям самых разных клиентов.||Больше всего автомобилей Chevrolet было продано в США: 1 775 812, что на 13% больше, чем в 2010 году. На втором месте – Бразилия (632 201 штука), а на третьем – Китай (595 068 штук). Резко увеличился объем продаж и в ряде других стран, таких как Вьетнам (+79%), Россия (+49%), Турция (+30%) и Германия (+21%).||Самым большим спросом в ушедшем году пользовался компактный седан Cruze: было продано более 670 000 автомобилей. Таким образом, Chevrolet Cruze стал абсолютным бестселлером бренда: за суммарное время продаж, которые начались в 2009 году, продано было 1,13 млн автомобилей.||Стоит отметить, что в России Chevrolet остается продаваемым брендом уже пятый год подряд. В 2011 году объем его продаж сос',182),(699,1,'Chevrolet установил мировой рекорд по продажам',182),(700,2,'20/01/2012',182),(701,3,'Citroen',183),(702,5,'На заводе Citroen в Калуге будут производить Citroen С-класса в кузове седан. Как сообщил генеральный директор Citroen в России Жан-Луи Шамла, новый автомобиль поступит в продажу в первой половине лета 2013 года.||Автокомпания оценивает российский рынок как очень перспективный и планирует уже в текущем году увеличить продажи на 40%. В соответствии с минимальным планом на ближайшие годы объем продаж должен ежегодно увеличиваться хотя бы на 10 000 автомобилей.||В наступившем году Citroen выпустит на российский рынок сразу несколько моделей: уже в марте начнутся продажи премиального хэтчбека DS4, в апреле появится С4 Aircross, в мае – С1, а в июне – DS5.||Напомним, завод в Калуге построен альянсом Peugeot-Citroen-Mitsubishi. Его производственная мощность составляет 125 тысяч автомобилей в год с возможностью расширения до 300 тысяч машин.',183),(703,1,'Citroen планирует производить седан С-класса в России',183),(704,2,'20/01/2012',183),(705,3,'мировой авторынок||цены',184),(706,5,'В Беларуси принят закон, в соответствии с которым дилеры при уплате НДС больше не смогут платить 20% налога не с общей стоимости автомобиля, а с разницы между ценой ее покупки и ценой ее продажи. Цены на импортируемые автомобили взлетят на 16-18% в самое ближайшее время: с 24 января.||Подорожают не только новые машины, но и автомобили на вторичном рынке, предсказывают эксперты. После повышения таможенных пошлин для физических лиц пригонять авто из ближнего зарубежья стало нерентабельно, а значит, этот сегмент вторичного рынка уже практически исчез. Купленные после грядущего подорожания автомобили со временем будут продавать, опираясь на сумму, которую пришлось уплатить за них изначально. Средние цены на б/у машины поднимутся, и те, кто успели купить автомобиль до отмены льгот дилерам, при перепродаже станут ориентироваться на них. Конечно, рост цен на вторичном рынке будет не столь резким, но постепенно стоит ожидать увеличения стоимости б/у автомобилей на те же 16%.',184),(707,1,'В Беларуси отменили таможенные льготы для автодилеров',184),(708,2,'20/01/2012',184),(709,3,'дороги||Москва',185),(710,5,'В Москве с 12 по 18 января 2012 г. выявлено 58 нарушений при строительстве дорог. Как сообщает пресс-служба Комитета государственного строительного надзора города Москвы (Мосгосстройнадзор), наибольшее количество нарушений при строительстве дорог допущено на следующих объектах: участок магистрали Четвертого транспортного кольца от шоссе Энтузиастов до Измайловского шоссе (2-й пусковой комплекс); реконструкция Дмитровского шоссе от Малого кольца МЖД до Коровинского шоссе; дорога и инженерные коммуникации для микрорайона 9-го района Северный; реконструкция Каширского шоссе от МКАД до Варшавского шоссе.||При производстве строительных работ нарушались правила техники производства, а также нормы безопасности. 18 нарушений устранены в ходе проверок, еще 40 нарушений находятся на стадии устранения и на контроле Мосгосстройнадзора. По всем нарушениям приняты меры административного воздействия.',185),(711,1,'В Москве за неделю выявили 58 нарушений при строительстве дорог',185),(712,2,'19/01/2012',185),(713,3,'народные гаражи||Москва||парковка',186),(714,5,'Власти Москвы намерены оформить имущественные права на 11 300 машино-мест в «народных гаражах» в собственность покупателей в I квартале 2012 г. Как сообщает комплекс градостроительной политики и строительства столицы, во II квартале планируется оформить еще почти 1800 машино-мест. Оставшиеся примерно 4000 машино-мест будут оформлены в заявленные сроки после сдачи гаражей в эксплуатацию.||По последним данным, в рамках программы «Народный гараж» заключено 18 908 договоров долевого участия. Тринадцать тысяч машино-мест в построенных «народных гаражах» уже используются по своему назначению, однако многие владельцы гаражей до сих пор не имеют права собственности на свои машино-места.||В городе существует проблема с оформлением имущественных прав по уже введенным объектам программы «Народный гараж». Вызвана она, по мнению властей, прежде всего межведомственными сбоями документооборота: Московское городское БТИ до недавнего времени выдавало документы, которые не принимали в Российском государ',186),(715,1,'Владельцев «народных гаражей» наконец оформят собственниками',186),(716,2,'19/01/2012',186),(717,3,'Москва||ЧП||пробки',187),(718,5,'На перекрестке Липецкой и Лебедянской улиц в Москве отключились все светофоры. ЧП произошло из-за повреждения электронного блока, которым управляются несколько светофоров, пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>. Дорожная обстановка на перекрестке сильно осложнилась, машины двигаются хаотично, создавая аварийные ситуации. Пешеходы перебегают шестиполосную трассу на свой страх и риск.||Электронный блок нескольких светофоров сломался из-за аварии, произошедшей на перекрестке еще утром: во время ДТП автомобиль врезался в стойку пешеходного светофора и сбил ее.',187),(719,1,'На оживленном перекрестке в Москве отключились все светофоры',187),(720,2,'19/01/2012',187),(721,3,'Москва||парковки',188),(722,5,'Власти Москвы намерены в 2012 г. принять закон о парковочной деятельности в городе. Как сообщает комплекс градостроительной политики и строительства города, сейчас идет разработка законопроекта, работа над документом будет завершена в ближайшее время, после чего его направят на согласование.||Закон о парковочной деятельности определит нормативную базу для организации и эксплуатации парковок в столице. На данный момент в правовом поле не существует даже такого понятия, как «парковка». В федеральном законодательстве есть только определения «стоянки» и «остановки», а «парковки» – нет. Попытки подготовить проект закона предпринимались в 2007 г., 2010 г. и 2011 г., но по различным причинам не прошли согласования.||Принять закон столичные власти планируют до 1 июля 2012 г. В дополнение к закону необходимо будет еще разработать общегородские правила парковки, а также единую для Москвы тарифную политику.',188),(723,1,'Власти Москвы намерены в 2012 г. принять закон о парковке',188),(724,2,'19/01/2012',188),(725,3,'Москва||власти||экология',189),(726,5,'Въезд автомобилей «Жигули» в центр столицы не собираются ограничивать, сообщил сегодня, 19 января, руководитель департамента транспорта и развития дорожно-транспортной инфраструктуры столицы Максим Ликсутов.||Напомним, с начала этого года въезд в центр города ограничен для грузовиков с экологическими характеристиками ниже стандарта Евро-4. В ближайшее время будет рассмотрен законопроект, предлагающий запретить въезд в центр и автомобилям, не соответствующим экологическому стандарту Евро-3. Ни «Жигули», ни произведенные до начала 2000-х иномарки этому стандарту не соответствуют.||«Главное, чтобы машина была технически исправна, а владелец должен соблюдать правила, и тогда проблем у него не будет», – подчеркнул М. Ликсутов.',189),(727,1,'«Жигулям» не будут запрещать въезд в центр Москвы',189),(728,2,'19/01/2012',189),(729,3,'Volkswagen||Beetle||кабриолет||шпионские фото||spy photo',190),(730,5,'Фотографы-шпионы «поймали» в объективы своих фотокамер новый кабриолет Volkswagen Beetle. Модель дебютирует в этом году. Автомобиль оснащен складываемой тканевой крышей последнего поколения. Кабриолет получил такое же ветровое стекло, заднюю часть и линию крыши, что и концепт-кар Volkswagen E-Bugster, представленный в Детройте в этом месяце.||В продаже в Европе новое купе Volkswagen Beetle появится в апреле этого года, в то же время должно было стартовать производство открытой версии модели на заводе в Мексике. Однако по последним данным запуск производства кабриолета был отложен на несколько месяцев.||Оснащаться Volkswagen Beetle Cabriolet будет теми же двигателями, что и купе: бензиновыми моторами объемом 1,2 л (104 л.с.), 1,4 л (158 л.с.) и 2 л (197 л.с.), а также дизельными агрегатами объемом 1,6 л (104 л.с.) и 2 л (138 л.с.).',190),(731,1,'Volkswagen тестирует новый кабриолет Beetle',190),(732,2,'19/01/2012',190),(733,3,'General Motors||технологии',191),(734,5,'В General Motors компьютеризируют... стекла пассажирских окон автомобилей. Компания работает над созданием прозрачных интерактивных дисплеев, которые станут частью стекол. Значительную часть целевой аудитории разработки составляют дети, но некоторые приложения наверняка порадуют и взрослых. Для участия в разработке технологической новинки концерн привлек студентов старшего курса Академии художеств «Бецалель».||Среди уже готовых приложений – программа, позволяющая рисовать на стекле, и симпатичный персонаж Отто, который бежит (или летит, в зависимости от скорости движения) вровень с автомобилем, вписываясь в пейзаж за окном. Кроме того, есть программа GPS-навигации, с помощью которой пассажир в случае надобности сможет стать штурманом. Еще одно приложение сообщает подробные данные о работе всех систем автомобиля.||Сенсорный дисплей умеет распознавать почерк: очертания букв можно рисовать прямо пальцем, причем на окне отобразятся не ваши каракули, а красивые ровные буквы. Надпись можно «',191),(735,1,'General Motors заменит задние стекла автомобилей интерактивными дисплеями. ВИДЕО',191),(736,2,'20/01/2012',191),(737,3,'Saab||Youngman||мировой автопром',192),(738,5,'Youngman не сдается: китайский автопроизводитель снова заявил о желании возродить Saab. Он готов заплатить нынешнему владельцу бренда, Swedish Automobile, 731 млн долларов. Именно такую сумму в Youngman готовы инвестировать в возможность создавать новые авто на базе последней платформы Saab Phoenix.||На следующей неделе высшее руководство Youngman во главе с генеральным директором и по совместительству владельцем компании прибудет в штаб-квартиру Swedish Automobile. В том случае, если стороны договорятся, производство Saab 9-3 начнется через 15 недель после подписания необходимых бумаг.||Напомним, ранее Youngman неоднократно пытался выкупить Saab у General Motors, но в GM оказались непреклонны: делиться своими разработками с китайцами автоконцерн не пожелал. Однако для совершения новой сделки согласие GM не потребуется, так как платформа Phoenix разрабатывалась без привлечения специалистов концерна.',192),(739,1,'Youngman не оставляет надежд возродить Saab',192),(740,2,'20/01/2012',192),(741,3,'Москва||дороги',193),(742,5,'Тендерный комитет Москвы начал рассмотрение заявок на участие в торгах по реконструкции Балаклавского проспекта и Ярославского шоссе. В торгах на право заключения госконтракта на выполнение подрядных работ по лоту «Реконструкция Балаклавского проспекта – Рублевского шоссе от МКАД до Варшавского шоссе» участвуют пять компаний. Начальная цена контракта – 8 млрд 90 млн руб.||На право заключения контракта на реконструкцию Ярославского шоссе от МКАД до Садового кольца претендуют три компании. Стартовая стоимость – 9 млрд 580 млн руб.||В рамках реализации проектов планируется расширение проезжей части, строительство новых и реконструкция существующих местных и боковых проездов, устройство эстакад, транспортных развязок, строительство подземных пешеходных переходов, благоустройство и озеленение. По условиям конкурсной документации срок выполнения работ не должен превышать 24 месяцев.',193),(743,1,'Москва рассмотрит заявки на ремонт Балаклавского пр. и Ярославского ш.',193),(744,2,'20/01/2012',193),(745,3,'дороги||финансы',194),(746,5,'Премьер-министр РФ Владимир Путин обещает изменить ситуацию с качеством российских дорог в лучшую сторону. Произойти это должно к чемпионату мира по футболу, который пройдет в 2018 году в России. Об этом пишет <a href=\"http://www.ria.ru/\" target=\"_blank\">РИА «Новости»</a>.||«Уверен, что к 2018 году мы ситуацию изменим, обязаны изменить», – сказал он на неформальной встрече с футбольными болельщиками после того, как один из участников встречи пожаловался на качество российских дорог. «Вопросы в недофинансировании, имею в виду огромные размеры страны», – ответил на это глава правительства.||В. Путин напомнил, что в прошлом году были созданы региональные дорожные фонды, в которые поступают акцизы от продажи бензина. По его словам, за счет повышения акцизов на бензин в региональные фонды в этом году должно попасть более 90 млрд рублей.||В тех случаях, где на строительстве дорог наживаются, виновных «надо ловить и сажать в тюрьму», заявил он.',194),(747,1,'В. Путин обещает, что дороги станут лучше уже через 5 лет',194),(748,2,'20/01/2012',194),(749,3,'бензин||цены||власти',195),(750,5,'Министерство энергетики РФ и нефтяные компании пришли к соглашению по вопросу розничных цен на горюче-смазочные материалы (ГСМ). «Несмотря на рост акцизов с 1 января 2012 года, у нас есть принципиальная договоренность с нефтяными компаниями, с участниками рынка ГСМ о неповышении цен на энергоносители, на ГСМ в рознице», – отметил глава ведомства Сергей Шматко. Он также рассказал, что Минэнерго организовало систему мониторинга цен на бензин на автозаправках. Особенно внимательно будут следить за стоимостью бензина на АЗС, принадлежащих независимым операторам. Цены должны оставаться на том же уровне, на каком они были в декабре 2011 года.||Напомним, что в начале прошлого года на внутреннем рынке нефтепродуктов РФ из-за роста акцизов резко повысились цены на топливо, а после и вовсе возник дефицит. Для решения этой проблемы правительство РФ установило заградительную пошлину на бензин в размере 90% от ставки пошлины на нефть, которая действует по сей день. Кроме того, был продлен срок дейс',195),(751,1,'Минэнерго проконтролирует стоимость бензина на АЗС',195),(752,2,'20/01/2012',195),(753,3,'Grandeur||Hyundai||новинки||седаны',196),(754,5,'Компания «Хендэ Мотор СНГ» объявила о скором старте российских продаж Grandeur нового поколения. Продажи модели начнутся во втором квартале 2012 года.||Созданный в рамках концепции «grand glide», Hyundai Grandeur получил плавные линии кузова, массивную решетку радиатора, светодиодные фары и широкий задний бампер.||Модель оснащена увеличенной на 65 мм по сравнению с предыдущей версией колесной базой (2845 мм), в результате чего увеличился салон машины. Габариты нового Grandeur также больше, чем у предыдущего поколения: общая длина составляет 4910 мм, ширина – 1860 мм и высота – 1470 мм.||Седан оснащен двигателем Lambda II 3,0 л мощностью 250 л.с. c непосредственным впрыском и шестиступенчатой автоматической коробкой передач. В качестве опций для Grandeur будут доступны панорамный люк, кожаные сиденья Nappa с подогревом, электропривод водительского сиденья с регулировкой по 14 положениям и электронный ключ запуска двигателя.||Стоимость автомобиля пока не сообщается.',196),(755,1,'Hyundai запускает российские продажи нового Grandeur',196),(756,2,'20/01/2012',196),(757,3,'регионы||дороги',197),(758,5,'Автолюбитель выиграл тяжбу против городских властей, не позаботившихся о состоянии дорог в зимнее время года. Забайкальский краевой суд обязал мэрию Читы выплатить истцу 70 тысяч рублей.||В марте 2011 года житель Читы попал в ДТП из-за ледяного наката, скрывшего под собой дорожное полотно. Водитель потерял управление на скользкой дороге и протаранил другой автомобиль, припаркованный на обочине. Оценив ситуацию, он обратился в Центральный райсуд города. Размер ущерба он оценил более чем в 145 тысяч рублей, объясняя, что данная сумма покрывает ремонт его автомобиля, который сильно пострадал при столкновении.||Изначально суд частично удовлетворил его иск, несколько снизив размер компенсации – до 133 тысяч рублей. По рассмотрении дела ответчиком был определен комитет жилищно-коммунального хозяйства администрации города. Но затем судебная коллегия Забайкальского краевого суда приняла другое решение: она уменьшила сумму компенсации до 70 тысяч рублей, объясняя это тем, что часть вины лежит и',197),(759,1,'Автолюбитель заставил ЖКХ платить за ремонт пострадавшей в ДТП машины',197),(760,2,'20/01/2012',197),(761,3,'ДТП',198),(762,5,'В результате дополнительной экспертизы по делу о ДТП с участием автомобиля вице-президента ЛУКОЙЛа Анатолия Баркова было установлено, что в момент столкновения Mercedes-Benz S500 Баркова находился на полосе встречного движения. Напомним, <a href=\"http://www.autonews.ru/autobusiness/news.shtml?/2011/08/12/1694523\">авария стоила жизни двум женщинам</a>, находившимся в автомобиле Сitroen C3, – врачам Ольге Александриной и Вере Сидельниковой. Эксперты также установили, что на правом переднем крыле автомобиля ЛУКОЙЛа были повреждения, которые искусственно замаскированы.||По результатам проведенного непосредственно после трагедии расследования виновной в ДТП была признана водитель Citroen. Дело было закрыто в связи с ее смертью. Однако позже адвокатам погибших удалось добиться от Конституционного суда РФ признания норм Уголовно-процессуального кодекса РФ, позволяющих прекратить уголовное дело в связи со смертью подозреваемого без согласия его близких родственников, не соответствующими Консти',198),(763,1,'В момент столкновения авто вице-президента ЛУКОЙЛа двигалось по встречной полосе',198),(764,2,'20/01/2012',198),(765,3,'автострахование||ОСАГО||техосмотр||законы',199),(766,5,'Покинул свой пост президент Российского союза автостраховщиков (РСА) Павел Бунин. Сам Бунин комментировать ситуацию отказался, но ясно, что это решение связано с критической ситуацией по выдаче полисов ОСАГО, которая привязана к техосмотру по новому закону о ТО.||О том, что в системе проведения техосмотра по новому образцу есть значительные недоработки, говорили еще в конце минувшего года. Во-первых, несмотря на ряд принятых в последний момент экстренных мер, талонов техосмотра всем не хватило. Ни новых, ни даже старых, действие которых продлили до апреля. Во-вторых, не готова инфраструктура: в некоторых регионах до сих пор ощущается острая нехватка аккредитованных пунктов техосмотра.||Напомним, выдача полисов ОСАГО в соответствии с новым законом привязана к прохождению ТО. То есть от невозможности пройти техосмотр страдают и страховщики, уже предсказывающие 10%-ное падение объема сборов от автострахования, и автолюбители. Новый техосмотр не только пока не стал более простым для обычны',199),(767,1,'Президент РСА Павел Бунин покинул пост из-за критической ситуации с ТО',199),(768,2,'20/01/2012',199),(769,3,'АвтоВАЗ||отзывы||российский авторынок',200),(770,5,'В автомобилях Lada Granta обнаружен дефект. Первая партия автомобилей уже доставлена дилерам для передачи будущим владельцам, оформившим в прошлом году предварительный заказ. Очевидно, для многих владельцев конечной целью чуть ли не первой поездки на новом авто станет гарантийный сервисный центр, где электропроводка контроллера электронной системы управления двигателем получит дополнительную герметизацию. Без нее автомобиль может в любой момент заглохнуть на ходу.||АвтоВАЗ признал брак и подчеркнул, что его можно легко и быстро устранить, что и будет сделано. Точное количество отзываемых автомобилей в компании не назвали. Ту часть автомобилей, что еще находится у дилеров, подлатают до продажи, но часть машин уже передана клиентам.||В АвтоВАЗе обещали, что в дальнейшем на всех выпускаемых автомобилях Lada Granta проблемная электропроводка будет снабжена дополнительным бандажем.',200),(771,1,'АвтоВАЗ отзывает все проданные Lada Granta',200),(772,2,'20/01/2012',200),(773,3,'цены||бензин',201),(774,5,'Потребительские цены на бензин в среднем по России в период с 10 по 16 января 2012 г. снизились на 0,2% и на 16 января с. г. составили 26,95 руб./л, сообщает Федеральная служба государственной статистики (Росстат).||При этом цена на бензин марки Аи-92 уменьшилась на 0,2% – до 26,27 руб./л, марки Аи-95 – на 0,1%, до 28,32 руб./л. Цены на дизельное топливо выросли на 0,1% – до 27,87 руб./л.||Снижение средних цен на бензин зарегистрировано в 33 центрах субъектов РФ, в том числе в Иркутске – на 1,4%, до 27,87 руб./л. В 45 центрах субъектов РФ цены на бензин остались на уровне предыдущей недели. Удешевление бензина было зафиксировано в пяти центрах субъектов РФ, в том числе в Брянске – на 0,3%, до 25,82 руб./л.||В Москве цены на бензин в среднем за неделю повысились на 0,1% – до 27,67 руб./л, в Санкт-Петербурге – остались на уровне 27,17 руб./л.',201),(775,1,'В России цены на бензин стали снижаться, в Москве – продолжают рост',201),(776,2,'20/01/2012',201),(777,3,'угоны',202),(778,5,'Молодая жительница Тольятти, выйдя во двор прогреть свое авто, не обнаружила его на месте и немедленно заявила в полицию. Как сообщили в пресс-центре ГИБДД по региону, в городе и окрестностях был объявлен план «Перехват», в ходе которого автомобиль был найден и возвращен владелице, а преступник – арестован.||Горе-угонщика поймали на трассе, причем пешим: он не обращал внимания, что топлива в баке только что угнанной машины не так уж много, пока оно не кончилось. Видимо, бросать машину – ВАЗ-2106 – ему показалось жалко, и он остался на трассе, надеясь, что кто-нибудь из проезжающих поделится с ним бензином. Стоит отметить, что себя грабитель «заправил» вполне основательно: задержавшие его полицейские и без алкотестера поняли, что он пьян.||В отношении него возбуждено уголовное дело по ч. 1 статьи 166 УК РФ (неправомерное завладение автомобилем или иным транспортным средством). Данное преступление наказывается лишением свободы на срок до пяти лет.',202),(779,1,'Пьяный автоугонщик пытался выпросить бензин для украденной машины',202),(780,2,'20/01/2012',202),(781,3,'Fiat||новинки',203),(782,5,'Фотошпионам удалось поймать новый FIAT Ellezero в объектив камеры в тот момент, когда автомобиль проходил дорожные тесты. Насколько можно судить, учитывая, что автомобиль весьма основательно укутан камуфляжем, дизайн Ellezaro выполнен в стиле, схожем со стилем FIAT 500.||Салон выглядит почти по-спартански: просто и лаконично. Кресла с двухцветной обивкой и контрастной прострочкой, металлическая отделка панелей. Задним пассажирам может оказаться тесновато. Скорее всего, Ellezero получит линейку двигателей, аналогичную линейке агрегатов FIAT 500. Автомобиль будет представлен на автосалоне в Женеве. Его продажи в Европе должны стартовать до конца этого года.',203),(783,1,'Фотошпионам удалось поймать новый FIAT Ellezero',203),(784,2,'20/01/2012',203);

/*Table structure for table `PatternType` */

DROP TABLE IF EXISTS `PatternType`;

CREATE TABLE `PatternType` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `source_type_id` int(11) DEFAULT NULL,
  `name` varchar(255) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `IDX_1C872F9A8C9334FB` (`source_type_id`),
  CONSTRAINT `FK_1C872F9A8C9334FB` FOREIGN KEY (`source_type_id`) REFERENCES `sourcetype` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=cp1251;

/*Data for the table `PatternType` */

insert  into `PatternType`(`id`,`source_type_id`,`name`) values (1,1,'title'),(2,1,'date'),(3,1,'tags'),(4,1,'author'),(5,1,'content'),(6,1,'next'),(7,1,'list'),(8,1,'post');

/*Table structure for table `Post` */

DROP TABLE IF EXISTS `Post`;

CREATE TABLE `Post` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `satellite_id` int(11) DEFAULT NULL,
  `title` varchar(255) NOT NULL,
  `content` text NOT NULL,
  `file_name` varchar(255) NOT NULL,
  `is_page` tinyint(1) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `UNIQ_FAB8C3B3D7DF1668` (`file_name`),
  KEY `IDX_FAB8C3B32D0EAD71` (`satellite_id`),
  CONSTRAINT `FK_FAB8C3B32D0EAD71` FOREIGN KEY (`satellite_id`) REFERENCES `satellite` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=cp1251;

/*Data for the table `Post` */

/*Table structure for table `Proxy` */

DROP TABLE IF EXISTS `Proxy`;

CREATE TABLE `Proxy` (
  `id` int(11) NOT NULL,
  `ip` int(11) NOT NULL,
  `port` int(11) NOT NULL,
  `proxy_type` varchar(255) DEFAULT NULL,
  `valid` tinyint(1) DEFAULT NULL,
  `checked_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `Unique_IP` (`port`,`ip`),
  CONSTRAINT `FK_B2B3E6BABF396750` FOREIGN KEY (`id`) REFERENCES `rawresult` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=cp1251;

/*Data for the table `Proxy` */

/*Table structure for table `RawResult` */

DROP TABLE IF EXISTS `RawResult`;

CREATE TABLE `RawResult` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `source_id` int(11) DEFAULT NULL,
  `created_at` datetime NOT NULL,
  `url` varchar(255) NOT NULL,
  `descriminator` tinytext NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `NewIndex1` (`url`),
  KEY `IDX_2A275658953C1C61` (`source_id`),
  CONSTRAINT `FK_2A275658953C1C61` FOREIGN KEY (`source_id`) REFERENCES `source` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=204 DEFAULT CHARSET=cp1251;

